INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/live/3.api/5.kit/12.resolving.md', 'Resolving', '{"type":"minimark","value":[["p",{},"Sometimes you need to resolve a paths: relative to the current module, with unknown name or extension. For example, you may want to add a plugin that is located in the same directory as the module. To handle this cases, nuxt provides a set of utilities to resolve paths. ",["code",{},"resolvePath"]," and ",["code",{},"resolveAlias"]," are used to resolve paths relative to the current module. ",["code",{},"findPath"]," is used to find first existing file in given paths. ",["code",{},"createResolver"]," is used to create resolver relative to base path."],["h2",{"id":"resolvepath"},["code",{},"resolvePath"]],["p",{},"Resolves full path to a file or directory respecting Nuxt alias and extensions options. If path could not be resolved, normalized input path will be returned."],["h3",{"id":"usage"},"Usage"],["pre",{"className":"language-ts shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"import { defineNuxtModule, resolvePath } from ''@nuxt/kit''\n\nexport default defineNuxtModule({\n  async setup () {\n    const entrypoint = await resolvePath(''@unhead/vue'')\n    console.log(`Unhead entrypoint is ${entrypoint}`)\n  },\n})\n","language":"ts","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s8R28"},"import"],["span",{"class":"sDfIl"}," {"],["span",{"class":"sZSNi"}," defineNuxtModule"],["span",{"class":"sDfIl"},","],["span",{"class":"sZSNi"}," resolvePath"],["span",{"class":"sDfIl"}," }"],["span",{"class":"s8R28"}," from"],["span",{"class":"sDfIl"}," ''"],["span",{"class":"sGFVr"},"@nuxt/kit"],["span",{"class":"sDfIl"},"''\n"]],["span",{"class":"line","line":2},["span",{"emptyLinePlaceholder":true},"\n"]],["span",{"class":"line","line":3},["span",{"class":"s8R28"},"export"],["span",{"class":"s8R28"}," default"],["span",{"class"