INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/live/3.api/5.kit/16.layers.md', 'Layers', '{"type":"minimark","value":[["p",{},"Nuxt layers provide a powerful way to share and extend functionality across projects. When working with layers in modules, you often need to access directory paths from each layer. Nuxt Kit provides the ",["code",{},"getLayerDirectories"]," utility to access resolved directory paths for all layers in your Nuxt application."],["h2",{"id":"getlayerdirectories"},["code",{},"getLayerDirectories"]],["p",{},"Get the resolved directory paths for all layers in a Nuxt application. This function provides a structured way to access layer directories without directly accessing the private ",["code",{},"nuxt.options._layers"]," property."],["h3",{"id":"usage"},"Usage"],["pre",{"className":"language-ts shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"import { defineNuxtModule, getLayerDirectories } from ''@nuxt/kit''\n\nexport default defineNuxtModule({\n  setup() {\n    const layerDirs = getLayerDirectories()\n    \n    // Access directories from all layers\n    for (const [index, layer] of layerDirs.entries()) {\n      console.log(`Layer ${index}:`)\n      console.log(`  Root: ${layer.root}`)\n      console.log(`  App: ${layer.app}`)\n      console.log(`  Server: ${layer.server}`)\n      console.log(`  Pages: ${layer.appPages}`)\n      // ... other directories\n    }\n  }\n})\n","language":"ts","meta":"twoslash","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s8R28"},"import"],["span",{"class":"sDfIl"}," {"],["span",{"class":"sZSNi"}," defineNuxtModule"],["span",{"class":"sDfIl"},","],["span",{"class":"sZSNi"}," getLayerDirectories"],["span",{"class":"sDfIl"}," }"],["span",{"class":"s8R28"}," from"],["span",{"class":"sDfIl"}," ''"],["span",{"class":"sGFVr"},"@nuxt/kit"],["span",{"class":"sDfIl"},"''\n"]],["span",{"class":"line","line":2},["span",{