INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/live/3.api/5.kit/3.compatibility.md', 'Compatibility', '{"type":"minimark","value":[["p",{},"Nuxt Kit utilities can be used in Nuxt 3, Nuxt 2 with Bridge and even Nuxt 2 without Bridge. To make sure your module is compatible with all versions, you can use the ",["code",{},"checkNuxtCompatibility"],", ",["code",{},"assertNuxtCompatibility"]," and ",["code",{},"hasNuxtCompatibility"]," functions. They will check if the current Nuxt version meets the constraints you provide. Also you can use ",["code",{},"isNuxt2"],", ",["code",{},"isNuxt3"]," and ",["code",{},"getNuxtVersion"]," functions for more granular checks."],["h2",{"id":"checknuxtcompatibility"},["code",{},"checkNuxtCompatibility"]],["p",{},"Checks if constraints are met for the current Nuxt version. If not, returns an array of messages. Nuxt 2 version also checks for ",["code",{},"bridge"]," support."],["h3",{"id":"usage"},"Usage"],["pre",{"className":"language-ts shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"import { defineNuxtModule, checkNuxtCompatibility } from ''@nuxt/kit''\n\nexport default defineNuxtModule({\n  async setup (_options, nuxt) {\n    const issues = await checkNuxtCompatibility({ nuxt: ''^2.16.0'' }, nuxt)\n    if (issues.length) {\n      console.warn(''Nuxt compatibility issues found:\\n'' + issues.toString())\n    } else {\n      // do something\n    }\n  }\n})\n","language":"ts","meta":"twoslash","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s8R28"},"import"],["span",{"class":"sDfIl"}," {"],["span",{"class":"sZSNi"}," defineNuxtModule"],["span",{"class":"sDfIl"},","],["span",{"class":"sZSNi"}," checkNuxtCompatibility"],["span",{"class":"sDfIl"}," }"],["span",{"class":"s8R28"}," from"],["span",{"class":"sDfIl"}," ''"],["span",{"class":"sGFVr"},"@nuxt/kit"],["span",{"class":"sDfIl"},"''\n"]],["span",{"class":"line","line":2},["span"