INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/live/3.api/5.kit/9.plugins.md', 'Plugins', '{"type":"minimark","value":[["p",{},"Plugins are self-contained code that usually add app-level functionality to Vue. In Nuxt, plugins are automatically imported from the ",["code",{},"app/plugins/"]," directory. However, if you need to ship a plugin with your module, Nuxt Kit provides the ",["code",{},"addPlugin"]," and ",["code",{},"addPluginTemplate"]," methods. These utils allow you to customize the plugin configuration to better suit your needs."],["h2",{"id":"addplugin"},["code",{},"addPlugin"]],["p",{},"Registers a Nuxt plugin and adds it to the plugins array."],["tip",{"icon":"i-lucide-video","target":"_blank","to":"https://vueschool.io/lessons/injecting-plugins?friend=nuxt"},["p",{},"Watch Vue School video about ",["code",{},"addPlugin"],"."]],["h3",{"id":"usage"},"Usage"],["pre",{"className":"language-ts shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"import { addPlugin, createResolver, defineNuxtModule } from ''@nuxt/kit''\n\nexport default defineNuxtModule({\n  setup () {\n    const { resolve } = createResolver(import.meta.url)\n\n    addPlugin({\n      src: resolve(''runtime/plugin.js''),\n      mode: ''client'',\n    })\n  },\n})\n","language":"ts","meta":"twoslash","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s8R28"},"import"],["span",{"class":"sDfIl"}," {"],["span",{"class":"sZSNi"}," addPlugin"],["span",{"class":"sDfIl"},","],["span",{"class":"sZSNi"}," createResolver"],["span",{"class":"sDfIl"},","],["span",{"class":"sZSNi"}," defineNuxtModule"],["span",{"class":"sDfIl"}," }"],["span",{"class":"s8R28"}," from"],["span",{"class":"sDfIl"}," ''"],["span",{"class":"sGFVr"},"@nuxt/kit"],["span",{"class":"sDfIl"},"''\n"]],["span",{"class":"line","line":2},["span",{"emptyLinePlaceholder":true},"\n"]],["span",{"class":"line","line":3},["span",{"class":"s8R2