INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/live/6.bridge/1.overview.md', 'Overview', '{"type":"minimark","value":[["note",{},["p",{},"If you''re starting a fresh Nuxt 3 project, please skip this section and go to ",["a",{"href":"/docs/getting-started/introduction"},"Nuxt 3 Installation"],"."]],["warning",{},["p",{},"Nuxt Bridge provides identical features to Nuxt 3 (",["a",{"href":"/docs/guide/concepts/auto-imports"},"docs"],") but there are some limitations, notably that ",["a",{"href":"/docs/api/composables/use-async-data"},["code",{},"useAsyncData"]]," and ",["a",{"href":"/docs/api/composables/use-fetch"},["code",{},"useFetch"]]," composables are not available. Please read the rest of this page for details."]],["p",{},"Bridge is a forward-compatibility layer that allows you to experience many of the new Nuxt 3 features by simply installing and enabling a Nuxt module."],["p",{},"Using Nuxt Bridge, you can make sure your project is (almost) ready for Nuxt 3 and you can gradually proceed with the transition to Nuxt 3."],["h2",{"id":"first-step"},"First Step"],["h3",{"id":"upgrade-nuxt-2"},"Upgrade Nuxt 2"],["p",{},"Make sure your dev server (",["code",{},"nuxt dev"],") isn''t running, remove any package lock files (",["code",{},"package-lock.json"]," and ",["code",{},"yarn.lock"],"), and install the latest Nuxt 2 version:"],["pre",{"className":"language-diff shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"- \"nuxt\": \"^2.16.3\"\n+ \"nuxt\": \"^2.17.3\"\n","filename":"package.json","language":"diff","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"sDfIl"},"-"],["span",{"class":"sRlkE"}," \"nuxt\": \"^2.16.3\"\n"]],["span",{"class":"line","line":2},["span",{"class":"sDfIl"},"+"],["span",{"class":"sGFVr"}," \"nuxt\": \"^2.17.3\"\n"]]]],["p",{},"Then, reinstall your dependencies:"],["code-group",{"sync":"pm"},["pre",{"className":"language-bash shiki