. Using TypeScript by renaming to ",["a",{"href":"/docs/guide/directory-structure/nuxt-config"},["code",{},"nuxt.config.ts"]]," is also possible and recommended."],["pre",{"className":"language-ts shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"import { defineNuxtConfig } from ''@nuxt/bridge''\n\nexport default defineNuxtConfig({\n  bridge: false\n})\n","filename":"nuxt.config.ts","language":"ts","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s8R28"},"import"],["span",{"class":"sDfIl"}," {"],["span",{"class":"sZSNi"}," defineNuxtConfig"],["span",{"class":"sDfIl"}," }"],["span",{"class":"s8R28"}," from"],["span",{"class":"sDfIl"}," ''"],["span",{"class":"sGFVr"},"@nuxt/bridge"],["span",{"class":"sDfIl"},"''\n"]],["span",{"class":"line","line":2},["span",{"emptyLinePlaceholder":true},"\n"]],["span",{"class":"line","line":3},["span",{"class":"s8R28"},"export"],["span",{"class":"s8R28"}," default"],["span",{"class":"s3cPz"}," defineNuxtConfig"],["span",{"class":"sZSNi"},"("],["span",{"class":"sDfIl"},"{\n"]],["span",{"class":"line","line":4},["span",{"class":"sRlkE"},"  bridge"],["span",{"class":"sDfIl"},":"],["span",{"class":"sbKd-"}," false\n"]],["span",{"class":"line","line":5},["span",{"class":"sDfIl"},"}"],["span",{"class":"sZSNi"},")\n"]]]],["h3",{"id":"update-commands"},"Update Commands"],["p",{},"The ",["code",{},"nuxt"]," command should now be changed to the ",["code",{},"nuxt2"]," command."],["pre",{"className":"language-diff shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"{\n  \"scripts\": {\n-   \"dev\": \"nuxt\",\n+   \"dev\": \"nuxt2\",\n-   \"build\": \"nuxt build\",\n+   \"build\": \"nuxt2 build\",\n-   \"start\": \"nuxt start\",\n+   \"start\": \"nuxt2 start\"\n  }\n}\n","language":"diff","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"sZSNi"},"{\n