INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/live/7.migration/.navigation.yml', 'Migrate to Nuxt 3', '{"title":"Migrate to Nuxt 3","titleTemplate":"Migrate to Nuxt 3: %s","icon":"i-lucide-circle-arrow-up"}', NULL, 'yml', NULL, '{"icon":"i-lucide-circle-arrow-up"}', 'true', '/nuxt/nuxtcore/live/migration/.navigation', '{"title":"Migrate to Nuxt 3"}', 'nuxt/nuxtcore/live/7.migration/.navigation', 'Migrate to Nuxt 3: %s', 'vvU-7beGrQYlv6O_6wgxKAjIBhQY2XgzYiocap-uaZI'); -- vvU-7beGrQYlv6O_6wgxKAjIBhQY2XgzYiocap-uaZI
INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/live/7.migration/1.overview.md', 'Overview', '{"type":"minimark","value":[["p",{},"There are significant changes when migrating a Nuxt 2 app to Nuxt 3, although you can expect migration to become more straightforward as we move toward a stable release."],["note",{},["p",{},"This migration guide is under progress to align with the development of Nuxt 3."]],["p",{},"Some of these significant changes include:"],["ol",{},["li",{},"Moving from Vue 2 to Vue 3, including defaulting to the Composition API and script setup."],["li",{},"Moving from webpack 4 and Babel to Vite or webpack 5 and esbuild."],["li",{},"Moving from a runtime Nuxt dependency to a minimal, standalone server compiled with nitropack."]],["tip",{},["p",{},"If you need to remain on Nuxt 2, but want to benefit from Nuxt 3 features in Nuxt 2, you can alternatively check out ",["a",{"href":"/docs/bridge/overview"},"how to get started with Bridge"],"."]],["h2",{"id":"next-steps"},"Next Steps"],["ul",{},["li",{},"Learn about differences in ",["a",{"href":"/docs/migration/configuration"},"configuration"]]]],"toc":{"title":"","searchDepth":2,"depth":2,"links":[{"id":"next-steps","depth":2,"text":"Next Steps"}]}}', 'Nuxt 3 is a complete rewrite of Nuxt 2, and also based on a new set of underlying technologies.', 'md', NULL, '{}', 'true', '/nuxt/nuxtcore/live/migration/overview', '{"title":"Overview","description":"Nuxt 3 is a complete rewrite of Nuxt 2, and also based on a new set of underlying technologies."}', 'nuxt/nuxtcore/live/7.migration/1.overview', NULL, 'fwfgsHJHoiyuaYm6jnjjWHotzk81q35jTOXBZI5wZuo'); -- fwfgsHJHoiyuaYm6jnjjWHotzk81q35jTOXBZI5wZuo
INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/live/7.migration/10.bundling.md', 'Build Tooling', '{"type":"minimark","value":[["p",{},"We use the following build tools by default:"],["ul",{},["li",{},["a",{"href":"https://vite.dev","rel":["nofollow"]},"Vite"]," or ",["a",{"href":"https://webpack.js.org","rel":["nofollow"]},"webpack"]],["li",{},["a",{"href":"https://rollupjs.org","rel":["nofollow"]},"Rollup"]],["li",{},["a",{"href":"https://postcss.org","rel":["nofollow"]},"PostCSS"]],["li",{},["a",{"href":"https://esbuild.github.io","rel":["nofollow"]},"esbuild"]]],["p",{},"For this reason, most of your previous ",["code",{},"build"]," configuration in ",["code",{},"nuxt.config"]," will now be ignored, including any custom babel configuration."],["p",{},"If you need to configure any of Nuxt''s build tools, you can do so in your ",["code",{},"nuxt.config"],", using the new top-level ",["code",{},"vite"],", ",["code",{},"webpack"]," and ",["code",{},"postcss"]," keys."],["p",{},"In addition, Nuxt ships with TypeScript support."],["read-more",{"to":"/docs/guide/concepts/typescript"}],["h2",{"id":"steps"},"Steps"],["ol",{},["li",{},"Remove ",["code",{},"@nuxt/typescript-build"]," and ",["code",{},"@nuxt/typescript-runtime"]," from your dependencies and modules."],["li",{},"Remove any unused babel dependencies from your project."],["li",{},"Remove any explicit core-js dependencies."],["li",{},"Migrate ",["code",{},"require"]," to ",["code",{},"import"],"."]]],"toc":{"title":"","searchDepth":2,"depth":2,"links":[{"id":"steps","depth":2,"text":"Steps"}]}}', 'Learn how to migrate from Nuxt 2 to Nuxt 3 build tooling.', 'md', NULL, '{}', 'true', '/nuxt/nuxtcore/live/migration/bundling', '{"title":"Build Tooling","description":"Learn how to migrate from Nuxt 2 to Nuxt 3 build tooling."}', 'nuxt/nuxtcore/live/7.migration/10.bundling', NULL, 'Gx9kii2op6RgxN2O9HJawuaikt4qne5O6GFylnP0xNM'); -- Gx9kii2op6RgxN2O9HJawuaikt4qne5O6GFylnP0xNM
INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/live/7.migration/11.server.md', 'Server', '{"type":"minimark","value":[["p",{},"In a built Nuxt 3 application, there is no runtime Nuxt dependency. That means your site will be highly performant, and ultra-slim. But it also means you can no longer hook into runtime Nuxt server hooks."],["read-more",{"to":"/docs/guide/concepts/server-engine"}],["h2",{"id":"steps"},"Steps"],["ol",{},["li",{},"Remove the ",["code",{},"render"]," key in your ",["code",{},"nuxt.config"],"."],["li",{},"Any files in ",["code",{},"~/server/api"]," and ",["code",{},"~/server/middleware"]," will be automatically registered; you can remove them from your ",["code",{},"serverMiddleware"]," array."],["li",{},"Update any other items in your ",["code",{},"serverMiddleware"]," array to point to files or npm packages directly, rather than using inline functions."]],["read-more",{"to":"/docs/guide/directory-structure/server"}],["read-more",{"to":"/docs/guide/going-further/hooks#server-hooks-runtime"}]],"toc":{"title":"","searchDepth":2,"depth":2,"links":[{"id":"steps","depth":2,"text":"Steps"}]}}', 'Learn how to migrate from Nuxt 2 to Nuxt 3 server.', 'md', NULL, '{}', 'true', '/nuxt/nuxtcore/live/migration/server', '{"title":"Server","description":"Learn how to migrate from Nuxt 2 to Nuxt 3 server."}', 'nuxt/nuxtcore/live/7.migration/11.server', NULL, 'AC60fW85wP_YtSXehqxh7u5VHvaavuS9k-v7YYKlpjA'); -- AC60fW85wP_YtSXehqxh7u5VHvaavuS9k-v7YYKlpjA
