INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/live/7.migration/20.module-authors.md', 'Modules', '{"type":"minimark","value":[["h2",{"id":"module-compatibility"},"Module Compatibility"],["p",{},"Nuxt 3 has a basic backward compatibility layer for Nuxt 2 modules using ",["code",{},"@nuxt/kit"]," auto wrappers. But there are usually steps to follow to make modules compatible with Nuxt 3 and sometimes, using Nuxt Bridge is required for cross-version compatibility."],["p",{},"We have prepared a ",["a",{"href":"/docs/guide/going-further/modules"},"Dedicated Guide"]," for authoring Nuxt 3 ready modules using ",["code",{},"@nuxt/kit"],". Currently best migration path is to follow it and rewrite your modules. Rest of this guide includes preparation steps if you prefer to avoid a full rewrite yet making modules compatible with Nuxt 3."],["tip",{"icon":"i-lucide-puzzle","to":"/modules"},["p",{},"Explore Nuxt 3 compatible modules."]],["h3",{"id":"plugin-compatibility"},"Plugin Compatibility"],["p",{},"Nuxt 3 plugins are ",["strong",{},"not"]," fully backward compatible with Nuxt 2."],["read-more",{"to":"/docs/guide/directory-structure/plugins"}],["h3",{"id":"vue-compatibility"},"Vue Compatibility"],["p",{},"Plugins or components using the Composition API need exclusive Vue 2 or Vue 3 support."],["p",{},"By using ",["a",{"href":"https://github.com/vueuse/vue-demi","rel":["nofollow"]},"vue-demi"]," they should be compatible with both Nuxt 2 and 3."],["h2",{"id":"module-migration"},"Module Migration"],["p",{},"When Nuxt 3 users add your module, you will not have access to the module container (",["code",{},"this.*"],") so you will need to use utilities from ",["code",{},"@nuxt/kit"]," to access the container functionality."],["h3",{"id":"test-with-nuxtbridge"},"Test with ",["code",{},"@nuxt/bridge"]],["p",{},"Migrating to ",["code",{},"@nuxt/bridge"]," is the first and most important step for supporting Nuxt 3."],["p",{},"If you have a fixture or example in your module, add ",["code",{},"@nuxt/bridge"]," package to its config (see ",["a",{"href":"/docs/bridge/overview#update-nuxtconfig"},"example"],")"],["h3",{"id":"migrate-from-commonjs-to-esm"},"Migrate from CommonJS to ESM"],["p",{},"Nuxt 3 natively supports TypeScript and ECMAScript Modules. Please check ",["a",{"href":"/docs/guide/concepts/esm"},"Native ES Modules"]," for more info and upgrading."],["h3",{"id":"ensure-plugins-default-export"},"Ensure Plugins Default Export"],["p",{},"If you inject a Nuxt plugin that does not have ",["code",{},"export default"]," (such as global Vue plugins), ensure you add ",["code",{},"export default () => { }"]," to the end of it."],["code-group",{},["pre",{"className":"language-js shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"// ~/plugins/vuelidate.js\nimport Vue from ''vue''\nimport Vuelidate from ''vuelidate''\n\nVue.use(Vuelidate)\n","filename":"Before","language":"js","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"sWuyu"},"// ~/plugins/vuelidate.js\n"]],["span",{"class":"line","line":2},["span",{"class":"s8R28"},"import"],["span",{"class":"sZSNi"}," Vue "],["span",{"class":"s8R28"},"from"],["span",{"class":"sDfIl"}," ''"],["span",{"class":"sGFVr"},"vue"],["span",{"class":"sDfIl"},"''\n"]],["span",{"class":"line","line":3},["span",{"class":"s8R28"},"import"],["span",{"class":"sZSNi"}," Vuelidate "],["span",{"class":"s8R28"},"from"],["span",{"class":"sDfIl"}," ''"],["span",{"class":"sGFVr"},"vuelidate"],["span",{"class":"sDfIl"},"''\n"]],["span",{"class":"line","line":4},["span",{"emptyLinePlaceholder":true},"\n"]],["span",{"class":"line","line":5},["span",{"class":"sZSNi"},"Vue"],["span",{"class":"sDfIl"},"."],["span",{"class":"s3cPz"},"use"],["span",{"class":"sZSNi"},"(Vuelidate)\n"]]]],["pre",{"className":"language-js shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"// ~/plu