INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/live/7.migration/3.auto-imports.md', 'Auto Imports', '{"type":"minimark","value":[["note",{},["p",{},"In the rest of the migration documentation, you will notice that key Nuxt and Vue utilities do not have explicit imports. This is not a typo; Nuxt will automatically import them for you, and you should get full type hinting if you have followed ",["a",{"href":"/docs/migration/configuration#typescript"},"the instructions"]," to use Nuxt''s TypeScript support."]],["p",{},["a",{"href":"/docs/guide/concepts/auto-imports"},"Read more about auto imports"]],["h2",{"id":"migration"},"Migration"],["ol",{},["li",{},"If you have been using ",["code",{},"@nuxt/components"]," in Nuxt 2, you can remove ",["code",{},"components: true"]," in your ",["code",{},"nuxt.config"],". If you had a more complex setup, then note that the component options have changed somewhat. See the ",["a",{"href":"/docs/guide/directory-structure/app/components"},"components documentation"]," for more information."]],["tip",{},["p",{},"You can look at ",["code",{},".nuxt/types/components.d.ts"]," and ",["code",{},".nuxt/types/imports.d.ts"]," to see how Nuxt has resolved your components and composable auto-imports."]]],"toc":{"title":"","searchDepth":2,"depth":2,"links":[{"id":"migration","depth":2,"text":"Migration"}]}}', 'Nuxt 3 adopts a minimal friction approach, meaning wherever possible components and composables are auto-imported.', 'md', NULL, '{}', 'true', '/nuxt/nuxtcore/live/migration/auto-imports', '{"title":"Auto Imports","description":"Nuxt 3 adopts a minimal friction approach, meaning wherever possible components and composables are auto-imported."}', 'nuxt/nuxtcore/live/7.migration/3.auto-imports', NULL, 'M4AIRh8XLUqY-mMYd2KxfS6NUiBwIC8576qy0kDxlI4'); -- M4AIRh8XLUqY-mMYd2KxfS6NUiBwIC8576qy0kDxlI4
