INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/vi/1.getting-started/01.introduction.md', 'Introduction', '{"type":"minimark","value":[["p",{},"Nuxt là một framework miễn phí và ",["a",{"href":"https://github.com/nuxt/nuxt","rel":["nofollow"]},"mã nguồn mở"]," với cách thức trực quan và có thể mở rộng để tạo ra các ứng dụng web full-stack và website an toàn kiểu dữ liệu, hiệu suất cao và sẵn sàng cho production với ",["a",{"href":"https://vuejs.org","rel":["nofollow"]},"Vue.js"],"."],["p",{},"Chúng tôi đã làm mọi thứ để bạn có thể bắt đầu viết các file ",["code",{},".vue"]," ngay từ đầu trong khi tận hưởng hot module replacement trong quá trình phát triển và một ứng dụng hiệu suất cao trong production với server-side rendering theo mặc định."],["p",{},"Nuxt không có vendor lock-in, cho phép bạn deploy ứng dụng của mình ",["a",{"href":"/blog/nuxt-on-the-edge"},["strong",{},"ở mọi nơi, thậm chí trên edge"]],"."],["tip",{},["p",{},"Nếu bạn muốn thử nghiệm với Nuxt trên trình duyệt, bạn có thể ",["a",{"href":"/docs/getting-started/installation#play-online"},"thử nó trong một trong những sandbox online của chúng tôi"],"."]],["h2",{"id":"automation-and-conventions"},"Automation and Conventions"],["p",{},"Nuxt sử dụng các quy ước và cấu trúc thư mục có ý kiến để tự động hóa các tác vụ lặp đi lặp lại và cho phép các developer tập trung vào việc phát triển tính năng. File cấu hình vẫn có thể tùy chỉnh và ghi đè các hành vi mặc định của nó."],["ul",{},["li",{},["strong",{},"File-based routing:"]," định nghĩa các route dựa trên cấu trúc thư mục ",["a",{"href":"/docs/guide/directory-structure/pages"},["code",{},"pages/"]]," của bạn. Điều này có thể giúp tổ chức ứng dụng dễ dàng hơn và tránh việc phải cấu hình route thủ công."],["li",{},["strong",{},"Code splitting:"]," Nuxt tự động chia code của bạn thành các chunk nhỏ hơn, điều này có thể giúp giảm thời gian tải ban đầu của ứng dụng."],["li",{},["strong",{},"Server-side rendering out of the box:"]," Nuxt đi kèm với khả năng SSR tích hợp sẵn, vì vậy bạn không cần phải thiết lập server riêng."],["li",{},["strong",{},"Auto-imports:"]," viết các Vue composable và component trong các thư mục tương ứng và sử dụng chúng mà không cần import với lợi ích của tree-shaking và các JS bundle được tối ưu hóa."],["li",{},["strong",{},"Data-fetching utilities:"]," Nuxt cung cấp các composable để xử lý data fetching tương thích với SSR cũng như các chiến lược khác nhau."],["li",{},["strong",{},"Zero-config TypeScript support:"]," viết code an toàn kiểu dữ liệu mà không cần học TypeScript với các type được tự động tạo và ",["code",{},"tsconfig.json"]," của chúng tôi."],["li",{},["strong",{},"Configured build tools:"]," chúng tôi sử dụng ",["a",{"href":"https://vite.dev","rel":["nofollow"]},"Vite"]," theo mặc định để hỗ trợ hot module replacement (HMR) trong development và đóng gói code cho production với các best-practice được tích hợp sẵn."]],["p",{},"Nuxt xử lý tất cả những điều này và cung cấp chức năng cả frontend và backend để bạn có thể tập trung vào điều quan trọng: ",["strong",{},"tạo ra ứng dụng web của bạn"],"."],["h2",{"id":"server-side-rendering"},"Server-Side Rendering"],["p",{},"Nuxt đi kèm với khả năng server-side rendering (SSR) tích hợp sẵn theo mặc định, mà không cần phải tự cấu hình server, điều này mang lại nhiều lợi ích cho các ứng dụng web:"],["ul",{},["li",{},["strong",{},"Thời gian tải trang ban đầu nhanh hơn:"]," Nuxt gửi một trang HTML được render đầy đủ đến trình duyệt, có thể được hiển thị ngay lập tức. Điều này có thể cung cấp thời gian tải trang nhanh hơn về mặt cảm nhận và trải nghiệm người dùng (UX) tốt hơn, đặc biệt trên các mạng chậm hoặc thiết bị yếu."],["li",{},["strong",{},"SEO được cải thiện:"]," các công cụ tìm kiếm có thể index các trang SSR tốt hơn vì nội dung HTML có sẵn ngay lập tức, thay vì yêu cầu JavaScript để render nội dung ở phía client."],["li",{},["strong",{},"Hiệu suất tốt hơn trên các thiết bị yếu:"]," nó giảm lượng JavaScript cần được tải xuống và thực thi ở phía client, điều này có thể có lợi cho các thiết bị yếu có thể gặp khó khăn khi xử lý các ứng dụng JavaScript nặng."],["li",{},["strong",{},"Khả năng truy cập tốt hơn:"]," nội dung có sẵn ngay lập tức khi tải trang ban đầu, cải thiện khả năng truy cập cho người dùng dựa vào screen reader hoặc các công nghệ hỗ trợ khác."],["li",{},["strong",{},"Caching dễ dàng hơn:"]," các trang có thể được cache ở phía server, điều này có thể cải thiện hiệu suất hơn nữa bằng cách giảm thời gian tạo và gửi nội dung đến client."]],["p",{},"Nhìn chung, server-side rendering có thể cung cấp trải nghiệm người dùng nhanh hơn và hiệu quả hơn, cũng như cải thiện tối ưu hóa công cụ tìm kiếm và khả năng truy cập."],["p",{},"Vì Nuxt là một framework linh hoạt, nó cung cấp cho bạn khả năng render tĩnh toàn bộ ứng dụng thành static hosting với ",["code",{},"nuxt generate"],", tắt SSR toàn cầu với tùy chọn ",["code",{},"ssr: false"]," hoặc tận dụng hybrid rendering bằng cách thiết lập tùy chọn ",["code",{},"routeRules"],"."],["read-more",{"title":"Nuxt rendering modes","to":"/docs/guide/concepts/rendering"}],["h3",{"id":"server-engine"},"Server engine"],["p",{},"Server engine Nuxt ",["a",{"href":"https://nitro.build/","rel":["nofollow"]},"Nitro"]," mở khóa các khả năng full-stack mới."],["p",{},"Trong development, nó sử dụng Rollup và Node.js workers cho server code và context isolation. Nó cũng tạo server API của bạn bằng cách đọc các file trong ",["code",{},"server/api/"]," và server middleware từ ",["code",{},"server/middleware/"],"."],["p",{},"Trong production, Nitro build ứng dụng và server của bạn thành một thư mục ",["code",{},".output"]," universal duy nhất. Output này nhẹ: được minify và loại bỏ khỏi bất kỳ Node.js module nào (trừ polyfill). Bạn có thể deploy output này trên bất kỳ hệ thống nào hỗ trợ JavaScript, từ Node.js, Serverless, Workers, Edge-side rendering hoặc hoàn toàn tĩnh."],["read-more",{"title":"Nuxt server engine","to":"/docs/guide/concepts/server-engine"}],["h3",{"id":"production-ready"},"Production-ready"],["p",{},"Một ứng dụng Nuxt có thể được deploy trên Node hoặc Deno server, pre-render để được host trong môi trường tĩnh, hoặc deploy đến các serverless và edge provider."],["read-more",{"title":"Deployment section","to":"/docs/getting-started/deployment"}],["h3",{"id":"modular"},"Modular"],["p",{},"Hệ thống module cho phép bạn mở rộng Nuxt với các tính năng tùy chỉnh và tích hợp với các dịch vụ bên thứ ba."],["read-more",{"title":"Nuxt Modules Concept","to":"/docs/guide/concepts/modules"}],["h3",{"id":"architecture"},"Architecture"],["p",{},"Nuxt được cấu tạo từ các ",["a",{"href":"https://github.com/nuxt/nuxt/tree/main/packages","rel":["nofollow"]},"core package"]," khác nhau:"],["ul",{},["li",{},"Core engine: ",["a",{"href":"https://github.com/nuxt/nuxt/tree/main/packages/nuxt","rel":["nofollow"]},"nuxt"]],["li",{},"Bundlers: ",["a",{"href":"https://github.com/nuxt/nuxt/tree/main/packages/vite","rel":["nofollow"]},"@nuxt/vite-builder"],", ",["a",{"href":"https://github.com/nuxt/nuxt/tree/main/packages/rspack","rel":["nofollow"]},"@nuxt/rspack-builder"]," và ",["a",{"href":"https://github.com/nuxt/nuxt/tree/main/packages/webpack","rel":["nofollow"]},"@nuxt/webpack-builder"]],["li",{},"Command line interface: ",["a",{"href":"https://github.com/nuxt/cli","rel":["nofollow"]},"@nuxt/cli"]],["li",{},"Server engine: ",["a",{"href":"https://github.com/nitrojs/nitro","rel":["nofollow"]},"nitro"]],["li",{},"Development kit: ",["a",{"href":"https://github.com/nuxt/nuxt/tree/main/packages/kit","rel":["nofollow"]},"@nuxt/kit"]]],["p",{},"Chúng tôi khuyến nghị đọc từng khái niệm để có cái nhìn đầy đủ về khả năng của Nuxt và phạm vi của từng package."]],"toc":{"title":"","searchDepth":2,"depth":2,"links":[{"id":"automation-and-conventions","depth":2,"text":"Automation and Conventions"},{"id":"server-side-rendering","depth":2,"text":"Server-Side Rendering","children":[{"id":"server-engine","depth":3,"text":"Server engine"},{"id":"production-ready","depth":3,"text":"Production-ready"},{"id":"modular","depth":3,"text":"Modular"},{"id":"architecture","depth":3,"text":"Architecture"}]}]}}', 'Nuxt''s goal is to make web development intuitive and performant with a great Developer Experience in mind.', 'md', NULL, '{}', '{"icon":"i-lucide-info"}', '/nuxt/nuxtcore/vi/getting-started/introduction', '{"title":"Introduction","description":"Nuxt''s goal is to make web development intuitive and performant with a great Developer Experience in mind."}', 'nuxt/nuxtcore/vi/1.getting-started/01.introduction', NULL, 'qVMlSkJqkIpp2mPCgNz6MY3ura5G1uo60O2z2W5sToc'); -- qVMlSkJqkIpp2mPCgNz6MY3ura5G1uo60O2z2W5sToc
INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/vi/1.getting-started/02.installation.md', 'Installation', '{"type":"minimark","value":[["h2",{"id":"play-online"},"Play Online"],["p",{},"If you just want to play around with Nuxt in your browser without setting up a project, you can use one of our online sandboxes:"],["card-group",{},["card",{"icon":"i-simple-icons-stackblitz","target":"_blank","title":"Open on StackBlitz","to":"https://nuxt.new/s/v4"}],["card",{"icon":"i-simple-icons-codesandbox","target":"_blank","title":"Open on CodeSandbox","to":"https://nuxt.new/c/v4"}]],["p",{},"Or follow the steps below to set up a new Nuxt project on your computer."],["h2",{"id":"new-project"},"New Project"],["h4",{"id":"prerequisites"},"Prerequisites"],["ul",{},["li",{},["strong",{},"Node.js"]," - ",["a",{"href":"https://nodejs.org/en","rel":["nofollow"]},["code",{},"20.x"]]," or newer (but we recommend the ",["a",{"href":"https://github.com/nodejs/release#release-schedule","rel":["nofollow"]},"active LTS release"],")"],["li",{},["strong",{},"Text editor"]," - There is no IDE requirement, but we recommend ",["a",{"href":"https://code.visualstudio.com/","rel":["nofollow"]},"Visual Studio Code"]," with the ",["a",{"href":"https://marketplace.visualstudio.com/items?itemName=Vue.volar","rel":["nofollow"]},"official Vue extension"]," (previously known as Volar) or ",["a",{"href":"https://www.jetbrains.com/webstorm/","rel":["nofollow"]},"WebStorm"],", which, along with ",["a",{"href":"https://www.jetbrains.com/ides/","rel":["nofollow"]},"other JetBrains IDEs"],", offers great Nuxt support right out-of-the-box."],["li",{},["strong",{},"Terminal"]," - In order to run Nuxt commands"]],["note",{},["details",{},["summary",{},"Additional notes for an optimal setup:"],["ul",{},["li",{},["strong",{},"Node.js"],": Make sure to use an even numbered version (18, 20, etc)"],["li",{},["strong",{},"Nuxtr"],": Install the community-developed ",["a",{"href":"https://marketplace.visualstudio.com/items?itemName=Nuxtr.nuxtr-vscode","rel":["nofollow"]},"Nuxtr extension"]],["li",{},["strong",{},"WSL"],": If you are using Windows and experience slow HMR, you may want to try using ",["a",{"href":"https://docs.microsoft.com/en-us/windows/wsl/install","rel":["nofollow"]},"WSL (Windows Subsystem for Linux)"]," which may solve some performance issues."]]]],["p",{},"Open a terminal (if you''re using ",["a",{"href":"https://code.visualstudio.com","rel":["nofollow"]},"Visual Studio Code"],", you can open an ",["a",{"href":"https://code.visualstudio.com/docs/editor/integrated-terminal","rel":["nofollow"]},"integrated terminal"],") and use the following command to create a new starter project:"],["code-group",{"sync":"pm"},["pre",{"className":"language-bash shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"npm create nuxt@latest <project-name>\n","filename":"npm","language":"bash","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s52Pk"},"npm"],["span",{"class":"sGFVr"}," create"],["span",{"class":"sGFVr"}," nuxt@latest"],["span",{"class":"sDfIl"}," <"],["span",{"class":"sGFVr"},"project-nam"],["span",{"class":"sZSNi"},"e"],["span",{"class":"sDfIl"},">\n"]]]],["pre",{"className":"language-bash shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"yarn create nuxt@latest <project-name>\n","filename":"yarn","language":"bash","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s52Pk"},"yarn"],["span",{"class":"sGFVr"}," create"],["span",{"class":"sGFVr"}," nuxt@latest"],["span",{"class":"sDfIl"}," <"],["span",{"class":"sGFVr"},"project-nam"],["span",{"class":"sZSNi"},"e"],["span",{"class":"sDfIl"},">\n"]]]],["pre",{"className":"language-bash shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"pnpm create nuxt@latest <project-name>\n","filename":"pnpm","language":"bash","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s52Pk"},"pnpm"],["span",{"class":"sGFVr"}," create"],["span",{"class":"sGFVr"}," nuxt@latest"],["span",{"class":"sDfIl"}," <"],["span",{"class":"sGFVr"},"project-nam"],["span",{"class":"sZSNi"},"e"],["span",{"class":"sDfIl"},">\n"]]]],["pre",{"className":"language-bash shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"bun create nuxt@latest <project-name>\n","filename":"bun","language":"bash","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s52Pk"},"bun"],["span",{"class":"sGFVr"}," create"],["span",{"class":"sGFVr"}," nuxt@latest"],["span",{"class":"sDfIl"}," <"],["span",{"class":"sGFVr"},"project-nam"],["span",{"class":"sZSNi"},"e"],["span",{"class":"sDfIl"},">\n"]]]],["pre",{"className":"language-bash shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"deno -A npm:create-nuxt@latest <project-name>\n","filename":"deno","language":"bash","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s52Pk"},"deno"],["span",{"class":"sGFVr"}," -A"],["span",{"class":"sGFVr"}," npm:create-nuxt@latest"],["span",{"class":"sDfIl"}," <"],["span",{"class":"sGFVr"},"project-nam"],["span",{"class":"sZSNi"},"e"],["span",{"class":"sDfIl"},">\n"]]]]],["tip",{},["p",{},"Alternatively, you can find other starters or themes by opening ",["a",{"href":"https://nuxt.new","rel":["nofollow"]},"nuxt.new"]," and following the instructions there."]],["p",{},"Open your project folder in Visual Studio Code:"],["pre",{"className":"language-bash shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"code <project-name>\n","filename":"Terminal","language":"bash","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class