INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/vi/1.getting-started/10.data-fetching.md', 'Data Fetching', '{"type":"minimark","value":[["p",{},"Nuxt đi kèm với hai composable và một thư viện tích hợp để thực hiện data-fetching trong môi trường trình duyệt hoặc server: ",["code",{},"useFetch"],", ",["a",{"href":"/docs/api/composables/use-async-data"},["code",{},"useAsyncData"]]," và ",["code",{},"$fetch"],"."],["p",{},"Tóm lại:"],["ul",{},["li",{},["a",{"href":"/docs/api/utils/dollarfetch"},["code",{},"$fetch"]]," là cách đơn giản nhất để tạo một network request."],["li",{},["a",{"href":"/docs/api/composables/use-fetch"},["code",{},"useFetch"]]," là một wrapper xung quanh ",["code",{},"$fetch"]," chỉ fetch data một lần trong ",["a",{"href":"/docs/guide/concepts/rendering#universal-rendering"},"universal rendering"],"."],["li",{},["a",{"href":"/docs/api/composables/use-async-data"},["code",{},"useAsyncData"]]," tương tự như ",["code",{},"useFetch"]," nhưng cung cấp kiểm soát chi tiết hơn."]],["p",{},"Cả ",["code",{},"useFetch"]," và ",["code",{},"useAsyncData"]," đều chia sẻ một tập hợp chung các tùy chọn và patterns mà chúng ta sẽ chi tiết trong các phần cuối."],["h2",{"id":"the-need-for-usefetch-and-useasyncdata"},"The need for ",["code",{},"useFetch"]," and ",["code",{},"useAsyncData"]],["p",{},"Nuxt là một framework có thể chạy mã isomorphic (hoặc universal) trong cả môi trường server và client. Nếu hàm ",["a",{"href":"/docs/api/utils/dollarfetch"},["code",{},"$fetch"]]," được sử dụng để thực hiện data fetching trong setup function của một Vue component, điều này có thể khiến data được fetch hai lần, một lần trên server (để render HTML) và một lần nữa trên client (khi HTML được hydrated). Điều này có thể gây ra vấn đề hydration, tăng thời gian để tương tác và gây ra hành vi không thể đoán trước."],["p",{},"Các composable ",["a",{"href":"/docs/api/composables/use-fetch"},["code",{},"useFetch"]]," và ",["a",{"href":"/docs/api/composables/use-async-da