INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/vi/1.getting-started/14.layers.md', 'Layers', '{"type":"minimark","value":[["p",{},"Một trong những tính năng cốt lõi của Nuxt là layers và hỗ trợ extending. Bạn có thể mở rộng một ứng dụng Nuxt mặc định để tái sử dụng component, util, và cấu hình. Cấu trúc layers gần như giống hệt với một ứng dụng Nuxt tiêu chuẩn, điều này khiến chúng dễ tác giả và bảo trì."],["h2",{"id":"use-cases"},"Use Cases"],["ul",{},["li",{},"Chia sẻ các preset cấu hình có thể tái sử dụng qua các dự án sử dụng ",["code",{},"nuxt.config"]," và ",["code",{},"app.config"]],["li",{},"Tạo một thư viện component sử dụng thư mục ",["a",{"href":"/docs/guide/directory-structure/components"},["code",{},"components/"]]],["li",{},"Tạo thư viện utility và composable sử dụng thư mục ",["a",{"href":"/docs/guide/directory-structure/composables"},["code",{},"composables/"]]," và ",["a",{"href":"/docs/guide/directory-structure/utils"},["code",{},"utils/"]]],["li",{},"Tạo các preset Nuxt module"],["li",{},"Chia sẻ thiết lập tiêu chuẩn qua các dự án"],["li",{},"Tạo theme Nuxt"],["li",{},"Cải thiện tổ chức code bằng cách triển khai kiến trúc modular và hỗ trợ pattern Domain-Driven Design (DDD) trong các dự án quy mô lớn."]],["h2",{"id":"usage"},"Usage"],["p",{},"Theo mặc định, bất kỳ layer nào trong dự án của bạn trong thư mục ",["code",{},"~~/layers"]," sẽ tự động được đăng ký làm layer trong dự án của bạn."],["note",{},["p",{},"Layer auto-registration được giới thiệu trong Nuxt v3.12.0."]],["p",{},"Ngoài ra, các alias layer có tên cho ",["code",{},"srcDir"]," của mỗi layer này sẽ tự động được tạo. Ví dụ, bạn sẽ có thể truy cập layer ",["code",{},"~~/layers/test"]," qua ",["code",{},"#layers/test"],"."],["note",{},["p",{},"Named layer alias được giới thiệu trong Nuxt v3.16.0."]],["p",{},"Ngoài ra, bạn có thể mở rộng từ một layer bằng cách thêm thuộc tính ",["a",{"href":"/docs/api/nuxt-config#extends"},"extends"]," vào file ",["a",{"href":"/docs/guide/directory-structure/nuxt-config"},["code",{},"nuxt.config"]]," của bạn."],["pre",{"className":"language-ts shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"export default defineNuxtConfig({\n  extends: [\n    ''../base'',                     // Extend từ một layer cục bộ\n    ''@my-themes/awesome'',          // Extend từ một npm package đã cài đặt\n    ''github:my-themes/awesome#v1'', // Extend từ một git repository\n  ]\n})\n","filename":"nuxt.config.ts","language":"ts","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s8R28"},"export"],["span",{"class":"s8R28"}," default"],["span",{"class":"s3cPz"}," defineNuxtConfig"],["span",{"class":"sZSNi"},"("],["span",{"class":"sDfIl"},"{\n"]],["span",{"class":"line","line":2},["span",{"class":"sRlkE"},"  extends"],["span",{"class":"sDfIl"},":"],["span",{"class":"sZSNi"}," [\n"]],["span",{"class":"line","line":3},["span",{"class":"sDfIl"},"    ''"],["span",{"class":"sGFVr"},"../base"],["span",{"class":"sDfIl"},"''"],["span",{"class":"sDfIl"},","],["span",{"class":"sWuyu"},"                     // Extend từ một layer cục bộ\n"]],["span",{"class":"line","line":4},["span",{"class":"sDfIl"},"    ''"],["span",{"class":"sGFVr"},"@my-themes/awesome"],["span",{"class":"sDfIl"},"''"],["span",{"class":"sDfIl"},","],["span",{"class":"sWuyu"},"          // Extend từ một npm package đã cài đặt\n"]],["span",{"class":"line","line":5},["span",{"class":"sDfIl"},"    ''"],["span",{"class":"sGFVr"},"github:my-themes/awesome#v1"],["span",{"class":"sDfIl"},"''"],["span",{"class":"sDfIl"},","],["span",{"class":"sWuyu"}," // Extend từ một git repository\n"]],["span",{"class":"line","line":6},["span",{"class":"sZSNi"},"  ]\n"]],["span",{"class":"line","line":7},["span",{"class":"sDfIl"},"}"],["span",{"class":"sZSNi"},")\n"]]]],["p",{},"Bạn cũng có thể truyền một authentication token nếu bạn đang extending từ một GitHub repository riêng tư:"],["pre",