INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/vi/1.getting-started/15.prerendering.md', 'Prerendering', '{"type":"minimark","value":[["p",{},"Nuxt cho phép các trang được chọn từ ứng dụng của bạn được render tại thời điểm build. Nuxt sẽ phục vụ các trang đã được prebuilt khi được yêu cầu thay vì tạo chúng một cách tự động."],["read-more",{"to":"/docs/guide/concepts/rendering","title":"Nuxt rendering modes"}],["h2",{"id":"crawl-based-pre-rendering"},"Crawl-based Pre-rendering"],["p",{},"Sử dụng lệnh ",["a",{"href":"/docs/api/commands/generate"},["code",{},"nuxt generate"]]," để build và pre-render ứng dụng của bạn bằng cách sử dụng ",["a",{"href":"/docs/guide/concepts/server-engine"},"Nitro"]," crawler. Lệnh này tương tự như ",["code",{},"nuxt build"]," với tùy chọn ",["code",{},"nitro.static"]," được đặt thành ",["code",{},"true"],", hoặc chạy ",["code",{},"nuxt build --prerender"],"."],["p",{},"Điều này sẽ build trang web của bạn, khởi tạo một instance nuxt, và theo mặc định, prerender trang root ",["code",{},"/"]," cùng với bất kỳ trang nào của trang web mà nó liên kết đến, bất kỳ trang nào của trang web mà chúng liên kết đến, và tiếp tục như vậy."],["code-group",{"sync":"pm"},["pre",{"className":"language-bash shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"npx nuxt generate\n","filename":"npm","language":"bash","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s52Pk"},"npx"],["span",{"class":"sGFVr"}," nuxt"],["span",{"class":"sGFVr"}," generate\n"]]]],["pre",{"className":"language-bash shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"yarn nuxt generate\n","filename":"yarn","language":"bash","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s52Pk"},"yarn"],["span",{"class":"sGFVr"}," nuxt"],["span",{"class":"sGFVr"}," generate\n"]]]],["pre",{"clas