INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/vi/2.guide/0.index.md', 'Nuxt Guide', '{"type":"minimark","value":[["card-group",{"className":["sm:grid-cols-1"]},["card",{"icon":"i-lucide-medal","title":"Key Concepts","to":"/docs/guide/concepts"},["p",{},"Discover the main concepts behind Nuxt, from auto-import, hybrid rendering to its TypeScript support."]],["card",{"icon":"i-lucide-folders","title":"Directory Structure","to":"/docs/guide/directory-structure"},["p",{},"Learn about Nuxt directory structure and what benefits each directory or file offers."]],["card",{"icon":"i-lucide-star","title":"Going Further","to":"/docs/guide/going-further"},["p",{},"Master Nuxt with advanced concepts like experimental features, hooks, modules, and more."]],["card",{"icon":"i-lucide-book-open","title":"Recipes","to":"/docs/guide/recipes"},["p",{},"Find solutions to common problems and learn how to implement them in your Nuxt project."]],["card",{"icon":"i-lucide-square-check","title":"Best Practices","to":"/docs/guide/best-practices"},["p",{},"Learn about best practices when developing with Nuxt"]]]],"toc":{"title":"","searchDepth":2,"depth":2,"links":[]}}', 'Learn how Nuxt works with in-depth guides.', 'md', NULL, '{"surround":false}', 'false', '/nuxt/nuxtcore/vi/guide', '{"title":"Nuxt Guide","description":"Learn how Nuxt works with in-depth guides."}', 'nuxt/nuxtcore/vi/2.guide/0.index', '%s', 'cXNOAHKaGyAZg-SA5X1S42WfbtQD6xM-894VrUyAfO8'); -- cXNOAHKaGyAZg-SA5X1S42WfbtQD6xM-894VrUyAfO8
