INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/vi/2.guide/1.concepts/1.auto-imports.md', 'Auto-imports', '{"type":"minimark","value":[["p",{},"Nuxt tự động import các component, composable và ",["a",{"href":"https://vuejs.org/api","rel":["nofollow"]},"Vue.js API"]," để sử dụng trong toàn bộ ứng dụng của bạn mà không cần import chúng một cách tường minh."],["pre",{"className":"language-vue shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"<script setup lang=\"ts\">\nconst count = ref(1) // ref được auto-imported\n</script>\n","filename":"app.vue","language":"vue","meta":"twoslash","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"sDfIl"},"<"],["span",{"class":"sRlkE"},"script"],["span",{"class":"smZ93"}," setup"],["span",{"class":"smZ93"}," lang"],["span",{"class":"sDfIl"},"="],["span",{"class":"sDfIl"},"\""],["span",{"class":"sGFVr"},"ts"],["span",{"class":"sDfIl"},"\""],["span",{"class":"sDfIl"},">\n"]],["span",{"class":"line","line":2},["span",{"class":"smZ93"},"const"],["span",{"class":"sZSNi"}," count "],["span",{"class":"sDfIl"},"="],["span",{"class":"s3cPz"}," ref"],["span",{"class":"sZSNi"},"("],["span",{"class":"sYRBq"},"1"],["span",{"class":"sZSNi"},") "],["span",{"class":"sWuyu"},"// ref được auto-imported\n"]],["span",{"class":"line","line":3},["span",{"class":"sDfIl"},"</"],["span",{"class":"sRlkE"},"script"],["span",{"class":"sDfIl"},">\n"]]]],["p",{},"Nhờ vào cấu trúc thư mục có định hướng của nó, Nuxt có thể tự động import các ",["a",{"href":"/docs/guide/directory-structure/components"},["code",{},"components/"]],", ",["a",{"href":"/docs/guide/directory-structure/composables"},["code",{},"composables/"]]," và ",["a",{"href":"/docs/guide/directory-structure/utils"},["code",{},"utils/"]]," của bạn."],["p",{},"Trái ngược với khai báo global truyền thống, Nuxt bảo tồn kiểu dữ liệu, hoàn thiện IDE và gợi ý, và ",["strong",{},"chỉ bao gồm những gì được sử dụng trong code production của bạn"],"."],["note",{},["p",{},"Trong tài liệu, mọi hàm không được import tường minh đều được auto-imported bởi Nuxt và có thể được sử dụng như vậy trong code của bạn. Bạn có thể tìm thấy tài liệu tham khảo cho các component, composable và utility được auto-imported trong ",["a",{"href":"/docs/api"},"phần API"],"."]],["note",{},["p",{},"Trong thư mục ",["a",{"href":"/docs/guide/directory-structure/server"},["code",{},"server"]],", Nuxt tự động import các hàm và biến được export từ ",["code",{},"server/utils/"],"."]],["note",{},["p",{},"Bạn cũng có thể tự động import các hàm được export từ các thư mục tùy chỉnh hoặc package third-party bằng cách cấu hình phần ",["a",{"href":"/docs/api/nuxt-config#imports"},["code",{},"imports"]]," trong file ",["code",{},"nuxt.config"]," của bạn."]],["h2",{"id":"built-in-auto-imports"},"Built-in Auto-imports"],["p",{},"Nuxt tự động import các hàm và composable để thực hiện ",["a",{"href":"/docs/getting-started/data-fetching"},"data fetching"],", truy cập vào ",["a",{"href":"/docs/api/composables/use-nuxt-app"},"app context"]," và ",["a",{"href":"/docs/guide/going-further/runtime-config"},"runtime config"],", quản lý ",["a",{"href":"/docs/getting-started/state-management"},"state"]," hoặc định nghĩa component và plugin."],["pre",{"className":"language-vue shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"<script setup lang=\"ts\">\n/* useFetch() được auto-imported */\nconst { data, refresh, status } = await useFetch(''/api/hello'')\n</script>\n","language":"vue","meta":"twoslash","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"sDfIl"},"<"],["span",{"class":"sRlkE"},"script"],["span",{"class":"smZ93"}," setup"],["span",{"class":"smZ93"}," lang"],["span",{"class":"sDfIl"},"="],["span",{"class":"sDfIl"},"\""],["span",{"class":"sGFVr"},"ts"],["span",{"class":"sDfIl"},"\""],["span",{"class":"sDfIl"},">\n"]],["span",{"