INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/vi/2.guide/1.concepts/4.server-engine.md', 'Server Engine', '{"type":"minimark","value":[["p",{},"Trong khi xây dựng Nuxt, chúng tôi đã tạo ra một server engine mới: ",["a",{"href":"https://nitro.build/","rel":["nofollow"]},"Nitro"],"."],["p",{},"Nó được giao kèm với nhiều tính năng:"],["ul",{},["li",{},"Hỗ trợ đa nền tảng cho Node.js, browser, service worker và nhiều hơn nữa."],["li",{},"Hỗ trợ serverless ngay từ đầu."],["li",{},"Hỗ trợ API routes."],["li",{},"Tự động code-splitting và các chunk được load bất đồng bộ."],["li",{},"Chế độ Hybrid cho static + serverless sites."],["li",{},"Development server với hot module reloading."]],["h2",{"id":"api-layer"},"API Layer"],["p",{},"Server ",["a",{"href":"/docs/guide/directory-structure/server#api-routes"},"API endpoints"]," và ",["a",{"href":"/docs/guide/directory-structure/server#server-middleware"},"Middleware"]," được thêm bởi Nitro mà bên trong sử dụng ",["a",{"href":"https://github.com/h3js/h3","rel":["nofollow"]},"h3"],"."],["p",{},"Các tính năng chính bao gồm:"],["ul",{},["li",{},"Handlers có thể trực tiếp trả về objects/arrays cho một JSON response được xử lý tự động"],["li",{},"Handlers có thể trả về promises, sẽ được awaited (",["code",{},"res.end()"]," và ",["code",{},"next()"]," cũng được hỗ trợ)"],["li",{},"Các helper functions cho body parsing, cookie handling, redirects, headers và nhiều hơn nữa"]],["p",{},"Xem ",["a",{"href":"https://github.com/h3js/h3","rel":["nofollow"]},"h3 docs"]," để biết thêm thông tin."],["read-more",{"to":"/docs/guide/directory-structure/server#server-routes"},["p",{},"Tìm hiểu thêm về API layer trong thư mục ",["code",{},"server/"],"."]],["h2",{"id":"direct-api-calls"},"Direct API Calls"],["p",{},"Nitro cho phép gọi ''trực tiếp'' các routes thông qua helper ",["a",{"href":"/docs/api/utils/dollarfetch"},["code",{},"$fetch"]]," có sẵn toàn cục. Điều này sẽ thực hiện một API call đến server nếu chạy trên browser, nhưng sẽ gọi trực tiếp function liên quan nếu chạy trên server, ",["strong",{},"tiết kiệm một API call bổ sung"],"."],["p",{},["a",{"href":"/docs/api/utils/dollarfetch"},["code",{},"$fetch"]]," API đang sử dụng ",["a",{"href":"https://github.com/unjs/ofetch","rel":["nofollow"]},"ofetch"],", với các tính năng chính bao gồm:"],["ul",{},["li",{},"Tự động parsing JSON responses (với quyền truy cập vào raw response nếu cần)"],["li",{},"Request body và params được xử lý tự động, với headers ",["code",{},"Content-Type"]," chính xác"]],["p",{},"Để biết thêm thông tin về các tính năng ",["code",{},"$fetch"],", xem ",["a",{"href":"https://github.com/unjs/ofetch","rel":["nofollow"]},"ofetch"],"."],["h2",{"id":"typed-api-routes"},"Typed API Routes"],["p",{},"Khi sử dụng API routes (hoặc middleware), Nitro sẽ tạo typings cho những routes này miễn là bạn đang trả về một giá trị thay vì sử dụng ",["code",{},"res.end()"]," để gửi một response."],["p",{},"Bạn có thể truy cập những types này khi sử dụng ",["a",{"href":"/docs/api/utils/dollarfetch"},["code",{},"$fetch()"]]," hoặc ",["a",{"href":"/docs/api/composables/use-fetch"},["code",{},"useFetch()"]],"."],["h2",{"id":"standalone-server"},"Standalone Server"],["p",{},"Nitro tạo ra một standalone server dist độc lập với ",["code",{},"node_modules"],"."],["p",{},"Server trong Nuxt 2 không standalone và yêu cầu một phần của Nuxt core được liên quan bằng cách chạy ",["code",{},"nuxt start"]," (với ",["a",{"href":"https://www.npmjs.com/package/nuxt-start","rel":["nofollow"]},["code",{},"nuxt-start"]]," hoặc ",["a",{"href":"https://www.npmjs.com/package/nuxt","rel":["nofollow"]},["code",{},"nuxt"]]," distributions) hoặc sử dụng programmatic tùy chỉnh, điều này dễ vỡ và dễ bị hỏng và không phù hợp cho môi trường serverless và service worker."],["p",{},"Nuxt tạo ra dist này khi chạy ",["code",{},"nuxt build"]," vào một thư mục ",["a",{"href":"/docs/guide/directory-structure/output"},["code",{},".output"]],"."],["p",{},"Output chứa runtime code để chạy Nuxt server của bạn trong bất kỳ môi trường nào (kể cả thử nghiệm chạy trong service worker của trình duyệt!) và phục vụ các tệp tĩnh, biến Nuxt trở thành một framework lai thực thụ cho JAMstack. Bên cạnh đó, Nuxt còn tích hợp một tầng lưu trữ gốc, hỗ trợ nhiều driver nguồn và cả tài nguyên cục bộ."],["read-more",{"to":"https://github.com/nitrojs/nitro","icon":"i-simple-icons-github","target":"_blank"},["p",{},"Read more about Nitro engine on GitHub."]]],"toc":{"title":"","searchDepth":2,"depth":2,"links":[{"id":"api-layer","depth":2,"text":"API Layer"},{"id":"direct-api-calls","depth":2,"text":"Direct API Calls"},{"id":"typed-api-routes","depth":2,"text":"Typed API Routes"},{"id":"standalone-server","depth":2,"text":"Standalone Server"}]}}', 'Nuxt is powered by a new server engine: Nitro.', 'md', NULL, '{}', 'true', '/nuxt/nuxtcore/vi/guide/concepts/server-engine', '{"title":"Server Engine","description":"Nuxt is powered by a new server engine: Nitro."}', 'nuxt/nuxtcore/vi/2.guide/1.concepts/4.server-engine', NULL, 'x1C69vuVuaJngRj0GrqfYVZzWq1LlRYdr2vgcCz6EOs'); -- x1C69vuVuaJngRj0GrqfYVZzWq1LlRYdr2vgcCz6EOs
INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/vi/2.guide/1.concepts/5.modules.md', 'Modules', '{"type":"minimark","value":[["h2",{"id":"exploring-nuxt-modules"},"Exploring Nuxt Modules"],["p",{},"Khi phát triển các ứng dụng cấp sản xuất với Nuxt, bạn có thể nhận thấy rằng chức năng cốt lõi của framework là chưa đủ. Nuxt có thể được mở rộng thông qua các tùy chọn cấu hình và plugin, nhưng việc duy trì những tùy chỉnh này trên nhiều dự án có thể tẻ nhạt, lặp lại và tốn thời gian. Mặt khác, hỗ trợ mọi nhu cầu của dự án ngay từ đầu sẽ khiến Nuxt trở nên rất phức tạp và khó sử dụng."],["p",{},"Đây là một trong những lý do tại sao Nuxt cung cấp hệ thống mô-đun cho phép mở rộng phần lõi. Các mô-đun Nuxt là các hàm async chạy tuần tự khi khởi động Nuxt ở chế độ phát triển bằng ",["a",{"href":"/docs/api/commands/dev"},["code",{},"nuxt dev"]]," hoặc xây dựng dự án cho sản xuất với ",["a",{"href":"/docs/api/commands/build"},["code",{},"nuxt build"]],". Chúng có thể ghi đè lên các mẫu, cấu hình trình tải webpack, thêm các thư viện CSS và thực hiện nhiều tác vụ hữu ích khác."],["p",{},"Điều tuyệt vời nhất là các mô-đun Nuxt có thể được phân phối dưới dạng các gói npm. Điều này cho phép chúng được tái sử dụng trên nhiều dự án và chia sẻ với cộng đồng, góp phần tạo ra một hệ sinh thái các tiện ích bổ sung chất lượng cao."],["read-more",{"to":"/modules"},["p",{},"Khám phá các Mô-đun Nuxt"]],["h2",{"id":"add-nuxt-modules"},"Add Nuxt Modules"],["p",{},"Sau khi cài đặt các mô-đun, bạn có thể thêm chúng vào tệp ",["a",{"href":"/docs/guide/directory-structure/nuxt-config"},["code",{},"nuxt.config.ts"]]," của bạn dưới thuộc tính ",["code",{},"modules"],". Các nhà phát triển mô-đun thường cung cấp các bước và chi tiết bổ sung cho việc sử dụng."],["pre",{"className":"language-ts shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"export default defineNuxtConfig({\n  modules: [\n    // Using package name (recommended usage)\n   