INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/vi/2.guide/3.going-further/1.experimental-features.md', 'Tính năng Thử nghiệm', '{"type":"minimark","value":[["p",{},"Nuxt bao gồm các tính năng thử nghiệm mà bạn có thể bật trong tệp cấu hình của mình."],["p",{},"Về mặt nội bộ, Nuxt sử dụng ",["code",{},"@nuxt/schema"]," để định nghĩa các tính năng thử nghiệm này. Bạn có thể tham khảo ",["a",{"href":"/docs/api/configuration/nuxt-config#experimental"},"API documentation"]," hoặc ",["a",{"href":"https://github.com/nuxt/nuxt/blob/main/packages/schema/src/config/experimental.ts","rel":["nofollow"]},"source code"]," để biết thêm thông tin."],["note",{},["p",{},"Lưu ý rằng các tính năng này là thử nghiệm và có thể bị xóa hoặc sửa đổi trong tương lai."]],["h2",{"id":"asynccontext"},"asyncContext"],["p",{},"Bật ngữ cảnh async gốc để có thể truy cập được cho các composables lồng nhau trong Nuxt và Nitro. Điều này mở ra khả năng sử dụng composables bên trong các composables async và giảm khả năng gặp lỗi ",["code",{},"Nuxt instance is unavailable"],"."],["pre",{"className":"language-ts shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"export default defineNuxtConfig({\n  experimental: {\n    asyncContext: true\n  }\n})\n","filename":"nuxt.config.ts","language":"ts","meta":"twoslash","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s8R28"},"export"],["span",{"class":"s8R28"}," default"],["span",{"class":"s3cPz"}," defineNuxtConfig"],["span",{"class":"sZSNi"},"("],["span",{"class":"sDfIl"},"{\n"]],["span",{"class":"line","line":2},["span",{"class":"sRlkE"},"  experimental"],["span",{"class":"sDfIl"},":"],["span",{"class":"sDfIl"}," {\n"]],["span",{"class":"line","line":3},["span",{"class":"sRlkE"},"    asyncContext"],["span",{"class":"sDfIl"},":"],["span",{"class":"sbKd-"}," true\n"]],["span",{"class":"line","line":4},["span",{"class":"sDfIl"},"  }\n"]],["span",{"class":"line"