INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/vi/2.guide/3.going-further/1.features.md', 'Features', '{"type":"minimark","value":[["p",{},"Một số tính năng của Nuxt có sẵn theo cơ chế opt-in, hoặc có thể bị tắt dựa trên nhu cầu của bạn."],["h2",{"id":"features"},["code",{},"features"]],["h3",{"id":"inlinestyles"},"inlineStyles"],["p",{},"Inline styles khi render HTML. Hiện tại chỉ khả dụng khi sử dụng Vite."],["p",{},"Bạn cũng có thể truyền một hàm nhận đường dẫn của một Vue component và trả về một boolean chỉ ra liệu có inline styles cho component đó hay không."],["pre",{"className":"language-ts shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"export default defineNuxtConfig({\n  features: {\n    inlineStyles: false // or a function to determine inlining\n  }\n})\n","filename":"nuxt.config.ts","language":"ts","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s8R28"},"export"],["span",{"class":"s8R28"}," default"],["span",{"class":"s3cPz"}," defineNuxtConfig"],["span",{"class":"sZSNi"},"("],["span",{"class":"sDfIl"},"{\n"]],["span",{"class":"line","line":2},["span",{"class":"sRlkE"},"  features"],["span",{"class":"sDfIl"},":"],["span",{"class":"sDfIl"}," {\n"]],["span",{"class":"line","line":3},["span",{"class":"sRlkE"},"    inlineStyles"],["span",{"class":"sDfIl"},":"],["span",{"class":"sbKd-"}," false"],["span",{"class":"sWuyu"}," // or a function to determine inlining\n"]],["span",{"class":"line","line":4},["span",{"class":"sDfIl"},"  }\n"]],["span",{"class":"line","line":5},["span",{"class":"sDfIl"},"}"],["span",{"class":"sZSNi"},")\n"]]]],["h3",{"id":"noscripts"},"noScripts"],["p",{},"Tắt việc render Nuxt scripts và JS resource hints. Cũng có thể được cấu hình chi tiết trong ",["code",{},"routeRules"],"."],["pre",{"className":"language-ts shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"export def