,["span",{"class":"sDfIl"},",\n"]],["span",{"class":"line","line":17},["span",{"class":"sZSNi"},"    runtimeConfig"],["span",{"class":"sDfIl"},",\n"]],["span",{"class":"line","line":18},["span",{"class":"sZSNi"},"    noSSR"],["span",{"class":"sDfIl"},",\n"]],["span",{"class":"line","line":19},["span",{"class":"sDfIl"},"  },\n"]],["span",{"class":"line","line":20},["span",{"emptyLinePlaceholder":true},"\n"]],["span",{"class":"line","line":21},["span",{"class":"sWuyu"},"  // Điều này sẽ được stringified và truyền từ server đến client\n"]],["span",{"class":"line","line":22},["span",{"class":"sRlkE"},"  payload"],["span",{"class":"sDfIl"},":"],["span",{"class":"sDfIl"}," {\n"]],["span",{"class":"line","line":23},["span",{"class":"sRlkE"},"    serverRendered"],["span",{"class":"sDfIl"},":"],["span",{"class":"sbKd-"}," true"],["span",{"class":"sDfIl"},",\n"]],["span",{"class":"line","line":24},["span",{"class":"sRlkE"},"    data"],["span",{"class":"sDfIl"},":"],["span",{"class":"sDfIl"}," {},\n"]],["span",{"class":"line","line":25},["span",{"class":"sRlkE"},"    state"],["span",{"class":"sDfIl"},":"],["span",{"class":"sDfIl"}," {}\n"]],["span",{"class":"line","line":26},["span",{"class":"sDfIl"},"  }\n"]],["span",{"class":"line","line":27},["span",{"emptyLinePlaceholder":true},"\n"]],["span",{"class":"line","line":28},["span",{"class":"sZSNi"},"  provide: "],["span",{"class":"sDfIl"},"("],["span",{"class":"s1nJG"},"name"],["span",{"class":"sDfIl"},":"],["span",{"class":"s52Pk"}," string"],["span",{"class":"sDfIl"},","],["span",{"class":"s1nJG"}," value"],["span",{"class":"sDfIl"},":"],["span",{"class":"s52Pk"}," any"],["span",{"class":"sDfIl"},")"],["span",{"class":"smZ93"}," =>"],["span",{"class":"sDfIl"}," void\n"]],["span",{"class":"line","line":29},["span",{"class":"sDfIl"},"}\n"]]]],["p",{},"Để biết thêm chi tiết, hãy xem ",["a",{"href":"https://github.com/nuxt/nuxt/blob/main/packages/nuxt/src/app/nuxt.ts","rel":["nofollow"]},"mã nguồn"],"."],["h2",{"id":"runtime-context-vs-build-context"},"Runtime Context vs. Build Context"],["p",{},"Nuxt xây dựng và bundle dự án bằng Node.js nhưng cũng có phía runtime."],["p",{},"Mặc dù cả hai khu vực đều có thể được mở rộng, ngữ cảnh runtime được cô lập khỏi build-time. Do đó, chúng không được chia sẻ trạng thái, mã, hoặc ngữ cảnh ngoài cấu hình runtime!"],["p",{},["code",{},"nuxt.config"]," và ",["a",{"href":"/docs/guide/going-further/modules"},"Nuxt Modules"]," có thể được sử dụng để mở rộng ngữ cảnh build, và ",["a",{"href":"/docs/guide/directory-structure/plugins"},"Nuxt Plugins"]," có thể được sử dụng để mở rộng runtime."],["p",{},"Khi xây dựng ứng dụng cho sản xuất, ",["code",{},"nuxt build"]," sẽ tạo một bản dựng độc lập trong thư mục ",["code",{},".output"],", độc lập với ",["code",{},"nuxt.config"]," và ",["a",{"href":"/docs/guide/going-further/modules"},"các module Nuxt"],"."],["style",{},"html pre.shiki code .smZ93, html code.shiki .smZ93{--shiki-light:#9C3EDA;--shiki-default:#9C3EDA;--shiki-dark:#C792EA}html pre.shiki code .sZSNi, html code.shiki .sZSNi{--shiki-light:#90A4AE;--shiki-default:#90A4AE;--shiki-dark:#BABED8}html pre.shiki code .sDfIl, html code.shiki .sDfIl{--shiki-light:#39ADB5;--shiki-default:#39ADB5;--shiki-dark:#89DDFF}html pre.shiki code .sWuyu, html code.shiki .sWuyu{--shiki-light:#90A4AE;--shiki-light-font-style:italic;--shiki-default:#90A4AE;--shiki-default-font-style:italic;--shiki-dark:#676E95;--shiki-dark-font-style:italic}html pre.shiki code .sRlkE, html code.shiki .sRlkE{--shiki-light:#E53935;--shiki-default:#E53935;--shiki-dark:#F07178}html pre.shiki code .sbKd-, html code.shiki .sbKd-{--shiki-light:#FF5370;--shiki-default:#FF5370;--shiki-dark:#FF9CAC}html pre.shiki code .s1nJG, html code.shiki .s1nJG{--shiki-light:#90A4AE;--shiki-light-font-style:italic;--shiki-default:#90A4AE;--shiki-default-font-style:italic;--shiki-dark:#BABED8;--shiki-dark-font-style:italic}html pre.shiki code .s52Pk, html code.shiki .s52Pk{--shiki-light:#E2931D;--shiki-default:#E29