INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/vi/2.guide/3.going-further/10.runtime-config.md', 'Runtime Config', '{"type":"minimark","value":[["h2",{"id":"exposing"},"Exposing"],["p",{},"Để expose config và environment variables cho phần còn lại của ứng dụng, bạn sẽ cần định nghĩa runtime configuration trong tệp ",["a",{"href":"/docs/guide/directory-structure/nuxt-config"},["code",{},"nuxt.config"]]," của bạn, sử dụng tùy chọn ",["a",{"href":"/docs/api/nuxt-config#runtimeconfig"},["code",{},"runtimeConfig"]],"."],["pre",{"className":"language-ts shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"export default defineNuxtConfig({\n  runtimeConfig: {\n    // The private keys which are only available within server-side\n    apiSecret: ''123'',\n    // Keys within public, will be also exposed to the client-side\n    public: {\n      apiBase: ''/api''\n    }\n  }\n})\n","filename":"nuxt.config.ts","language":"ts","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s8R28"},"export"],["span",{"class":"s8R28"}," default"],["span",{"class":"s3cPz"}," defineNuxtConfig"],["span",{"class":"sZSNi"},"("],["span",{"class":"sDfIl"},"{\n"]],["span",{"class":"line","line":2},["span",{"class":"sRlkE"},"  runtimeConfig"],["span",{"class":"sDfIl"},":"],["span",{"class":"sDfIl"}," {\n"]],["span",{"class":"line","line":3},["span",{"class":"sWuyu"},"    // The private keys which are only available within server-side\n"]],["span",{"class":"line","line":4},["span",{"class":"sRlkE"},"    apiSecret"],["span",{"class":"sDfIl"},":"],["span",{"class":"sDfIl"}," ''"],["span",{"class":"sGFVr"},"123"],["span",{"class":"sDfIl"},"''"],["span",{"class":"sDfIl"},",\n"]],["span",{"class":"line","line":5},["span",{"class":"sWuyu"},"    // Keys within public, will be also exposed to the client-side\n"]],["span",{"class":"line","line":6},["span",{"class":"sRlkE"},"    public"],["span",{"class":"