INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/vi/2.guide/3.going-further/11.nightly-release-channel.md', 'Nightly Release Channel', '{"type":"minimark","value":[["p",{},"Nuxt lands commits, improvements, và bug fixes mỗi ngày. Bạn có thể opt in để test chúng sớm hơn trước release tiếp theo."],["p",{},"Sau khi một commit được merge vào branch ",["code",{},"main"]," của ",["a",{"href":"https://github.com/nuxt/nuxt","rel":["nofollow"]},"nuxt/nuxt"]," và ",["strong",{},"passes all tests"],", chúng tôi trigger một automated npm release, sử dụng GitHub Actions."],["p",{},"Bạn có thể sử dụng các ''nightly'' releases này để beta test các tính năng và thay đổi mới."],["p",{},"Phương pháp build và publishing và chất lượng của các ''nightly'' releases này giống như stable ones. Sự khác biệt duy nhất là bạn nên thường xuyên check GitHub repository để cập nhật. Có một cơ hội nhỏ về regressions không được catch trong quá trình review và bởi automated tests. Do đó, chúng tôi internally sử dụng channel này để double-check mọi thứ trước mỗi release."],["note",{},["p",{},"Các tính năng chỉ có sẵn trên nightly release channel được mark với một alert trong documentation."]],["warning",{},["p",{},"Kênh nightly release ",["code",{},"latest"]," hiện tại đang tracking branch Nuxt v4, nghĩa là nó đặc biệt có khả năng có breaking changes ngay bây giờ — hãy cẩn thận! Bạn có thể opt in vào 3.x branch nightly releases với ",["code",{},"\"nuxt\": \"npm:nuxt-nightly@3x\""],"."]],["h2",{"id":"opting-in"},"Opting In"],["p",{},"Cập nhật dependency ",["code",{},"nuxt"]," bên trong ",["code",{},"package.json"],":"],["pre",{"className":"language-diff shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"{\n  \"devDependencies\": {\n--    \"nuxt\": \"^4.0.0\"\n++    \"nuxt\": \"npm:nuxt-nightly@latest\"\n  }\n}\n","filename":"package.json","language":"diff","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["sp