INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/vi/2.guide/4.recipes/3.custom-usefetch.md', 'Custom useFetch in Nuxt', '{"type":"minimark","value":[["p",{},"Khi làm việc với Nuxt, bạn có thể đang tạo frontend và fetch một API bên ngoài, và bạn có thể muốn đặt một số tùy chọn mặc định để fetch từ API của bạn."],["p",{},"Hàm tiện ích ",["a",{"href":"/docs/api/utils/dollarfetch"},["code",{},"$fetch"]]," (được sử dụng bởi composable ",["a",{"href":"/docs/api/composables/use-fetch"},["code",{},"useFetch"]],") được thiết kế không thể cấu hình toàn cục. Điều này quan trọng để hành vi fetch trong toàn bộ ứng dụng của bạn vẫn nhất quán, và các tích hợp khác (như modules) có thể dựa vào hành vi của các tiện ích cốt lõi như ",["code",{},"$fetch"],"."],["p",{},"Tuy nhiên, Nuxt cung cấp một cách để tạo một fetcher tùy chỉnh cho API của bạn (hoặc nhiều fetcher nếu bạn có nhiều API để gọi)."],["h2",{"id":"custom-fetch"},"Custom ",["code",{},"$fetch"]],["p",{},"Hãy tạo một instance ",["code",{},"$fetch"]," tùy chỉnh với một ",["a",{"href":"/docs/guide/directory-structure/plugins"},"Nuxt plugin"],"."],["note",{},["p",{},["code",{},"$fetch"]," là một instance được cấu hình của ",["a",{"href":"https://github.com/unjs/ofetch","rel":["nofollow"]},"ofetch"]," hỗ trợ thêm base URL của server Nuxt của bạn cũng như các lệnh gọi hàm trực tiếp trong SSR (tránh các vòng lặp HTTP)."]],["p",{},"Hãy giả định ở đây rằng:"],["ul",{},["li",{},"The main API is ",["a",{"href":"https://api.nuxt.com","rel":["nofollow"]},"https://api.nuxt.com"]],["li",{},"We are storing the JWT token in a session with ",["a",{"href":"https://github.com/atinux/nuxt-auth-utils","rel":["nofollow"]},"nuxt-auth-utils"]],["li",{},"If the API responds with a ",["code",{},"401"]," status code, we redirect the user to the ",["code",{},"/login"]," page"]],["pre",{"className":"language-ts shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"export default defineNuxtPlugin(