INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/vi/2.guide/5.best-practices/performance.md', 'Nuxt performance', '{"type":"minimark","value":[["p",{},"Nuxt đi kèm với các tính năng tích hợp được thiết kế để cải thiện hiệu suất ứng dụng của bạn và góp phần vào ",["a",{"href":"https://web.dev/articles/vitals","rel":["nofollow"]},"Core Web Vitals"]," tốt hơn. Ngoài ra còn có nhiều module cốt lõi Nuxt giúp cải thiện hiệu suất trong các lĩnh vực cụ thể. Hướng dẫn này nêu ra các thực tiễn tốt nhất để tối ưu hóa hiệu suất ứng dụng Nuxt của bạn."],["h2",{"id":"built-in-features"},"Built-in Features"],["p",{},"Nuxt cung cấp một số tính năng tích hợp giúp bạn tối ưu hóa hiệu suất của trang web. Hiểu cách các tính năng này hoạt động là rất quan trọng để đạt được hiệu suất nhanh như chớp."],["h3",{"id":"links"},"Links"],["p",{},["a",{"href":"/docs/api/components/nuxt-link"},["code",{},"<NuxtLink>"]]," là một thay thế drop-in cho component Vue Router''s ",["code",{},"<RouterLink>"]," và thẻ HTML ",["code",{},"<a>"],". Nó thông minh xác định xem link là internal hay external và render tương ứng với các tối ưu hóa có sẵn (prefetching, thuộc tính mặc định, v.v.)"],["pre",{"className":"language-html shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"<template>\n  <NuxtLink to=\"/about\">About page</NuxtLink>\n</template>\n\n<!-- Which will render to with Vue Router & Smart Prefetching -->\n<a href=\"/about\">About page</a>\n","language":"html","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"sDfIl"},"<"],["span",{"class":"sRlkE"},"template"],["span",{"class":"sDfIl"},">\n"]],["span",{"class":"line","line":2},["span",{"class":"sDfIl"},"  <"],["span",{"class":"sRlkE"},"NuxtLink"],["span",{"class":"smZ93"}," to"],["span",{"class":"sDfIl"},"="],["span",{"class":"sDfIl"},"\""],["span",{"class":"sGFVr"},"/about"],["span",{"class":"sDfIl"},"\""],["span",{"class":"sDfIl"},">"],[