INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/vi/3.api/2.composables/use-lazy-fetch.md', 'useLazyFetch', '{"type":"minimark","value":[["h2",{"id":"description"},"Description"],["p",{},"Theo mặc định, ",["a",{"href":"/docs/api/composables/use-fetch"},["code",{},"useFetch"]]," chặn điều hướng cho đến khi trình xử lý async của nó được giải quyết. ",["code",{},"useLazyFetch"]," cung cấp một wrapper xung quanh ",["a",{"href":"/docs/api/composables/use-fetch"},["code",{},"useFetch"]]," kích hoạt điều hướng trước khi trình xử lý được giải quyết bằng cách đặt tùy chọn ",["code",{},"lazy"]," thành ",["code",{},"true"],"."],["note",{},["p",{},["code",{},"useLazyFetch"]," has the same signature as ",["a",{"href":"/docs/api/composables/use-fetch"},["code",{},"useFetch"]],"."]],["note",{},["p",{},"Việc awaiting ",["code",{},"useLazyFetch"]," ở chế độ này chỉ đảm bảo cuộc gọi được khởi tạo. Trên điều hướng phía client, dữ liệu có thể không khả dụng ngay lập tức, và bạn nên đảm bảo xử lý trạng thái pending trong ứng dụng của mình."]],["read-more",{"to":"/docs/api/composables/use-fetch"}],["h2",{"id":"example"},"Example"],["pre",{"className":"language-vue shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"<script setup lang=\"ts\">\n/* Điều hướng sẽ xảy ra trước khi fetching hoàn tất.\n * Xử lý trạng thái ''pending'' và ''error'' trực tiếp trong template của component\n */\nconst { status, data: posts } = await useLazyFetch(''/api/posts'')\nwatch(posts, (newPosts) => {\n  // Vì posts có thể bắt đầu là null, bạn sẽ không có quyền truy cập\n  // vào nội dung của nó ngay lập tức, nhưng bạn có thể watch nó.\n})\n</script>\n\n<template>\n  <div v-if=\"status === ''pending''\">\n    Đang tải ...\n  </div>\n  <div v-else>\n    <div v-for=\"post in posts\">\n      <!-- do something -->\n    </div>\n  </div>\n</template>\n","filename":"pages/index.vue","language":"vue","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"