INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/vi/3.api/2.composables/use-loading-indicator.md', 'useLoadingIndicator', '{"type":"minimark","value":[["h2",{"id":"description"},"Description"],["p",{},"Một composable trả về trạng thái loading của trang. Được sử dụng bởi ",["a",{"href":"/docs/api/components/nuxt-loading-indicator"},["code",{},"<NuxtLoadingIndicator>"]]," và có thể kiểm soát.\nNó hook vào ",["a",{"href":"/docs/api/advanced/hooks#app-hooks-runtime"},["code",{},"page:loading:start"]]," và ",["a",{"href":"/docs/api/advanced/hooks#app-hooks-runtime"},["code",{},"page:loading:end"]]," để thay đổi trạng thái của nó."],["h2",{"id":"parameters"},"Parameters"],["ul",{},["li",{},["code",{},"duration"],": Thời lượng của thanh loading, tính bằng mili giây (mặc định ",["code",{},"2000"],")."],["li",{},["code",{},"throttle"],": Throttle việc xuất hiện và ẩn, tính bằng mili giây (mặc định ",["code",{},"200"],")."],["li",{},["code",{},"estimatedProgress"],": Theo mặc định Nuxt sẽ back off khi nó tiếp cận 100%. Bạn có thể cung cấp một hàm tùy chỉnh để tùy chỉnh ước tính tiến độ, hàm này nhận thời lượng của thanh loading (ở trên) và thời gian đã trôi qua. Nó nên trả về một giá trị từ 0 đến 100."]],["h2",{"id":"properties"},"Properties"],["h3",{"id":"isloading"},["code",{},"isLoading"]],["ul",{},["li",{},["strong",{},"type"],": ",["code",{},"Ref<boolean>"]],["li",{},["strong",{},"description"],": Trạng thái loading"]],["h3",{"id":"error"},["code",{},"error"]],["ul",{},["li",{},["strong",{},"type"],": ",["code",{},"Ref<boolean>"]],["li",{},["strong",{},"description"],": Trạng thái lỗi"]],["h3",{"id":"progress"},["code",{},"progress"]],["ul",{},["li",{},["strong",{},"type"],": ",["code",{},"Ref<number>"]],["li",{},["strong",{},"description"],": Trạng thái tiến độ. Từ ",["code",{},"0"]," đến ",["code",{},"100"],"."]],["h2",{"id":"methods"},"Methods"],["h3",{"id":"start"},["code",{},"start()"]],["p",{},"Đặt ",["code",{},"isLoading"]," thành true và bắt đầu tăng