INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/vi/3.api/3.utils/prefetch-components.md', 'prefetchComponents', '{"type":"minimark","value":[["p",{},"Việc prefetching component tải xuống mã code trong nền, điều này dựa trên giả định rằng component có khả năng sẽ được sử dụng để render, cho phép component tải ngay lập tức nếu và khi người dùng yêu cầu. Component được tải xuống và lưu cache cho việc sử dụng tương lai dự kiến mà không cần người dùng thực hiện yêu cầu rõ ràng."],["p",{},"Sử dụng ",["code",{},"prefetchComponents"]," để prefetch thủ công các component riêng lẻ đã được đăng ký toàn cục trong ứng dụng Nuxt của bạn. Theo mặc định, Nuxt đăng ký chúng như các async components. Bạn phải sử dụng phiên bản Pascal-cased của tên component."],["pre",{"className":"language-ts shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"await prefetchComponents(''MyGlobalComponent'')\n\nawait prefetchComponents([''MyGlobalComponent1'', ''MyGlobalComponent2''])\n","language":"ts","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s8R28"},"await"],["span",{"class":"s3cPz"}," prefetchComponents"],["span",{"class":"sZSNi"},"("],["span",{"class":"sDfIl"},"''"],["span",{"class":"sGFVr"},"MyGlobalComponent"],["span",{"class":"sDfIl"},"''"],["span",{"class":"sZSNi"},")\n"]],["span",{"class":"line","line":2},["span",{"emptyLinePlaceholder":true},"\n"]],["span",{"class":"line","line":3},["span",{"class":"s8R28"},"await"],["span",{"class":"s3cPz"}," prefetchComponents"],["span",{"class":"sZSNi"},"(["],["span",{"class":"sDfIl"},"''"],["span",{"class":"sGFVr"},"MyGlobalComponent1"],["span",{"class":"sDfIl"},"''"],["span",{"class":"sDfIl"},","],["span",{"class":"sDfIl"}," ''"],["span",{"class":"sGFVr"},"MyGlobalComponent2"],["span",{"class":"sDfIl"},"''"],["span",{"class":"sZSNi"},"])\n"]]]],["note",{},["p",{},"Việc triển khai hiện tại hoạt động giống hệt như ",["a",{"href":"/docs/