INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/vi/3.api/3.utils/preload-components.md', 'preloadComponents', '{"type":"minimark","value":[["p",{},"Việc preloading components tải các components mà trang của bạn sẽ cần rất sớm, mà bạn muốn bắt đầu tải sớm trong vòng đời render. Điều này đảm bảo chúng có sẵn sớm hơn và ít có khả năng chặn việc render trang, cải thiện hiệu suất."],["p",{},"Sử dụng ",["code",{},"preloadComponents"]," để preload thủ công các component riêng lẻ đã được đăng ký toàn cục trong ứng dụng Nuxt của bạn. Theo mặc định, Nuxt đăng ký chúng như các async components. Bạn phải sử dụng phiên bản Pascal-cased của tên component."],["pre",{"className":"language-js shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"await preloadComponents(''MyGlobalComponent'')\n\nawait preloadComponents([''MyGlobalComponent1'', ''MyGlobalComponent2''])\n","language":"js","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s8R28"},"await"],["span",{"class":"s3cPz"}," preloadComponents"],["span",{"class":"sZSNi"},"("],["span",{"class":"sDfIl"},"''"],["span",{"class":"sGFVr"},"MyGlobalComponent"],["span",{"class":"sDfIl"},"''"],["span",{"class":"sZSNi"},")\n"]],["span",{"class":"line","line":2},["span",{"emptyLinePlaceholder":true},"\n"]],["span",{"class":"line","line":3},["span",{"class":"s8R28"},"await"],["span",{"class":"s3cPz"}," preloadComponents"],["span",{"class":"sZSNi"},"(["],["span",{"class":"sDfIl"},"''"],["span",{"class":"sGFVr"},"MyGlobalComponent1"],["span",{"class":"sDfIl"},"''"],["span",{"class":"sDfIl"},","],["span",{"class":"sDfIl"}," ''"],["span",{"class":"sGFVr"},"MyGlobalComponent2"],["span",{"class":"sDfIl"},"''"],["span",{"class":"sZSNi"},"])\n"]]]],["note",{},["p",{},"Trên server, ",["code",{},"preloadComponents"]," sẽ không có hiệu lực."]],["style",{},"html pre.shiki code .s8R28, html code.shiki .s8R28{--shiki-light:#39ADB5;--shiki-light-fo