INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/vi/5.community/2.getting-help.md', 'Getting Help', '{"type":"minimark","value":[["p",{},"Vào một thời điểm nào đó, bạn có thể gặp phải một vấn đề cần được giúp đỡ."],["p",{},"Nhưng đừng lo lắng! Chúng tôi là một cộng đồng thân thiện của các nhà phát triển và chúng tôi rất muốn giúp đỡ."],["card-group",{},["card",{"icon":"i-simple-icons-discord","target":"_blank","title":"Discord","to":"https://go.nuxt.com/discord"},["p",{},"Nhận trợ giúp thời gian thực, trao đổi với nhóm cốt lõi và cộng đồng, và cập nhật tin tức Nuxt mới nhất."]],["card",{"icon":"i-simple-icons-nuxt","target":"_blank","title":"Nuxters","to":"https://nuxters.nuxt.com"},["p",{},"Kết nối với những người đam mê Nuxt khác."]]],["h2",{"id":"i-cant-figure-out-how-to"},"\"I can''t figure out how to (...).\""],["p",{},"Bạn đã đọc qua các tài liệu này và nghĩ rằng nó nên khả thi, nhưng không rõ cách thực hiện. Điều tốt nhất là ",["a",{"href":"https://github.com/nuxt/nuxt/discussions","rel":["nofollow"]},"mở một Thảo luận GitHub"],"."],["p",{},"Vui lòng đừng cảm thấy xấu hổ khi đặt một câu hỏi mà bạn nghĩ là dễ - chúng tôi đều đã trải qua điều đó! ❤️"],["p",{},"Mọi người bạn gặp sẽ giúp đỡ vì họ quan tâm, không phải vì họ được trả tiền để làm vậy. Điều tử tế nhất là làm cho họ dễ dàng giúp bạn. Dưới đây là một số ý tưởng:"],["ul",{},["li",{},["em",{},"Giải thích mục tiêu của bạn, không chỉ vấn đề bạn đang đối mặt."]," \"Tôi cần đảm bảo các đầu vào biểu mẫu của tôi có thể truy cập, vì vậy tôi đang cố gắng làm cho các id khớp giữa máy chủ và máy khách.\""],["li",{},["em",{},"Đảm bảo bạn đã đọc tài liệu trước và sử dụng công cụ tìm kiếm yêu thích của bạn"],". Hãy cho mọi người biết bằng cách nói điều gì đó như \"Tôi đã Google cho ''nuxt script setup'' nhưng tôi không thể tìm thấy ví dụ mã ở đâu cả.\""],["li",{},["em",{},"Giải thích những gì bạn đã thử."]," Kể cho mọi người về loại giải pháp bạn đã thử nghiệm, và tại sao. Thường thì điều này có thể làm cho lời khuyên của mọi người phù hợp hơn với tình huống của bạn."],["li",{},["em",{},"Chia sẻ mã của bạn."]," Mọi người có lẽ sẽ không thể giúp nếu họ chỉ thấy thông báo lỗi hoặc ảnh chụp màn hình - nhưng tất cả điều đó thay đổi nếu bạn chia sẻ mã của bạn ở định dạng sao chép/dán - ưu tiên dưới dạng tái tạo tối thiểu như CodeSandbox."]],["p",{},"Và cuối cùng, chỉ cần đặt câu hỏi! Không cần ",["a",{"href":"https://dontasktoask.com","rel":["nofollow"]},"yêu cầu phép để đặt câu hỏi"]," hoặc ",["a",{"href":"https://www.nohello.com","rel":["nofollow"]},"chờ ai đó trả lời lời chào của bạn"],". Nếu bạn làm vậy, bạn có thể không nhận được phản hồi vì mọi người đang chờ toàn bộ câu hỏi trước khi tham gia."],["h2",{"id":"could-there-be-a-bug"},"\"Could there be a bug?\""],["p",{},"Một cái gì đó không hoạt động theo cách mà tài liệu nói rằng nó nên. Bạn không chắc liệu đó có phải là lỗi hay không. Bạn đã tìm kiếm qua ",["a",{"href":"https://github.com/nuxt/nuxt/issues","rel":["nofollow"]},"các vấn đề mở"]," và ",["a",{"href":"https://github.com/nuxt/nuxt/discussions","rel":["nofollow"]},"thảo luận"]," nhưng bạn không thể tìm thấy gì. (nếu có vấn đề đã đóng, vui lòng tạo một vấn đề mới)"],["p",{},"Chúng tôi khuyên bạn nên xem ",["a",{"href":"/docs/community/reporting-bugs"},"cách báo cáo lỗi"],". Nuxt vẫn đang trong quá trình phát triển tích cực, và mọi vấn đề giúp làm cho nó tốt hơn."],["h2",{"id":"i-need-professional-help"},"\"I need professional help\""],["p",{},"Nếu cộng đồng không thể cung cấp sự giúp đỡ bạn cần trong khung thời gian bạn có, NuxtLabs cung cấp hỗ trợ chuyên nghiệp với ",["a",{"href":"https://nuxt.com/enterprise/support","rel":["nofollow"]},"Nuxt Experts"],"."],["p",{},"Mục tiêu của Nuxt Expert là cung cấp hỗ trợ cho hệ sinh thái Vue, đồng thời tạo cơ hội tự do cho những người đóng góp cho các giải pháp mã nguồn mở, từ đó giúp duy trì tính bền vững của hệ sinh thái."],["p",{},"Các chuyên gia Nuxt là những người đóng góp được chọn của Vue, Nuxt và Vite cung cấp hỗ trợ và dịch vụ tư vấn chuyên nghiệp."]],"toc":{"title":"","searchDepth":2,"depth":2,"links":[{"id":"i-cant-figure-out-how-to","depth":2,"text":"\"I can''t figure out how to (...).\""},{"id":"could-there-be-a-bug","depth":2,"text":"\"Could there be a bug?\""},{"id":"i-need-professional-help","depth":2,"text":"\"I need professional help\""}]}}', 'We''re a friendly community of developers and we''d love to help.', 'md', NULL, '{}', '{"icon":"i-lucide-life-buoy"}', '/nuxt/nuxtcore/vi/community/getting-help', '{"title":"Getting Help","description":"We''re a friendly community of developers and we''d love to help."}', 'nuxt/nuxtcore/vi/5.community/2.getting-help', NULL, 'aqppmUz6cPeV_91k0JklabLwqVfU-LAX6PATHU7A9FU'); -- aqppmUz6cPeV_91k0JklabLwqVfU-LAX6PATHU7A9FU
INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/vi/5.community/3.reporting-bugs.md', 'Reporting Bugs', '{"type":"minimark","value":[["p",{},"Dù chúng tôi cố gắng hết sức, chúng tôi sẽ không bao giờ loại bỏ hoàn toàn lỗi."],["p",{},"Ngay cả khi bạn không thể sửa mã cơ bản, việc báo cáo lỗi tốt có thể cho phép ai đó có chút quen thuộc hơn với cơ sở mã phát hiện một mẫu hoặc thực hiện sửa chữa nhanh."],["p",{},"Dưới đây là một vài bước chính."],["h2",{"id":"có-phải-là-lỗi-thật-không"},"Có Phải Là Lỗi Thật Không?"],["p",{},"Hãy xem xét liệu bạn đang tìm kiếm sự giúp đỡ với một cái gì đó, hay bạn nghĩ có lỗi với chính Nuxt. Nếu là trường hợp trước, chúng tôi rất muốn giúp bạn - nhưng cách tốt nhất để làm điều đó là thông qua ",["a",{"href":"/docs/community/getting-help"},"yêu cầu giúp đỡ"]," thay vì báo cáo lỗi."],["h2",{"id":"tìm-kiếm-các-vấn-đề"},"Tìm Kiếm Các Vấn Đề"],["p",{},"Tìm kiếm qua ",["a",{"href":"https://github.com/nuxt/nuxt/issues","rel":["nofollow"]},"các vấn đề mở"]," và ",["a",{"href":"https://github.com/nuxt/nuxt/discussions","rel":["nofollow"]},"thảo luận"]," trước. Nếu bạn tìm thấy bất kỳ thứ gì dường như là lỗi tương tự, tốt hơn là bình luận trên luồng hiện có hơn là tạo bản sao."],["h2",{"id":"tạo-một-tái-tạo-tối-thiểu"},"Tạo Một Tái Tạo Tối Thiểu"],["p",{},"Điều quan trọng là có thể tái tạo lỗi một cách đáng tin cậy - theo cách tối thiểu và tách biệt với phần còn lại của dự án của bạn. Điều này thu hẹp những gì có thể gây ra vấn đề và làm cho nó có thể cho ai đó không chỉ tìm ra nguyên nhân, mà còn kiểm tra giải pháp tiềm năng."],["p",{},"Bắt đầu với hộp cát Nuxt và thêm ",["strong",{},"số lượng mã tối thiểu"]," cần thiết để tái tạo lỗi bạn đang gặp phải."],["note",{},["p",{},"Nếu vấn đề của bạn liên quan đến Vue hoặc Vite, vui lòng thử tái tạo nó trước với Vue SSR starter."]],["p",{},["strong",{},"Nuxt"],":"],["card-group",{},["card",{"icon":"i-simple-icons-stackblitz","target":"_blank","title":"Nuxt on StackBlitz","to":"https://nuxt.new/s/v3"}],["card",{"icon":"i-simple-icons-codesandbox","target":"_blank","title":"Nuxt on CodeSandbox","to":"https://nuxt.new/c/v3"}]],["p",{},["strong",{},"Vue"],":"],["card-group",{},["card",{"icon":"i-simple-icons-stackblitz","target":"_blank","title":"Vue SSR on StackBlitz","to":"https://stackblitz.com/github/nuxt-contrib/vue3-ssr-starter/tree/main?terminal=dev"}],["card",{"icon":"i-simple-icons-codesandbox","target":"_blank","title":"Vue SSR on CodeSandbox","to":"https://codesandbox.io/s/github/nuxt-contrib/vue3-ssr-starter/main"}],["card",{"icon":"i-simple-icons-github","target":"_blank","title":"Vue SSR Template on GitHub","to":"https://github.com/nuxt-contrib/vue3-ssr-starter/generate"}]],["p",{},"Một khi bạn đã tái tạo vấn đề, hãy loại bỏ càng nhiều mã từ tái tạo của bạn càng tốt (trong khi vẫn tái tạo lỗi). Thời gian dành để làm cho tái tạo càng tối thiểu càng tốt sẽ tạo ra sự khác biệt lớn cho bất kỳ ai đặt ra để sửa vấn đề."],["h2",{"id":"tìm-ra-nguyên-nhân-có-thể-là-gì"},"Tìm Ra Nguyên Nhân Có Thể Là Gì"],["p",{},"Với dự án Nuxt, có rất nhiều bộ phận chuyển động - từ ",["a",{"href":"/modules"},"các module Nuxt"]," đến ",["a",{"href":"https://www.npmjs.com","rel":["nofollow"]},"các thư viện JavaScript khác"],". Hãy thử báo cáo lỗi ở nơi phù hợp và cụ thể nhất. Điều đó có thể là module Nuxt gây ra vấn đề, hoặc thư viện upstream mà Nuxt phụ thuộc vào."]],"toc":{"title":"","searchDepth":2,"depth":2,"links":[{"id":"có-phải-là-lỗi-thật-không","depth":2,"text":"Có Phải Là Lỗi Thật Không?"},{"id":"tìm-kiếm-các-vấn-đề","depth":2,"text":"Tìm Kiếm Các Vấn Đề"},{"id":"tạo-một-tái-tạo-tối-thiểu","depth":2,"text":"Tạo Một Tái Tạo Tối Thiểu"},{"id":"tìm-ra-nguyên-nhân-có-thể-là-gì","depth":2,"text":"Tìm Ra Nguyên Nhân Có Thể Là Gì"}]}}', 'One of the most valuable roles in open source is taking the time to report bugs helpfully.', 'md', NULL, '{}', '{"icon":"i-lucide-bug"}', '/nuxt/nuxtcore/vi/community/reporting-bugs', '{"title":"Reporting Bugs","description":"One of the most valuable roles in open source is taking the time to report bugs helpfully."}', 'nuxt/nuxtcore/vi/5.community/3.reporting-bugs', NULL, '8c7jUdbGJJ6CLipESykkoczU9qu3iH02knUseDRxIuA'); -- 8c7jUdbGJJ6CLipESykkoczU9qu3iH02knUseDRxIuA
INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/vi/5.community/4.contribution.md', 'Contribution', '{"type":"minimark","value":[["p",{},"Có nhiều cách khác nhau mà bạn có thể đóng góp cho hệ sinh thái Nuxt."],["h2",{"id":"ecosystem"},"Ecosystem"],["p",{},"Hệ sinh thái Nuxt bao gồm nhiều dự án và tổ chức khác nhau:"],["ul",{},["li",{},["a",{"href":"https://github.com/nuxt","rel":["nofollow"]},"nuxt/"]," - kho lưu trữ cốt lõi cho chính framework Nuxt. ",["a",{"href":"https://github.com/nuxt/nuxt","rel":["nofollow"]},["strong",{},"nuxt/nuxt"]]," chứa framework Nuxt (cả phiên bản 2 và 3)."],["li",{},["a",{"href":"https://github.com/nuxt-modules","rel":["nofollow"]},"nuxt-modules/"]," - các module và thư viện được đóng góp và duy trì bởi cộng đồng. Có một ",["a",{"href":"/docs/guide/going-further/modules/#joining-nuxt-modules-and-nuxtjs"},"quy trình để di chuyển một module"]," sang ",["code",{},"nuxt-modules"],". Mặc dù các module này có người duy trì riêng, chúng không phụ thuộc vào một người duy nhất."],["li",{},["a",{"href":"https://github.com/unjs","rel":["nofollow"]},"unjs/"]," - nhiều thư viện này được sử dụng trong toàn bộ hệ sinh thái Nuxt. Chúng được thiết kế như các thư viện phổ quát, không phụ thuộc vào framework hoặc môi trường. Chúng tôi chào đón đóng góp và sử dụng bởi các framework và dự án khác."]],["h2",{"id":"how-to-contribute"},"How To Contribute"],["h3",{"id":"triage-issues-and-help-out-in-discussions"},"Triage Issues and Help Out in Discussions"],["p",{},"Kiểm tra các vấn đề và thảo luận cho dự án bạn muốn giúp đỡ. Ví dụ, đây là ",["a",{"href":"https://github.com/nuxt/nuxt/issues","rel":["nofollow"]},"bảng vấn đề"]," và ",["a",{"href":"https://github.com/nuxt/nuxt/discussions","rel":["nofollow"]},"thảo luận"]," cho Nuxt. Giúp đỡ người dùng khác, chia sẻ giải pháp thay thế, tạo bản sao, hoặc thậm chí đào sâu vào một lỗi một chút và chia sẻ phát hiện của bạn sẽ tạo ra sự khác biệt lớn."],["h3",{"id":"creating-an-issue"},"Creating an Issue"],["p",{},"Cảm ơn bạn đã dành thời gian tạo một vấn đề! ❤️"],["ul",{},["li",{},["strong",{},"Báo cáo lỗi"],": Kiểm tra ",["a",{"href":"/docs/community/reporting-bugs"},"hướng dẫn của chúng tôi"]," để biết một số việc cần làm trước khi mở một vấn đề."],["li",{},["strong",{},"Yêu cầu tính năng"],": Kiểm tra xem có vấn đề hoặc thảo luận hiện có nào bao phủ phạm vi của tính năng bạn đang nghĩ đến không. Nếu tính năng đó dành cho một phần khác của hệ sinh thái Nuxt (như một module), hãy cân nhắc nêu yêu cầu tính năng ở đó trước. Nếu tính năng bạn nghĩ đến là chung hoặc API không hoàn toàn rõ ràng, hãy cân nhắc mở một thảo luận trong phần ",["strong",{},"Ý tưởng"]," để thảo luận với cộng đồng trước."]],["p",{},"Chúng tôi sẽ cố gắng hết sức để tuân theo ",["a",{"href":"https://mermaid.live/view#pako:eNqFlE1v2zAMhv8K4UuToslhx2Bo0TZt12Edhm7YMCAXWqJtorLk6qOpkfS_j7KdfpyWQ-BQr8mHL6nsCuU0FauiMm6rGvQRfq03FuRzvvvTYIQHthpcBT_ugQNwPHuZjheLxf4i1VDx8x4udrf5EBCOQvSsYg4ffS79KS9pmX9QALTgyid2KYB7Ih-4bmKWbDk2YB0E1gRUVaRi-FDmmjAmT3u4nB3DmoNKIUA1BsGSohA49jnVMQhHbDh_EZQUImyxh-gAtfaiG-KWSJ-N8nt6YtpCdgEeE5rXPOdav5YwWJIJU7zrvNADV9C7JBIyIC07Wxupkx3LFQ5vCkguRno5f9fP2qnUko0Y2dk9rGdvHAa9IIhVGlCp5FFNPN-ce4DKeXBd53xMliOLp9IZtyORQVsnrGm-WJzejtUu5fFqdr5FGQ3bLslYvGthjZbJTLpReZG5_lLYw7XQ_CbPVT92ws9gnEJj-v84dk-PiaXnmF1XGAaPsOsMKywNvYmG80ZohV8k4wDR9_N3KN_dHm5mh1lnkM5FsYzRfNiTvJoT5gnQsl6uxjqXLhkNQ9syHJ0UZZ8ERUIlNShr6N8gZDEliR-ow7QZa0fhY4LoHLRo-8N7ZxPwjRj5ZZYXpvOSNs9v3Jjs8NXB4ets92xan3zydXZHvj64lKMayh4-gZC1bjASW2ipLeWuzIuToiXfImu5rbucclMIc0ubYiWPGv3DptjYF9Fhiu5nb1Wxij7RSZE6jZHWjLXHtlhVaIJESXN0_m68_sO_wMs_oO9gyg","rel":["nofollow"]},"sơ đồ quyết định vấn đề nội bộ của chúng tôi"]," khi trả lời các vấn đề."],["h3",{"id":"send-a-pull-request"},"Send a Pull Request"],["p",{},"Chúng tôi luôn chào đón các pull request! ❤️"],["h4",{"id":"before-you-start"},"Before You Start"],["p",{},"Trước khi sửa một lỗi, chúng tôi khuyên bạn nên kiểm tra xem ",["strong",{},"có vấn đề nào mô tả nó không"],", vì có thể đó là vấn đề tài liệu hoặc có một số ngữ cảnh hữu ích để biết."],["p",{},"Nếu bạn đang làm việc trên một tính năng, thì chúng tôi yêu cầu bạn ",["strong",{},"mở vấn đề yêu cầu tính năng trước"]," để thảo luận với người duy trì xem tính năng đó có được mong muốn không - và thiết kế của những tính năng đó. Điều này giúp tiết kiệm thời gian cho cả người duy trì và người đóng góp và có nghĩa là các tính năng có thể được phát hành nhanh hơn. Vấn đề ",["strong",{},"nên được xác nhận"]," bởi một thành viên nhóm framework trước khi xây dựng tính năng trong một pull request."],["p",{},"Đối với sửa lỗi đánh máy, bạn nên gộp nhiều sửa lỗi đánh máy vào một pull request để duy trì lịch sử commit sạch hơn."],["p",{},"Đối với những thay đổi lớn hơn đối với chính Nuxt, chúng tôi khuyên bạn nên ",["a",{"href":"#create-a-module"},"tạo một module Nuxt"]," trước và triển khai tính năng ở đó. Điều này cho phép proof-of-concept nhanh. Sau đó, bạn có thể ",["a",{"href":"#make-an-rfc"},"tạo một RFC"]," dưới dạng thảo luận. Khi người dùng áp dụng nó và bạn thu thập phản hồi, nó có thể được tinh chỉnh và thêm vào lõi Nuxt hoặc tiếp tục như một module độc lập."],["h4",{"id":"commit-conventions"},"Commit Conventions"],["p",{},"Chúng tôi sử dụng ",["a",{"href":"https://www.conventionalcommits.org","rel":["nofollow"]},"Conventional Commits"]," cho thông điệp commit, điều này ",["a",{"href":"https://github.com/unjs/changelogen","rel":["nofollow"]},"cho phép tạo changelog tự động"]," dựa trên các commit. Vui lòng đọc hướng dẫn kỹ nếu bạn chưa quen với nó."],["p",{},"Lưu ý rằng ",["code",{},"fix:"]," và ",["code",{},"feat:"]," dành cho ",["strong",{},"thay đổi mã thực tế"]," (có thể ảnh hưởng đến logic). Đối với thay đổi đánh máy hoặc tài liệu, hãy sử dụng ",["code",{},"docs:"]," hoặc ",["code",{},"chore:"]," thay thế:"],["ul",{},["li",{},["del",{},["code",{},"fix: typo"]]," -> ",["code",{},"docs: fix typo"]]],["p",{},"Nếu bạn đang làm việc trong một dự án có monorepo, như ",["code",{},"nuxt/nuxt"],", hãy đảm bảo chỉ định phạm vi chính của commit của bạn trong ngoặc đơn. Ví dụ: ",["code",{},"feat(kit): add ''addMagicStuff'' utility"],"."],["h4",{"id":"making-the-pull-request"},"Making the Pull Request"],["p",{},"Nếu bạn không biết cách gửi pull request, chúng tôi khuyên bạn nên đọc ",["a",{"href":"https://docs.github.com/en/pull-requests/collaborating-with-pull-requests/proposing-changes-to-your-work-with-pull-requests/creating-a-pull-request","rel":["nofollow"]},"hướng dẫn"],"."],["p",{},"Khi gửi pull request, hãy đảm bảo tiêu đề PR của bạn cũng tuân theo ",["a",{"href":"#commit-conventions"},"Quy ước Commit"],"."],["p",{},"Nếu PR của bạn sửa hoặc giải quyết các vấn đề hiện có, hãy đảm bảo đề cập đến chúng trong mô tả PR."],["p",{},"Bạn có thể có nhiều commit trong một PR duy nhất; bạn không cần rebase hoặc force push cho các thay đổi của mình vì chúng tôi sẽ sử dụng ",["code",{},"Squash and Merge"]," để gộp các commit thành một commit khi hợp nhất."],["p",{},"Chúng tôi không thêm bất kỳ commit hooks nào để cho phép commit nhanh. Nhưng trước khi bạn tạo pull request, bạn nên đảm bảo rằng bất kỳ script lint/test nào đang chạy thành công."],["p",{},"Nói chung, vui lòng cũng đảm bảo rằng không có thay đổi ",["em",{},"không liên quan"]," trong PR. Ví dụ, nếu trình chỉnh sửa của bạn đã thực hiện bất kỳ thay đổi nào về khoảng trắng hoặc định dạng ở nơi khác trong tệp bạn đã chỉnh sửa, hãy hoàn nguyên những thay đổi đó để rõ ràng hơn những gì PR của bạn thay đổi. Và vui lòng tránh bao gồm nhiều tính năng hoặc sửa lỗi không liên quan trong một PR duy nhất. Nếu có thể tách biệt chúng, thì tốt hơn là có nhiều PR để xem xét và hợp nhất riêng biệt. Nói chung, một PR nên làm ",["em",{},"một việc duy nhất"],"."],["h4",{"id":"once-youve-made-a-pull-request"},"Once You''ve Made a Pull Request"],["p",{},"Khi bạn đã tạo pull request, chúng tôi sẽ cố gắng hết sức để xem xét nó kịp thời."],["p",{},"Nếu chúng tôi chỉ định nó cho một người duy trì, thì điều đó có nghĩa là người đó sẽ đặc bi