UPDATE _content_info SET ready = true WHERE id = 'checksum_nuxtcore'; -- meta
CREATE TABLE IF NOT EXISTS _content_info (id TEXT PRIMARY KEY, "ready" BOOLEAN, "structureVersion" VARCHAR, "version" VARCHAR, "__hash__" TEXT UNIQUE); -- structure
DROP TABLE IF EXISTS _content_nuxtcontent; -- structure
CREATE TABLE IF NOT EXISTS _content_nuxtcontent (id TEXT PRIMARY KEY, "title" VARCHAR, "body" TEXT, "description" VARCHAR, "extension" VARCHAR, "links" TEXT, "meta" TEXT, "navigation" TEXT DEFAULT true, "path" VARCHAR, "seo" TEXT DEFAULT '{}', "stem" VARCHAR, "titleTemplate" VARCHAR NULL, "__hash__" TEXT UNIQUE); -- structure
