INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/vi/6.bridge/5.nuxt3-compatible-api.md', 'New Composition API', '{"type":"minimark","value":[["p",{},"Bằng cách di chuyển từ ",["code",{},"@nuxtjs/composition-api"]," sang API tương thích với Nuxt 3, sẽ có ít việc viết lại hơn khi di chuyển sang Nuxt 3."],["h2",{"id":"ssrref-and-shallowssrref"},["code",{},"ssrRef"]," and ",["code",{},"shallowSsrRef"]],["p",{},"Hai hàm này đã được thay thế bằng một composable mới hoạt động rất giống nhau dưới hood: ",["code",{},"useState"],"."],["p",{},"Sự khác biệt chính là bạn phải cung cấp một ",["em",{},"key"]," cho trạng thái này (mà Nuxt đã tạo tự động cho ",["code",{},"ssrRef"]," và ",["code",{},"shallowSsrRef"],"), và nó chỉ có thể được gọi trong một plugin Nuxt 3 (được định nghĩa bởi ",["code",{},"defineNuxtPlugin"],") hoặc một instance component. (Nói cách khác, bạn không thể sử dụng ",["a",{"href":"/docs/api/composables/use-state"},["code",{},"useState"]]," với ngữ cảnh global/ambient, vì nguy cơ trạng thái chia sẻ trên các yêu cầu.)"],["pre",{"className":"language-diff shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"- import { ssrRef } from ''@nuxtjs/composition-api''\n\n- const ref1 = ssrRef(''initialData'')\n- const ref2 = ssrRef(() => ''factory function'')\n+ const ref1 = useState(''ref1-key'', () => ''initialData'')\n+ const ref2 = useState(''ref2-key'', () => ''factory function'')\n  // accessing the state\n  console.log(ref1.value)\n","language":"diff","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"sDfIl"},"-"],["span",{"class":"sRlkE"}," import { ssrRef } from ''@nuxtjs/composition-api''\n"]],["span",{"class":"line","line":2},["span",{"emptyLinePlaceholder":true},"\n"]],["span",{"class":"line","line":3},["span",{"class":"sDfIl"},"-"],["span",{"class":"sRlkE"}," const ref1 = ssrRef(''initialData'')\n"]],["span",{"class":"line","line":4},["span",{"class"