INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/vi/7.migration/1.overview.md', 'Overview', '{"type":"minimark","value":[["p",{},"Có những thay đổi đáng kể khi di chuyển ứng dụng Nuxt 2 sang Nuxt 3, mặc dù bạn có thể mong đợi quá trình di chuyển trở nên đơn giản hơn khi chúng tôi tiến tới phiên bản ổn định."],["note",{},["p",{},"Hướng dẫn di chuyển này đang được tiến hành để phù hợp với sự phát triển của Nuxt 3."]],["p",{},"Một số thay đổi đáng kể này bao gồm:"],["ol",{},["li",{},"Chuyển từ Vue 2 sang Vue 3, bao gồm mặc định sử dụng Composition API và script setup."],["li",{},"Chuyển từ webpack 4 và Babel sang Vite hoặc webpack 5 và esbuild."],["li",{},"Chuyển từ phụ thuộc runtime Nuxt sang máy chủ độc lập tối thiểu được biên dịch với nitropack."]],["tip",{},["p",{},"Nếu bạn cần duy trì Nuxt 2, nhưng muốn hưởng lợi từ các tính năng của Nuxt 3 trong Nuxt 2, bạn có thể thay thế kiểm tra ",["a",{"href":"/docs/bridge/overview"},"cách bắt đầu với Bridge"],"."]],["h2",{"id":"next-steps"},"Next Steps"],["ul",{},["li",{},"Tìm hiểu về sự khác biệt trong ",["a",{"href":"/docs/migration/configuration"},"cấu hình"]]]],"toc":{"title":"","searchDepth":2,"depth":2,"links":[{"id":"next-steps","depth":2,"text":"Next Steps"}]}}', 'Nuxt 3 is a complete rewrite of Nuxt 2, and also based on a new set of underlying technologies.', 'md', NULL, '{}', 'true', '/nuxt/nuxtcore/vi/migration/overview', '{"title":"Overview","description":"Nuxt 3 is a complete rewrite of Nuxt 2, and also based on a new set of underlying technologies."}', 'nuxt/nuxtcore/vi/7.migration/1.overview', NULL, 'ZTUG8zgl0x3h7MVrhj_UuNmwGvB5ebeftBaolMntzoY'); -- ZTUG8zgl0x3h7MVrhj_UuNmwGvB5ebeftBaolMntzoY
INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/vi/7.migration/10.bundling.md', 'Build Tooling', '{"type":"minimark","value":[["p",{},"Chúng tôi sử dụng các build tools sau theo mặc định:"],["ul",{},["li",{},["a",{"href":"https://vite.dev","rel":["nofollow"]},"Vite"]," or ",["a",{"href":"https://webpack.js.org","rel":["nofollow"]},"webpack"]],["li",{},["a",{"href":"https://rollupjs.org","rel":["nofollow"]},"Rollup"]],["li",{},["a",{"href":"https://postcss.org","rel":["nofollow"]},"PostCSS"]],["li",{},["a",{"href":"https://esbuild.github.io","rel":["nofollow"]},"esbuild"]]],["p",{},"Vì lý do này, hầu hết cấu hình ",["code",{},"build"]," trước đây của bạn trong ",["code",{},"nuxt.config"]," sẽ hiện bị bỏ qua, bao gồm bất kỳ cấu hình babel tùy chỉnh nào."],["p",{},"Nếu bạn cần cấu hình bất kỳ build tools nào của Nuxt, bạn có thể làm vậy trong ",["code",{},"nuxt.config"]," của bạn, sử dụng các keys top-level mới ",["code",{},"vite"],", ",["code",{},"webpack"]," và ",["code",{},"postcss"],"."],["p",{},"Ngoài ra, Nuxt đi kèm với hỗ trợ TypeScript."],["read-more",{"to":"/docs/guide/concepts/typescript"}],["h2",{"id":"steps"},"Steps"],["ol",{},["li",{},"Xóa ",["code",{},"@nuxt/typescript-build"]," và ",["code",{},"@nuxt/typescript-runtime"]," khỏi dependencies và modules của bạn."],["li",{},"Xóa bất kỳ babel dependencies không sử dụng nào khỏi project của bạn."],["li",{},"Xóa bất kỳ core-js dependencies rõ ràng nào."],["li",{},"Di chuyển ",["code",{},"require"]," sang ",["code",{},"import"],"."]]],"toc":{"title":"","searchDepth":2,"depth":2,"links":[{"id":"steps","depth":2,"text":"Steps"}]}}', 'Learn how to migrate from Nuxt 2 to Nuxt 3 build tooling.', 'md', NULL, '{}', 'true', '/nuxt/nuxtcore/vi/migration/bundling', '{"title":"Build Tooling","description":"Learn how to migrate from Nuxt 2 to Nuxt 3 build tooling."}', 'nuxt/nuxtcore/vi/7.migration/10.bundling', NULL, 'zX0Y3MrDFHjc0Qn3KSnktowyRWUFe94Zz7p4BMJxXr8'); -- zX0Y3MrDFHjc0Qn3KSnktowyRWUFe94Zz7p4BMJxXr8
INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/vi/7.migration/11.server.md', 'Server', '{"type":"minimark","value":[["p",{},"Trong một ứng dụng Nuxt 3 đã build, không có runtime Nuxt dependency. Điều đó có nghĩa là site của bạn sẽ rất performant, và ultra-slim. Nhưng nó cũng có nghĩa là bạn không thể hook vào runtime Nuxt server hooks nữa."],["read-more",{"to":"/docs/guide/concepts/server-engine"}],["h2",{"id":"steps"},"Steps"],["ol",{},["li",{},"Xóa key ",["code",{},"render"]," trong ",["code",{},"nuxt.config"]," của bạn."],["li",{},"Bất kỳ tệp nào trong ",["code",{},"~/server/api"]," và ",["code",{},"~/server/middleware"]," sẽ được đăng ký tự động; bạn có thể xóa chúng khỏi mảng ",["code",{},"serverMiddleware"]," của bạn."],["li",{},"Cập nhật bất kỳ items nào khác trong mảng ",["code",{},"serverMiddleware"]," của bạn để trỏ đến tệp hoặc npm packages trực tiếp, thay vì sử dụng inline functions."]],["read-more",{"to":"/docs/guide/directory-structure/server"}],["read-more",{"to":"/docs/guide/going-further/hooks#server-hooks-runtime"}]],"toc":{"title":"","searchDepth":2,"depth":2,"links":[{"id":"steps","depth":2,"text":"Steps"}]}}', 'Learn how to migrate from Nuxt 2 to Nuxt 3 server.', 'md', NULL, '{}', 'true', '/nuxt/nuxtcore/vi/migration/server', '{"title":"Server","description":"Learn how to migrate from Nuxt 2 to Nuxt 3 server."}', 'nuxt/nuxtcore/vi/7.migration/11.server', NULL, 'CvAEFuYALVwr1NKaxL_RqJktVQ6sAjRh7V3BmSvQlrc'); -- CvAEFuYALVwr1NKaxL_RqJktVQ6sAjRh7V3BmSvQlrc
