INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/vi/7.migration/20.module-authors.md', 'Modules', '{"type":"minimark","value":[["h2",{"id":"module-compatibility"},"Module Compatibility"],["p",{},"Nuxt 3 có một lớp tương thích ngược cơ bản cho các modules Nuxt 2 sử dụng auto wrappers ",["code",{},"@nuxt/kit"],". Nhưng thường có các bước để làm cho modules tương thích với Nuxt 3 và đôi khi, sử dụng Nuxt Bridge là cần thiết cho cross-version compatibility."],["p",{},"Chúng tôi đã chuẩn bị một ",["a",{"href":"/docs/guide/going-further/modules"},"Hướng dẫn Dành riêng"]," cho việc authoring modules sẵn sàng Nuxt 3 sử dụng ",["code",{},"@nuxt/kit"],". Hiện tại best migration path là làm theo nó và rewrite modules của bạn. Phần còn lại của hướng dẫn này bao gồm các bước chuẩn bị nếu bạn muốn tránh một full rewrite nhưng vẫn làm cho modules tương thích với Nuxt 3."],["tip",{"icon":"i-lucide-puzzle","to":"/modules"},["p",{},"Khám phá các modules tương thích Nuxt 3."]],["h3",{"id":"plugin-compatibility"},"Plugin Compatibility"],["p",{},"Plugins Nuxt 3 ",["strong",{},"không"]," hoàn toàn tương thích ngược với Nuxt 2."],["read-more",{"to":"/docs/guide/directory-structure/plugins"}],["h3",{"id":"vue-compatibility"},"Vue Compatibility"],["p",{},"Plugins hoặc components sử dụng Composition API cần hỗ trợ Vue 2 hoặc Vue 3 độc quyền."],["p",{},"Bằng cách sử dụng ",["a",{"href":"https://github.com/vueuse/vue-demi","rel":["nofollow"]},"vue-demi"]," chúng nên tương thích với cả Nuxt 2 và 3."],["h2",{"id":"module-migration"},"Module Migration"],["p",{},"Khi users Nuxt 3 thêm module của bạn, bạn sẽ không có quyền truy cập vào module container (",["code",{},"this.*"],") vì vậy bạn sẽ cần sử dụng utilities từ ",["code",{},"@nuxt/kit"]," để truy cập chức năng container."],["h3",{"id":"test-with-nuxtbridge"},"Test with ",["code",{},"@nuxt/bridge"]],["p",{},"Di chuyển sang ",["code",{},"@nuxt/bridge"]," là bước đầu tiên và quan trọng nhất để hỗ trợ Nuxt 3."],["p",{},"Nếu bạn có mộ