INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/vi/7.migration/3.auto-imports.md', 'Auto Imports', '{"type":"minimark","value":[["note",{},["p",{},"Trong phần còn lại của tài liệu di chuyển, bạn sẽ nhận thấy rằng các tiện ích Nuxt và Vue chính không có import rõ ràng. Điều này không phải là lỗi đánh máy; Nuxt sẽ tự động import chúng cho bạn, và bạn sẽ nhận được gợi ý kiểu đầy đủ nếu bạn đã làm theo ",["a",{"href":"/docs/migration/configuration#typescript"},"các hướng dẫn"]," để sử dụng hỗ trợ TypeScript của Nuxt."]],["p",{},["a",{"href":"/docs/guide/concepts/auto-imports"},"Đọc thêm về auto imports"]],["h2",{"id":"migration"},"Migration"],["ol",{},["li",{},"Nếu bạn đã sử dụng ",["code",{},"@nuxt/components"]," trong Nuxt 2, bạn có thể xóa ",["code",{},"components: true"]," trong ",["code",{},"nuxt.config"]," của bạn. Nếu bạn có thiết lập phức tạp hơn, thì lưu ý rằng các tùy chọn component đã thay đổi một chút. Xem ",["a",{"href":"/docs/guide/directory-structure/components"},"tài liệu components"]," để biết thêm thông tin."]],["tip",{},["p",{},"Bạn có thể xem ",["code",{},".nuxt/types/components.d.ts"]," và ",["code",{},".nuxt/types/imports.d.ts"]," để xem cách Nuxt đã giải quyết auto-imports của components và composables của bạn."]]],"toc":{"title":"","searchDepth":2,"depth":2,"links":[{"id":"migration","depth":2,"text":"Migration"}]}}', 'Nuxt 3 adopts a minimal friction approach, meaning wherever possible components and composables are auto-imported.', 'md', NULL, '{}', 'true', '/nuxt/nuxtcore/vi/migration/auto-imports', '{"title":"Auto Imports","description":"Nuxt 3 adopts a minimal friction approach, meaning wherever possible components and composables are auto-imported."}', 'nuxt/nuxtcore/vi/7.migration/3.auto-imports', NULL, 'RXukxZcUhnqztfnfpBEvmeUGJw5sdt_CXXyrVBnG6MI'); -- RXukxZcUhnqztfnfpBEvmeUGJw5sdt_CXXyrVBnG6MI
