INSERT INTO _content_nuxtcore VALUES ('nuxtcore/nuxt/nuxtcore/vi/7.migration/6.pages-and-layouts.md', 'Pages and Layouts', '{"type":"minimark","value":[["h2",{"id":"appvue"},["code",{},"app.vue"]],["p",{},"Nuxt 3 cung cấp một điểm nhập tập trung cho ứng dụng của bạn qua ",["code",{},"~/app.vue"],"."],["note",{},["p",{},"Nếu bạn không có tệp ",["code",{},"app.vue"]," trong thư mục nguồn của bạn, Nuxt sẽ sử dụng phiên bản mặc định của riêng nó."]],["p",{},"Tệp này là một nơi tuyệt vời để đặt bất kỳ code tùy chỉnh nào cần chạy một lần khi ứng dụng của bạn khởi động, cũng như bất kỳ components nào có mặt trên mọi trang của ứng dụng của bạn. Ví dụ, nếu bạn chỉ có một layout, bạn có thể di chuyển điều này sang ",["code",{},"app.vue"]," thay thế."],["read-more",{"to":"/docs/guide/directory-structure/app"}],["link-example",{"to":"/docs/examples/hello-world"}],["h3",{"id":"migration"},"Migration"],["p",{},"Cân nhắc tạo một tệp ",["code",{},"app.vue"]," và bao gồm bất kỳ logic nào cần chạy một lần ở cấp cao nhất của ứng dụng của bạn. Bạn có thể kiểm tra ",["a",{"href":"/docs/guide/directory-structure/app"},"một ví dụ tại đây"],"."],["h2",{"id":"layouts"},"Layouts"],["p",{},"Nếu bạn đang sử dụng layouts trong ứng dụng của mình cho nhiều trang, chỉ có một thay đổi nhỏ cần thiết."],["p",{},"Trong Nuxt 2, component ",["code",{},"<Nuxt>"]," được sử dụng trong một layout để render trang hiện tại. Trong Nuxt 3, layouts sử dụng slots thay thế, vì vậy bạn sẽ phải thay thế component đó bằng ",["code",{},"<slot />"],". Điều này cũng cho phép các use cases nâng cao với named và scoped slots. ",["a",{"href":"/docs/guide/directory-structure/layouts"},"Đọc thêm về layouts"],"."],["p",{},"Bạn cũng sẽ cần thay đổi cách bạn định nghĩa layout được sử dụng bởi một trang bằng cách sử dụng compiler macro ",["code",{},"definePageMeta"],". Layouts sẽ được kebab-cased. Vì vậy ",["code",{},"layouts/customLayout.vue"]," trở thành ",["code",{},"custom-layout"]," khi được tham chiếu trong trang của bạn."