INSERT INTO _content_nuxtcontent VALUES ('nuxtcontent/nuxt/nuxtcontent/live/1.getting-started/4.migration.md', 'Migration', '{"type":"minimark","value":[["p",{},"Nuxt Content v3 has been rebuilt from the ground up, resulting in a new library with enhanced capabilities. While we''ve redesigned concepts and components in a similar way as Content v2, breaking changes are inevitable."],["p",{},"Don''t worry, you don''t need to modify your content files. We made sure that Content v3 handles content in the same way as Content v2."],["h2",{"id":"changes"},"Changes"],["h3",{"id":"vue-utils"},"Vue utils"],["ul",{},["li",{},["code",{},"queryContent()"]," API is replaced with new ",["code",{},"queryCollection()"]]],["prose-tip",{},["p",{},"The new API is backed by SQL and content queries happens within a specific collection."]],["ul",{},["li",{},["code",{},"fetchContentNavigation()"]," API is replaced with new ",["code",{},"queryCollectionNavigation()"]],["li",{},"Surroundings now has its own separate API ",["code",{},"queryCollectionItemSurroundings()"]],["li",{},"Document driven mode is dropped: ",["code",{},"Markdown"]," files will not convert to Nuxt pages automatically, you need to create pages, ",["a",{"href":"/docs/components/content-renderer#example-usage"},"check this section to see how"],"."],["li",{},["code",{},"useContent()"]," composable is removed"],["li",{},["code",{},"searchContent()"]," is dropped in favor of the new ",["code",{},"queryCollectionSearchSections"]," API"],["li",{},"Full text search can easily be done using the ",["code",{},"queryCollectionSearchSections"]," API, ",["a",{"href":"/docs/advanced/fulltext-search"},"check this section to see how"]]],["h3",{"id":"components"},"Components"],["ul",{},["li",{},"All content should be rendered using ",["code",{},"<ContentRenderer>"]," component. ",["code",{},"<ContentDoc>"],", ",["code",{},"<ContentList>"],", ",["code",{},"<ContentNavigation>"]," and ",["code",{},"<ContentQuery>"]," components are dropped in v3."],["li",{},["code",{},"<ContentSlot>"]," and ",["code",{},"<MDCSlot>"]," components are not supported in v3. Instead components can simply use Vue''s native ",["code",{},"<slot>"]," component"]],["prose-note",{},["p",{},["code",{},"<ContentSlot>"]," and ",["code",{},"<MDCSlot>"]," was initially pro to manipulate content before rendering and removing wrapping paragraphs from slot content. This unwrapping behavior is now supported via ",["code",{},"mdc-unwrap"]," attribute in ",["code",{},"<slot>"]," component. Example: ",["code",{},"<slot mdc-unwrap=\"p\" />"]]],["ul",{},["li",{},"Components created under the ",["code",{},"components/content"]," directory are no longer automatically registered as global components. If you use ",["a",{"href":"https://vuejs.org/guide/essentials/component-basics.html#dynamic-components","rel":["nofollow"]},"dynamic rendering"]," to render these components outside markdown files, you must manually register them in your Nuxt app. Check out the ",["a",{"href":"https://nuxt.com/docs/guide/directory-structure/components#custom-directories","rel":["nofollow"]},"Nuxt - Custom Components Directories"]," documentation for more information on how to do so."]],["h3",{"id":"types"},"Types"],["ul",{},["li",{},["code",{},"import type { NavItem } from ''@nuxt/content/dist/runtime/types''"]," is replaced with ",["code",{},"import type { ContentNavigationItem } from ''@nuxt/content''"]]],["h3",{"id":"general"},"General"],["ul",{},["li",{},["code",{},"_dir.yml"]," files are renamed to ",["code",{},".navigation.yml"]],["li",{},"There is no source option in module options, instead you can define ",["a",{"href":"/docs/collections/sources"},"multiple sources"]," for your collections in ",["code",{},"content.config.ts"],"."],["li",{},"Document ",["code",{},"._path"]," is now renamed to ",["code",{},".path"],", likewise all internal fields with ",["code",{},"_"]," prefix are removed or renamed."],["li",{},["code",{},"useContentHelpers()"]," is removed"],["li",{},"Module does not ignore dot files by default, you can ignore them by adding ",["code",{},"ignore: [''**/.*'']"]," in ",["code",{},"exclude"]," options of your collection source."],["li",{},"Due to SQL limitations, sort order now uses alphabetical order instead for numerical order. Check out the ",["a",{"href":"/docs/collections/types#ordering-files"},"Ordering Files"]," section for more information."],["li",{},"Module options have changed from v2. Check out ",["a",{"href":"/docs/getting-started/configuration"},"configuration page"]," for details."]],["h3",{"id":"nuxt-studio-integration"},"Nuxt Studio integration"],["ul",{},["li",{},"The ",["a",{"href":"https://nuxt.studio","rel":["nofollow"]},"studio module"]," has been deprecated and a new generic ",["code",{},"Preview API"]," has been implemented directly into Nuxt Content, you can remove the ",["code",{},"@nuxthq/studio"]," package from your dependencies and from the ",["code",{},"nuxt.config.ts"]," modules. Instead we just need to enable the preview mode in the Nuxt configuration file by binding the Studio API."]],["pre",{"className":"language-ts shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"export default defineNuxtConfig({\n  content: {\n    preview: {\n      api: ''https://api.nuxt.studio''\n    }\n  },\n})\n","filename":"nuxt.config.ts","language":"ts","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s8R28"},"export"],["span",{"class":"s8R28"}," default"],["span",{"class":"s3cPz"}," defineNuxtConfig"],["span",{"class":"sZSNi"},"("],["span",{"class":"sDfIl"},"{\n"]],["span",{"class":"line","line":2},["span",{"class":"sRlkE"},"  content"],["span",{"class":"sDfIl"},":"],["span",{"class":"sDfIl"}," {\n"]],["span",{"class":"line","line":3},["span",{"class":"sRlkE"},"    preview"],["span",{"class":"sDfIl"},":"],["span",{"class":"sDfIl"}," {\n"]],["span",{"class":"line","line":4},["span",{"class":"sRlk