INSERT INTO _content_nuxtcontent VALUES ('nuxtcontent/nuxt/nuxtcontent/live/2.collections/1.define.md', 'Define Content Collections', '{"type":"minimark","value":[["p",{},"The Nuxt Content module automatically parses any content files within the ",["code",{},"content/"]," directory located at the root of your Nuxt application. This setup allows you to freely structure the folder to suit your project''s needs."],["p",{},"For better organization, consider using Content Collections, which let you categorize and manage content more effectively. These collections are defined in a ",["code",{},"content.config.ts"]," file."],["warning",{},["p",{},"If no ",["code",{},"content.config.ts"]," file is present, all files within the content folder are parsed and imported by default. However, once a config file is added, only files matching the specified path patterns defined in collections will be imported."]],["h2",{"id":"what-are-content-collections"},"What are Content Collections?"],["p",{},"Content Collections organize related items within your Nuxt Content project. They provide a structured way to manage your content, making it easier to query, display, and maintain your site''s data."],["p",{},"Key features include:"],["ul",{},["li",{},["strong",{},"Logical Grouping"],": Group similar content together, such as blog posts, product pages, or documentation articles"],["li",{},["strong",{},"Shared Configuration"],": Apply common settings and validations across all items within a collection"],["li",{},["strong",{},"Improved Querying"],": Fetch and filter related content items efficiently"],["li",{},["strong",{},"Automatic Type Inference"],": Get type safety and autocompletion in your development environment"],["li",{},["strong",{},"Flexible Structure"],": Organize collections by content type, category, or any other logical grouping that suits your needs"]],["h2",{"id":"defining-collections"},"Defining Collections"],["p",{},"Create a ",["code",{},"content.config.ts"]," file in yo