INSERT INTO _content_nuxtcontent VALUES ('nuxtcontent/nuxt/nuxtcontent/live/2.collections/5.inherit-schema-from-component.md', 'Inherit Schema from a Vue Component', '{"type":"minimark","value":[["p",{},"You can reuse a Vue component''s props as part of your collection schema. This helps keep your content model aligned with your UI, reduces duplication, and prevents drift."],["h2",{"id":"how-it-works"},"How it works"],["p",{},"Nuxt Content provides a ",["code",{},"property()"]," helper that augments your validator and adds the following utility:"],["ul",{},["li",{},["strong",{},"inherit(path)"],": replace the current object schema with the props JSON Schema inferred from a Vue component at ",["code",{},"path"]]],["p",{},"Under the hood, Nuxt Content reads the component''s props (via ",["code",{},"nuxt-component-meta"],") and converts them to JSON Schema, then merges them into your collection schema."],["h2",{"id":"example"},"Example"],["pre",{"className":"language-ts shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"import { defineContentConfig, defineCollection, z, property } from ''@nuxt/content''\n\nexport default defineContentConfig({\n  collections: {\n    pages: defineCollection({\n      type: ''page'',\n      source: ''**/*.md'',\n      schema: z.object({\n        // Reuse props from a local component\n        hero: property(z.object({})).inherit(''app/components/HeroSection.vue''),\n\n        // Reuse props from a dependency (path is resolved like an import)\n        button: property(z.object({})).inherit(''@nuxt/ui/components/Button.vue'')\n      })\n    })\n  }\n})\n","filename":"content.config.ts","language":"ts","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s8R28"},"import"],["span",{"class":"sDfIl"}," {"],["span",{"class":"sZSNi"}," defineContentConfig"],["span",{"class":"sDfIl"},","],["span",{"class":"sZSNi"}," defineCollection"],["span",{"class":"sD