INSERT INTO _content_nuxtcontent VALUES ('nuxtcontent/nuxt/nuxtcontent/live/6.deploy/1.server.md', 'Server Hosting', '{"type":"minimark","value":[["h2",{"id":"what-is-nodejs-preset"},"What is Node.js preset?"],["p",{},"Node preset is the default preset for Nuxt, when building your project, Nuxt will output a Node.js server that you can run with ",["code",{},"node .output/server/index.mjs"],"."],["h2",{"id":"environment-requirement"},"Environment requirement"],["p",{},"If you are using the default ",["code",{},"better-sqlite3"]," module to operate the sqlite database,\nthen you have to deploy to an OS with Glibc version higher than 2.29, eg. Debian 11, Ubuntu 20.04."],["note",{},["p",{},"You can use ",["code",{},"ldd --version"]," to check the Glibc version. Checkout ",["a",{"href":"https://github.com/nuxt/content/issues/3248","rel":["nofollow"]},"issue #3248"]," for more details."]],["h2",{"id":"building-with-nodejs-preset"},"Building with Node.js preset"],["p",{},"Build project with Nuxt build command:"],["pre",{"className":"language-bash shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"nuxi build\n","filename":"Terminal","language":"bash","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s52Pk"},"nuxi"],["span",{"class":"sGFVr"}," build\n"]]]],["p",{},"When running ",["code",{},"nuxi build"]," with the Node server preset, the result will be an entry point that launches a ready-to-run Node server."],["pre",{"className":"language-bash shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"$ node .output/server/index.mjs\nListening on http://localhost:3000\n","filename":"Terminal","language":"bash","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s52Pk"},"$"],["span",{"class":"sGFVr"}," node"],["span",{"class":"sGFVr"}," .output/server/index.mjs\n"]],["span",{"class":"line","line":2},["span",{"class":"s52Pk"},"Listening"],["span",{"class":"sGFVr"}," on"],["span",{"class":"sGFVr"}," http://localhost:3000\n"]]]],["note",{},["p",{},"The SQLite database will be loaded on the server side when booting the server as well as in the browser for client-side navigation or actions."]],["style",{},"html pre.shiki code .s52Pk, html code.shiki .s52Pk{--shiki-light:#E2931D;--shiki-default:#E2931D;--shiki-dark:#FFCB6B}html pre.shiki code .sGFVr, html code.shiki .sGFVr{--shiki-light:#91B859;--shiki-default:#91B859;--shiki-dark:#C3E88D}html .light .shiki span {color: var(--shiki-light);background: var(--shiki-light-bg);font-style: var(--shiki-light-font-style);font-weight: var(--shiki-light-font-weight);text-decoration: var(--shiki-light-text-decoration);}html.light .shiki span {color: var(--shiki-light);background: var(--shiki-light-bg);font-style: var(--shiki-light-font-style);font-weight: var(--shiki-light-font-weight);text-decoration: var(--shiki-light-text-decoration);}html .default .shiki span {color: var(--shiki-default);background: var(--shiki-default-bg);font-style: var(--shiki-default-font-style);font-weight: var(--shiki-default-font-weight);text-decoration: var(--shiki-default-text-decoration);}html .shiki span {color: var(--shiki-default);background: var(--shiki-default-bg);font-style: var(--shiki-default-font-style);font-weight: var(--shiki-default-font-weight);text-decoration: var(--shiki-default-text-decoration);}html .dark .shiki span {color: var(--shiki-dark);background: var(--shiki-dark-bg);font-style: var(--shiki-dark-font-style);font-weight: var(--shiki-dark-font-weight);text-decoration: var(--shiki-dark-text-decoration);}html.dark .shiki span {color: var(--shiki-dark);background: var(--shiki-dark-bg);font-style: var(--shiki-dark-font-style);font-weight: var(--shiki-dark-font-weight);text-decoration: var(--shiki-dark-text-decoration);}"]],"toc":{"title":"","searchDepth":2,"depth":2,"links":[{"id":"what-is-nodejs-preset","depth":2,"text":"What is Node.js preset?"},{"id":"environment-requirement","depth":2,"text":"Environment requirement"},{"id":"building-with-nodejs-preset","depth":2,"text":"Building with Node.js preset"}]}}', 'Node preset is the default preset for Nuxt and Nuxt Content. It is used to build and run Nuxt applications on Node.js.', 'md', NULL, '{}', '{"title":"Server"}', '/nuxt/nuxtcontent/live/deploy/server', '{"title":"Server Hosting","description":"Node preset is the default preset for Nuxt and Nuxt Content. It is used to build and run Nuxt applications on Node.js."}', 'nuxt/nuxtcontent/live/6.deploy/1.server', NULL, 'LcbVrRqlu-y7LOGVQ35WbvjKijsWabtZ-CmIN_PMo-A'); -- LcbVrRqlu-y7LOGVQ35WbvjKijsWabtZ-CmIN_PMo-A
INSERT INTO _content_nuxtcontent VALUES ('nuxtcontent/nuxt/nuxtcontent/live/6.deploy/10.static.md', 'Static Hosting', '{"type":"minimark","value":[["h2",{"id":"what-is-static-hosting"},"What is Static Hosting?"],["p",{},"Static hosting is a type of hosting where your website is built and served as static files (HTML, CSS, JS) that can be served by any static file server."],["p",{},"Nuxt Content can be deployed to static hosting using Nuxt prerendering."],["h2",{"id":"building-with-ssg"},"Building with SSG"],["p",{},"To build your app with static site generation, run the following command:"],["pre",{"className":"language-bash shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"npx nuxi generate\n","language":"bash","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s52Pk"},"npx"],["span",{"class":"sGFVr"}," nuxi"],["span",{"class":"sGFVr"}," generate\n"]]]],["tip",{"icon":"i-lucide-check"},["p",{},"This command will create a ",["code",{},"dist/"]," directory with your static site. You can upload it to any static hosting service."]],["p",{},"Nuxt will automatically pre-render all pages using an internal crawler, you can customize it''s behavior with the ",["code",{},"nitro.prerender"]," options."],["note",{"to":"https://nuxt.com/docs/getting-started/prerendering"},["p",{},"Learn more about pre-rendering in Nuxt."]],["h2",{"id":"what-about-the-database"},"What about the Database?"],["p",{},"Nuxt Content will load the database in the browser using ",["a",{"href":"/docs/advanced/database#wasm-sqlite-in-browser"},"WASM SQLite"],", this way, the content queries happening on client-side navigation or actions will run in the browser."],["style",{},"html pre.shiki code .s52Pk, html code.shiki .s52Pk{--shiki-light:#E2931D;--shiki-default:#E2931D;--shiki-dark:#FFCB6B}html pre.shiki code .sGFVr, html code.shiki .sGFVr{--shiki-light:#91B859;--shiki-default:#91B859;--shiki-dark:#C3E88D