INSERT INTO _content_nuxtcontent VALUES ('nuxtcontent/nuxt/nuxtcontent/live/6.deploy/6.vercel.md', 'Vercel', '{"type":"minimark","value":[["card",{},["p",{},"Quick Setup"],["ul",{},["li",{},"Execute ",["code",{},"npx vercel deploy"]," command or go to Vercel dashboard and create a new project using git repository."]]],["hr",{}],["p",{},"Nuxt Content projects can be deployed to Vercel with zero configuration. The module will automatically detect a Vercel environment and will prepare the necessary configuration for deployment."],["p",{},"All you need to do is to execute ",["code",{},"npx vercel deploy"]," command or go to Vercel dashboard and create a new project using git repository."],["p",{},"That''s it 🎉"],["note",{},["p",{},"By default module will use SQlite database in Vercel located at ",["code",{},"/tmp"]," directory. You can override the database configuration by providing your own database configuration."],["br",{}],["p",{},"There are a couple of database providers that are supported by Vercel. You can use any of them by providing the correct connection string in ",["code",{},"nuxt.config.ts"],"."]],["p",{},"Checkout:"],["ul",{},["li",{},["a",{"href":"https://nuxt.com/deploy/vercel","rel":["nofollow"]},"Nuxt Deploy documentation"]],["li",{},["a",{"href":"https://vercel.com/docs/deployments/deployment-methods","rel":["nofollow"]},"Vercel documentation"]]]],"toc":{"title":"","searchDepth":2,"depth":2,"links":[]}}', 'Deploy your Content app to Vercel', 'md', NULL, '{}', 'true', '/nuxt/nuxtcontent/live/deploy/vercel', '{"title":"Vercel","description":"Deploy your Content app to Vercel"}', 'nuxt/nuxtcontent/live/6.deploy/6.vercel', NULL, 'MA-eB2uDItPLdYr9howU4Z19-1CT-qjwF1Lo9l0RGqA'); -- MA-eB2uDItPLdYr9howU4Z19-1CT-qjwF1Lo9l0RGqA
INSERT INTO _content_nuxtcontent VALUES ('nuxtcontent/nuxt/nuxtcontent/live/6.deploy/7.netlify.md', 'Netlify', '{"type":"minimark","value":[["card",{},["p",{},"Quick Setup"],["ul",{},["li",{},"Go go Netlify dashboard and create a new project using git repository."],["li",{},"Go to ",["code",{},"Site Configuration"]," under ",["code",{},"Dependency management"]," and change Node Version to ",["code",{},"20.x"]," or higher."],["li",{},"Go to ",["code",{},"deploys"]," and retry last deployment."]]],["hr",{}],["p",{},"Nuxt Content projects can be deployed to Netlify with zero configuration. The module will automatically detects Netlify environment and prepare the necessary configuration for Netlify."],["p",{},"All you need to do is to go to Netlify dashboard and create a new project using git repository."],["note",{},["p",{},"By default Netlify uses Node.js 18.x which is not supported by the module. You need to change the Node.js version in ",["code",{},"Site Configuration"]," under ",["code",{},"Dependency management"],"."]],["p",{},"That''s it 🎉"],["p",{},"Checkout:"],["ul",{},["li",{},["a",{"href":"https://nuxt.com/deploy/netlify","rel":["nofollow"]},"Nuxt Deploy documentation"]],["li",{},["a",{"href":"https://www.netlify.com/blog/2016/09/29/a-step-by-step-guide-deploying-on-netlify/","rel":["nofollow"]},"Netlify documentation"]]]],"toc":{"title":"","searchDepth":2,"depth":2,"links":[]}}', 'Deploy your Content app to Netlify', 'md', NULL, '{}', 'true', '/nuxt/nuxtcontent/live/deploy/netlify', '{"title":"Netlify","description":"Deploy your Content app to Netlify"}', 'nuxt/nuxtcontent/live/6.deploy/7.netlify', NULL, 'fueeJIX5-PX2HbQVvsSongEi29kSVUftvMAzYxcP1rU'); -- fueeJIX5-PX2HbQVvsSongEi29kSVUftvMAzYxcP1rU
INSERT INTO _content_nuxtcontent VALUES ('nuxtcontent/nuxt/nuxtcontent/live/6.deploy/8.aws-amplify.md', 'AWS Amplify', '{"type":"minimark","value":[["card",{},["p",{},"Quick Setup"],["ul",{},["li",{},"Install ",["code",{},"sqlite3"]," package in your project."],["li",{},"Go to AWS Amplify dashboard and create a new project using git repository and deploy the app."]]],["hr",{}],["p",{},"Nuxt Content projects can be deployed to AWS Amplify with zero configuration.\nThe module will automatically detect an AWS Amplify environment and will prepare the necessary configuration for deployment."],["p",{},"All you need to do is to install ",["code",{},"sqlite3"]," package in your project and go to AWS Amplify dashboard and create a new project using git repository."],["p",{},"That''s it 🎉"],["note",{},["p",{},"By default module will use SQlite database located at ",["code",{},"/tmp"]," directory. You can override the database configuration by providing your own database configuration."]],["p",{},"Checkout:"],["ul",{},["li",{},["a",{"href":"https://nuxt.com/deploy/aws-amplify","rel":["nofollow"]},"Nuxt Deploy documentation"]]]],"toc":{"title":"","searchDepth":2,"depth":2,"links":[]}}', 'Deploy your Content app to AWS Amplify', 'md', NULL, '{}', 'true', '/nuxt/nuxtcontent/live/deploy/aws-amplify', '{"title":"AWS Amplify","description":"Deploy your Content app to AWS Amplify"}', 'nuxt/nuxtcontent/live/6.deploy/8.aws-amplify', NULL, 'zAVi2O75q3Lg_jPXUicIMYV7O8P-C5wZSkkpuzILLQY'); -- zAVi2O75q3Lg_jPXUicIMYV7O8P-C5wZSkkpuzILLQY
INSERT INTO _content_nuxtcontent VALUES ('nuxtcontent/nuxt/nuxtcontent/live/6.deploy/9.docker.md', 'Docker', '{"type":"minimark","value":[["p",{},"Docker is a popular containerization platform that allows you to package your application with all its dependencies into a single container. This makes it easy to deploy your Content app on any platform that supports Docker."],["h2",{"id":"with-nodejs-image"},"With Node.js image"],["p",{},"Using Docker''s Node.js image, you can deploy your Content app. All you need is to create a Dockerfile and build the image. Here is an example Dockerfile:"],["pre",{"className":"language-docker shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"# Build Stage 1\n\nFROM node:22-alpine AS build\nWORKDIR /app\n\nRUN corepack enable\n\n# Copy package.json and your lockfile, here we add pnpm-lock.yaml for illustration\nCOPY package.json pnpm-lock.yaml .npmrc ./\n\n# Install dependencies\nRUN pnpm i\n\n# Copy the entire project\nCOPY . ./\n\n# Build the project\nRUN pnpm run build\n\n# Build Stage 2\n\nFROM node:22-alpine\nWORKDIR /app\n\n# Only `.output` folder is needed from the build stage\nCOPY --from=build /app/.output/ ./\n\n# Change the port and host\nENV PORT=80\nENV HOST=0.0.0.0\n\nEXPOSE 80\n\nCMD [\"node\", \"/app/server/index.mjs\"]\n","filename":"Dockerfile","language":"docker","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{},"# Build Stage 1\n"]],["span",{"class":"line","line":2},["span",{"emptyLinePlaceholder":true},"\n"]],["span",{"class":"line","line":3},["span",{},"FROM node:22-alpine AS build\n"]],["span",{"class":"line","line":4},["span",{},"WORKDIR /app\n"]],["span",{"class":"line","line":5},["span",{"emptyLinePlaceholder":true},"\n"]],["span",{"class":"line","line":6},["span",{},"RUN corepack enable\n"]],["span",{"class":"line","line":7},["span",{"emptyLinePlaceholder":true},"\n"]],["span",{"class":"line","line":8},["span",{},"# Copy package.json and your lockfile, here we add pnpm-lock.yaml for illustration\n"]],["span",{"class":"line","line":9},["span",{},"COPY package.json pnpm-lock.yaml .npmrc ./\n"]],["span",{"class":"line","line":10},["span",{"emptyLinePlaceholder":true},"\n"]],["span",{"class":"line","line":11},["span",{},"# Install dependencies\n"]],["span",{"class":"line","line":12},["span",{},"RUN pnpm i\n"]],["span",{"class":"line","line":13},["span",{"emptyLinePlaceholder":true},"\n"]],["span",{"class":"line","line":14},["span",{},"# Copy the entire project\n"]],["span",{"class":"line","line":15},["span",{},"COPY . ./\n"]],["span",{"class":"line","line":16},["span",{"emptyLinePlaceholder":true},"\n"]],["span",{"class":"line","line":17},["span",{},"# Build the project\n"]],["span",{"class":"line","line":18},["span",{},"RUN pnpm run build\n"]],["span",{"class":"line","line":19},["span",{"emptyLinePlaceholder":true},"\n"]],["span",{"class":"line","line":20},["span",{},"# Build Stage 2\n"]],["span",{"class":"line","line":21},["span",{"emptyLinePlaceholder":true},"\n"]],["span",{"class":"line","line":22},["span",{},"FROM node:22-alpine\n"]],["span",{"class":"line","line":23},["span",{},"WORKDIR /app\n"]],["span",{"class":"line","line":24},["span",{"emptyLinePlaceholder":true},"\n"]],["span",{"class":"line","line":25},["span",{},"# Only `.output` folder is needed from the build stage\n"]],["span",{"class":"line","line":26},["span",{},"COPY --from=build /app/.output/ ./\n"]],["span",{"class":"line","line":27},["span",{"emptyLinePlaceholder":true},"\n"]],["span",{"class":"line","line":28},["span",{},"# Change the port and host\n"]],["span",{"class":"line","line":29},["span",{},"ENV PORT=80\n"]],["span",{"class":"line","line":30},["span",{},"ENV HOST=0.0.0.0\n"]],["span",{"class":"line","line":31},["span",{"emptyLinePlaceholder":true},"\n"]],["span",{"class":"line","line":32},["span",{},"EXPOSE 80\n"]],["span",{"class":"line","line":33},["span",{"emptyLinePlaceholder":true},"\n"]],["span",{"class":"line","line":34},["span",{},"CMD [\"node\", \"/app/server/index.mjs\"]\n"]]]],["h2",{"id":"with-bun-image"},"With Bun image"],["p",{},"If you like to use Bun, you can use the official Bun image. Here is an example Dockerfile:"],["pre",{"className":"language-docker shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"# use the official Bun image\n# see all versions at https://hub.docker.com/r/oven/bun/tags\nFROM oven/bun:1 AS build\nWORKDIR /app\n\nCOPY package.json bun.lockb ./\n\n# use ignore-scripts to avoid builting node modules like better-sqlite3\nRUN bun install --frozen-lockfile --ignore-scripts\n\n# Copy the entire project\nCOPY . .\n\nRUN bun --bun run build\n\n# copy production dependencies and source code into final image\nFROM oven/bun:1 AS production\nWORKDIR /app\n\n# Only `.output` folder is needed from the build stage\nCOPY --from=build /app/.output /app\n\n# run the app\nEXPOSE 3000/tcp\nENTRYPOINT [ \"bun\", \"--bun\", \"run\", \"/app/server/index.mjs\" ]\n","filename":"Dockerfile","language":"docker","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{},"# use the official Bun image\n"]],["span",{"class":"line","line":2},["span",{},"# see all versions at https://hub.docker.com/r/oven/bun/tags\n"]],["span",{"class":"line","line":3},["span",{},"FROM oven/bun:1 AS build\n"]],["span",{"class":"line","line":4},["span",{},"WORKDIR /app\n"]],["span",{"class":"line","line":5},["span",{"emptyLinePlaceholder":true},"\n"]],["span",{"class":"line","line":6},["span",{},"COPY package.json bun.lockb ./\n"]],["span",{"class":"line","line":7},["span",{"emptyLinePlaceholder":true},"\n"]],["span",{"class":"line","line":8},["span",{},"# use ignore-scripts to avoid builting node modules like better-sqlite3\n"]],["span",{"class":"line","line":9},["span",{},"RUN bun install --frozen-lockfile --ignore-scripts\n"]],["span",{"class":"line","line":10},["span",{"emptyLinePlacehol