INSERT INTO _content_nuxtcontent VALUES ('nuxtcontent/nuxt/nuxtcontent/live/8.advanced/8.transformers.md', 'Transformers', '{"type":"minimark","value":[["p",{},"Transformers in Nuxt Content allow you to programmatically parse, modify, or analyze your content files as they are processed. They are especially useful for:"],["ul",{},["li",{},"Adding or modifying fields (e.g., appending to the title, generating slugs)"],["li",{},"Extracting metadata (e.g., listing used components)"],["li",{},"Enriching content with computed data"],["li",{},"Supporting new content types"]],["h2",{"id":"defining-a-transformer"},"Defining a Transformer"],["p",{},"You can define a transformer using the ",["code",{},"defineTransformer"]," helper from ",["code",{},"@nuxt/content"],":"],["pre",{"className":"language-ts shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"import { defineTransformer } from ''@nuxt/content''\n\nexport default defineTransformer({\n  name: ''title-suffix'',\n  extensions: [''.md''], // File extensions to apply this transformer to\n  transform(file) {\n    // Modify the file object as needed\n    return {\n      ...file,\n      title: file.title + '' (suffix)'',\n    }\n  },\n})\n","filename":"~~/transformers/title-suffix.ts","language":"ts","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s8R28"},"import"],["span",{"class":"sDfIl"}," {"],["span",{"class":"sZSNi"}," defineTransformer"],["span",{"class":"sDfIl"}," }"],["span",{"class":"s8R28"}," from"],["span",{"class":"sDfIl"}," ''"],["span",{"class":"sGFVr"},"@nuxt/content"],["span",{"class":"sDfIl"},"''\n"]],["span",{"class":"line","line":2},["span",{"emptyLinePlaceholder":true},"\n"]],["span",{"class":"line","line":3},["span",{"class":"s8R28"},"export"],["span",{"class":"s8R28"}," default"],["span",{"class":"s3cPz"}," defineTransformer"],["span",{"class":"sZSNi"},"("],["span",{"class":"sDfIl"},"{\n"]],["span",{"class"