INSERT INTO _content_nuxtcontent VALUES ('nuxtcontent/nuxt/nuxtcontent/live/9.studio/.navigation.yml', 'Studio', '{"icon":"i-lucide-monitor","title":"Studio"}', NULL, 'yml', NULL, '{"icon":"i-lucide-monitor"}', 'true', '/nuxt/nuxtcontent/live/studio/.navigation', '{"title":"Studio"}', 'nuxt/nuxtcontent/live/9.studio/.navigation', NULL, 'bILqvRJUP5OAbns5J1U-GXTcbVkAEUS-e9YFZIHIx7Q'); -- bILqvRJUP5OAbns5J1U-GXTcbVkAEUS-e9YFZIHIx7Q
INSERT INTO _content_nuxtcontent VALUES ('nuxtcontent/nuxt/nuxtcontent/live/9.studio/1.setup.md', 'Setup Nuxt Studio', '{"type":"minimark","value":[["p",{},"Studio is an intuitive CMS interface to edit your Nuxt Content websites."],["p",{},"It takes advantage of the ",["code",{},"Preview API"]," included in Nuxt Content to propose the best editing experience for your content files. Editors can benefit from a user-friendly interface to edit their ",["code",{},"Markdown"],", ",["code",{},"YAML"]," or ",["code",{},"JSON"]," files."],["p",{},"Developers can customize the editing experience and provide tools to the editor who can focus on content without requiring any technical expertise."],["video",{":autoplay":"true",":controls":"true",":loop":"true","poster":"/home/videos/HomeNotionLikePoster.webp","src":"https://res.cloudinary.com/nuxt/video/upload/v1733494722/contentv3final_rc8bvu.mp4"}],["h2",{"id":"authentication"},"Authentication"],["p",{},"The Studio admin is located on ",["a",{"href":"https://nuxt.studio","rel":["nofollow"]},"nuxt.studio"],". From there you can either login with GitHub or with Google. Both methods give you the same editing rights but since Studio is ",["a",{"href":"/docs/studio/github"},"synchronized with GitHub"],", the repository import must be handled by a GitHub user."],["warning",{},["p",{},"Google authentication is adapted for non technical users. Google users have to join a team with existing projects to edit them."]],["h2",{"id":"connect-your-nuxt-content-repository"},"Connect your Nuxt Content Repository"],["p",{},"Once you are logged in the admin, you have the possibility to import your Nuxt Content repository from the interface."],["tip",{},["p",{},"Two options are available, you can either import an existing repository or use one of our ",["a",{"href":"/templates"},"templates"],"."]],["p",{},"The only requirement is to host your repository on GitHub. From the interface, connect to our ",["a",{"href":"/docs/studio/github"},"GitHub app"],", you''ll be then able to link a GitHub repository to your Studio project."],["p",{},"Once the project is created, you can start editing your files with the Studio editors and publish your changes."],["h2",{"id":"enable-the-full-editing-experience"},["strong",{},"Enable the Full Editing Experience"]],["p",{},"To unlock the complete range of features in Studio, make sure your project URL is properly configured in the deployment section."],["p",{},["strong",{},"This will enable all Studio features including:"]],["ul",{},["li",{},["strong",{},"Live preview of your website:"]," instantly see changes reflected on your site as you work."],["li",{},["strong",{},"Dynamic form generation:"]," Automatically generate forms to edit your files based on your Nuxt Content ",["a",{"href":"/docs/collections/define"},"collections"],"."],["li",{},["strong",{},"Enhanced markdown editors:"]," enjoy an improved editing experience, including the ability to list and integrate ",["a",{"href":"/docs/studio/content#vue-components"},"Vue components"]," that you’ve exposed."],["li",{},["strong",{},"Automatic app configuration:"]," generate ",["a",{"href":"/docs/studio/config"},"application configurations"]," based on the schema you’ve defined."]],["tip",{},["p",{},"We are proposing a GitHub Pages deployment. By using it, we will handle all the requirements for you. However, if you want to use the deployment platform of your choice, please follow the simple guideline below."]],["p",{},"To be able to set your URL in Studio, you just need to set the ",["code",{},"Preview API"]," in the content configuration of your ",["code",{},"nuxt.config.ts"]," file."],["pre",{"className":"language-ts shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"export default defineNuxtConfig({\n  content: {\n    preview: {\n      api: ''https://api.nuxt.studio''\n    }\n  }\n})\n","filename":"nuxt.config.ts","language":"ts","meta":"","style":""},["code",{"__ignoreMap"