INSERT INTO _content_nuxtcontent VALUES ('nuxtcontent/nuxt/nuxtcontent/live/9.studio/4.medias.md', 'Manage and integrate Medias in Nuxt Content Studio CMS', '{"type":"minimark","value":[["h2",{"id":"browse-your-medias"},"Browse your medias"],["p",{},"All medias located in the ",["code",{},"/public"]," directory are available in the ",["strong",{},"Media"]," tab of the Studio platform."],["p",{},["img",{"alt":"Media gallery on Studio","src":"/docs/studio/editors-medias.webp"}]],["p",{},"It''s an intuitive interface for non technical users to manage their ",["code",{},"/public"]," directory."],["p",{},"Users can easily browse folders, upload new media at any level, and drag and drop media into other folders, making medias organization straightforward."],["p",{},"The interface is designed to be intuitive for non-technical users. It can be viewed as a user friendly IDE."],["h2",{"id":"use-it-in-the-notion-like-editor"},"Use it in the Notion-like editor"],["p",{},"Users can simply drag and drop images directly into the editor. An upload modal will open to let you choose the destination folder."],["p",{},"By typing ",["code",{},"/"]," and searching for ",["code",{},"Image"],", they can quickly insert a media. A modal will open to let them choose the media they want to insert."],["p",{},"From the media modal, you can set the ",["a",{"href":"https://www.w3schools.com/tags/att_img_alt.asp","rel":["nofollow"]},"alt attribute"]," for SEO and accessibility purpose."]],"toc":{"title":"","searchDepth":2,"depth":2,"links":[{"id":"browse-your-medias","depth":2,"text":"Browse your medias"},{"id":"use-it-in-the-notion-like-editor","depth":2,"text":"Use it in the Notion-like editor"}]}}', 'Explore how to browse and manage media files, and integrate them into your projects using Nuxt Content Studio CMS features.', 'md', NULL, '{}', '{"title":"Media Library"}', '/nuxt/nuxtcontent/live/studio/medias', '{"title":"Manage and integrate Medias in Nuxt Content Studio CMS","description":"Explore how to browse and manage media files, and integrate them into your projects using Nuxt Content Studio CMS features."}', 'nuxt/nuxtcontent/live/9.studio/4.medias', NULL, 'OmgwaGiFyE2kjLYBPgjFfKzZelALgpIG4erPIta8TFE'); -- OmgwaGiFyE2kjLYBPgjFfKzZelALgpIG4erPIta8TFE
