INSERT INTO _content_nuxtcontent VALUES ('nuxtcontent/nuxt/nuxtcontent/vi/1.getting-started/4.migration.md', 'Migration', '{"type":"minimark","value":[["p",{},"Nuxt Content v3 đã được xây dựng lại từ đầu, dẫn đến một thư viện mới với khả năng nâng cao. Mặc dù chúng tôi đã thiết kế lại các khái niệm và thành phần tương tự như Content v2, nhưng những thay đổi đột phá là không thể tránh khỏi."],["p",{},"Đừng lo lắng, bạn không cần sửa đổi các tệp nội dung của mình. Chúng tôi đảm bảo rằng Content v3 xử lý nội dung theo cùng cách như Content v2."],["h2",{"id":"changes"},"Changes"],["h3",{"id":"vue-utils"},"Vue utils"],["ul",{},["li",{},["code",{},"queryContent()"]," API được thay thế bằng ",["code",{},"queryCollection()"]," mới"]],["prose-tip",{},["p",{},"API mới được hỗ trợ bởi SQL và các truy vấn nội dung xảy ra trong một bộ sưu tập cụ thể."]],["ul",{},["li",{},["code",{},"fetchContentNavigation()"]," API được thay thế bằng ",["code",{},"queryCollectionNavigation()"]," mới"],["li",{},"Surroundings hiện có API riêng biệt ",["code",{},"queryCollectionItemSurroundings()"]],["li",{},"Chế độ Document driven bị loại bỏ: Các tệp ",["code",{},"Markdown"]," sẽ không chuyển đổi thành các trang Nuxt tự động, bạn cần tạo các trang, ",["a",{"href":"/docs/components/content-renderer#example-usage"},"check this section to see how"],"."],["li",{},"Composable ",["code",{},"useContent()"]," bị loại bỏ"],["li",{},["code",{},"searchContent()"]," bị loại bỏ để ủng hộ API ",["code",{},"queryCollectionSearchSections"]," mới"],["li",{},"Tìm kiếm toàn văn có thể dễ dàng thực hiện bằng API ",["code",{},"queryCollectionSearchSections"],", ",["a",{"href":"/docs/advanced/fulltext-search"},"check this section to see how"]]],["h3",{"id":"components"},"Components"],["ul",{},["li",{},"Tất cả nội dung nên được hiển thị bằng thành phần ",["code",{},"<ContentRenderer>"],". Các thành phần ",["code",{},"<ContentDoc>"],", ",["code",{},"<ContentList>"],", ",["code",{},"<ContentNavigation>"]," và ",["code",{}