INSERT INTO _content_nuxtcontent VALUES ('nuxtcontent/nuxt/nuxtcontent/vi/2.collections/1.define.md', 'Define Content Collections', '{"type":"minimark","value":[["p",{},"Module Nuxt Content tự động phân tích bất kỳ tệp nội dung nào trong thư mục ",["code",{},"content/"]," nằm ở gốc của ứng dụng Nuxt của bạn. Thiết lập này cho phép bạn tự do cấu trúc thư mục để phù hợp với nhu cầu của dự án."],["p",{},"Để tổ chức tốt hơn, hãy cân nhắc sử dụng Content Collections, cho phép bạn phân loại và quản lý nội dung hiệu quả hơn. Các bộ sưu tập này được định nghĩa trong tệp ",["code",{},"content.config.ts"],"."],["warning",{},["p",{},"Nếu không có tệp ",["code",{},"content.config.ts"],", tất cả tệp trong thư mục content sẽ được phân tích và nhập theo mặc định. Tuy nhiên, một khi tệp cấu hình được thêm vào, chỉ những tệp khớp với các mẫu đường dẫn được chỉ định trong bộ sưu tập mới được nhập."]],["h2",{"id":"what-are-content-collections"},"What are Content Collections?"],["p",{},"Content Collections tổ chức các mục liên quan trong dự án Nuxt Content của bạn. Chúng cung cấp cách có cấu trúc để quản lý nội dung của bạn, giúp dễ dàng truy vấn, hiển thị và duy trì dữ liệu trang web của bạn."],["p",{},"Các tính năng chính bao gồm:"],["ul",{},["li",{},["strong",{},"Nhóm logic"],": Nhóm nội dung tương tự lại với nhau, chẳng hạn như bài đăng blog, trang sản phẩm hoặc bài viết tài liệu"],["li",{},["strong",{},"Cấu hình chia sẻ"],": Áp dụng cài đặt và xác thực chung trên tất cả mục trong bộ sưu tập"],["li",{},["strong",{},"Truy vấn cải thiện"],": Lấy và lọc các mục nội dung liên quan hiệu quả"],["li",{},["strong",{},"Suy luận loại tự động"],": Nhận an toàn loại và tự động hoàn thành trong môi trường phát triển của bạn"],["li",{},["strong",{},"Cấu trúc linh hoạt"],": Tổ chức bộ sưu tập theo loại nội dung, danh mục hoặc bất kỳ nhóm logic nào phù hợp với nhu cầu của bạn"]],["h2",{"id":"defining-collections"},"Defining Collections"],["p",{},"Tạo tệp ",["code",{},"content.config.ts"]," trong t