INSERT INTO _content_nuxtcontent VALUES ('nuxtcontent/nuxt/nuxtcontent/vi/6.deploy/1.server.md', 'Server Hosting', '{"type":"minimark","value":[["h2",{"id":"what-is-nodejs-preset"},"What is Node.js preset?"],["p",{},"Node preset là preset mặc định cho Nuxt, khi xây dựng dự án của bạn, Nuxt sẽ xuất ra một máy chủ Node.js mà bạn có thể chạy với ",["code",{},"node .output/server/index.mjs"],"."],["h2",{"id":"environment-requirement"},"Environment requirement"],["p",{},"Nếu bạn đang sử dụng module ",["code",{},"better-sqlite3"]," mặc định để vận hành cơ sở dữ liệu sqlite,\nthì bạn phải triển khai lên một hệ điều hành có phiên bản Glibc cao hơn 2.29, ví dụ: Debian 11, Ubuntu 20.04."],["note",{},["p",{},"Bạn có thể sử dụng ",["code",{},"ldd --version"]," để kiểm tra phiên bản Glibc. Xem ",["a",{"href":"https://github.com/nuxt/content/issues/3248","rel":["nofollow"]},"issue #3248"]," để biết thêm chi tiết."]],["h2",{"id":"building-with-nodejs-preset"},"Building with Node.js preset"],["p",{},"Xây dựng dự án với lệnh build của Nuxt:"],["pre",{"className":"language-bash shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"nuxi build\n","filename":"Terminal","language":"bash","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s52Pk"},"nuxi"],["span",{"class":"sGFVr"}," build\n"]]]],["p",{},"Khi chạy ",["code",{},"nuxi build"]," với preset máy chủ Node, kết quả sẽ là một điểm nhập khởi chạy máy chủ Node sẵn sàng chạy."],["pre",{"className":"language-bash shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"$ node .output/server/index.mjs\nListening on http://localhost:3000\n","filename":"Terminal","language":"bash","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s52Pk"},"$"],["span",{"class":"sGFVr"}," node"],["span",{"class":"sGFVr"}," .output/server/index.mjs\n"]],["span",{"class":"line","line":2},["span",{"class":"s52Pk"},"Listening"],["span",{"class":"sGFVr"}," on"],["span",{"class":"sGFVr"}," http://localhost:3000\n"]]]],["note",{},["p",{},"Cơ sở dữ liệu SQLite sẽ được tải ở phía máy chủ khi khởi động máy chủ cũng như trong trình duyệt cho điều hướng hoặc hành động phía máy khách."]],["style",{},"html pre.shiki code .s52Pk, html code.shiki .s52Pk{--shiki-light:#E2931D;--shiki-default:#E2931D;--shiki-dark:#FFCB6B}html pre.shiki code .sGFVr, html code.shiki .sGFVr{--shiki-light:#91B859;--shiki-default:#91B859;--shiki-dark:#C3E88D}html .light .shiki span {color: var(--shiki-light);background: var(--shiki-light-bg);font-style: var(--shiki-light-font-style);font-weight: var(--shiki-light-font-weight);text-decoration: var(--shiki-light-text-decoration);}html.light .shiki span {color: var(--shiki-light);background: var(--shiki-light-bg);font-style: var(--shiki-light-font-style);font-weight: var(--shiki-light-font-weight);text-decoration: var(--shiki-light-text-decoration);}html .default .shiki span {color: var(--shiki-default);background: var(--shiki-default-bg);font-style: var(--shiki-default-font-style);font-weight: var(--shiki-default-font-weight);text-decoration: var(--shiki-default-text-decoration);}html .shiki span {color: var(--shiki-default);background: var(--shiki-default-bg);font-style: var(--shiki-default-font-style);font-weight: var(--shiki-default-font-weight);text-decoration: var(--shiki-default-text-decoration);}html .dark .shiki span {color: var(--shiki-dark);background: var(--shiki-dark-bg);font-style: var(--shiki-dark-font-style);font-weight: var(--shiki-dark-font-weight);text-decoration: var(--shiki-dark-text-decoration);}html.dark .shiki span {color: var(--shiki-dark);background: var(--shiki-dark-bg);font-style: var(--shiki-dark-font-style);font-weight: var(--shiki-dark-font-weight);text-decoration: var(--shiki-dark-text-decoration);}"]],"toc":{"title":"","searchDepth":2,"depth":2,"links":[{"id":"what-is-nodejs-preset","depth":2,"text":"What is Node.js preset?"},{"id":"environment-requirement","depth":2,"text":"Environment requirement"},{"id":"building-with-nodejs-preset","depth":2,"text":"Building with Node.js preset"}]}}', 'Node preset is the default preset for Nuxt and Nuxt Content. It is used to build and run Nuxt applications on Node.js.', 'md', NULL, '{}', '{"title":"Server"}', '/nuxt/nuxtcontent/vi/deploy/server', '{"title":"Server Hosting","description":"Node preset is the default preset for Nuxt and Nuxt Content. It is used to build and run Nuxt applications on Node.js."}', 'nuxt/nuxtcontent/vi/6.deploy/1.server', NULL, 'evz1nZK-GAvQxFHq6ylBwoZsmUj9_YTxLN9C5vnYmfw'); -- evz1nZK-GAvQxFHq6ylBwoZsmUj9_YTxLN9C5vnYmfw
INSERT INTO _content_nuxtcontent VALUES ('nuxtcontent/nuxt/nuxtcontent/vi/6.deploy/10.static.md', 'Static Hosting', '{"type":"minimark","value":[["h2",{"id":"what-is-static-hosting"},"What is Static Hosting?"],["p",{},"Static hosting là một loại hosting nơi trang web của bạn được xây dựng và phục vụ dưới dạng các tệp tĩnh (HTML, CSS, JS) có thể được phục vụ bởi bất kỳ máy chủ tệp tĩnh nào."],["p",{},"Nuxt Content có thể được triển khai lên static hosting bằng cách sử dụng Nuxt prerendering."],["h2",{"id":"building-with-ssg"},"Building with SSG"],["p",{},"Để xây dựng ứng dụng của bạn với static site generation, chạy lệnh sau:"],["pre",{"className":"language-bash shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"npx nuxi generate\n","language":"bash","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s52Pk"},"npx"],["span",{"class":"sGFVr"}," nuxi"],["span",{"class":"sGFVr"}," generate\n"]]]],["tip",{"icon":"i-lucide-check"},["p",{},"Lệnh này sẽ tạo một thư mục ",["code",{},"dist/"]," với trang web tĩnh của bạn. Bạn có thể tải nó lên bất kỳ dịch vụ static hosting nào."]],["p",{},"Nuxt sẽ tự động pre-render tất cả các trang bằng cách sử dụng một crawler nội bộ, bạn có thể tùy chỉnh hành vi của nó với các tùy chọn ",["code",{},"nitro.prerender"],"."],["note",{"to":"https://nuxt.com/docs/getting-started/prerendering"},["p",{},"Tìm hiểu thêm về pre-rendering trong Nuxt."]],["h2",{"id":"what-about-the-database"},"What about the Database?"],["p",{},"Nuxt Content sẽ tải cơ sở dữ liệu trong trình duyệt bằng cách sử dụng ",["a",{"href":"/docs/advanced/database#wasm-sqlite-in-browser"},"WASM SQLite"],", theo cách này, các truy vấn nội dung xảy ra trên điều hướng phía client hoặc hành động sẽ chạy trong trình duyệt."],["style",{},"html pre.shiki code .s52Pk, html code.shiki .s52Pk{--shiki-light:#E2931D;--shiki-default:#E2931D;--shiki-dark:#FFCB6B}html pre.shiki code .sGFVr, html code.shiki .sGFVr{--shiki-light:#91B859;--shiki-default:#91B859;--shiki-dark:#C3E88D}html .light .shiki span {color: var(--shiki-light);background: var(--shiki-light-bg);font-style: var(--shiki-light-font-style);font-weight: var(--shiki-light-font-weight);text-decoration: var(--shiki-light-text-decoration);}html.light .shiki span {color: var(--shiki-light);background: var(--shiki-light-bg);font-style: var(--shiki-light-font-style);font-weight: var(--shiki-light-font-weight);text-decoration: var(--shiki-light-text-decoration);}html .default .shiki span {color: var(--shiki-default);background: var(--shiki-default-bg);font-style: var(--shiki-default-font-style);font-weight: var(--shiki-default-font-weight);text-decoration: var(--shiki-default-text-decoration);}html .shiki span {color: var(--shiki-default);background: var(--shiki-default-bg);font-style: var(--shiki-default-font-style);font-weight: var(--shiki-default-font-weight);text-decoration: var(--shiki-default-text-decoration);}html .dark .shiki span {color: var(--shiki-dark);background: var(--shiki-dark-bg);font-style: var(--shiki-dark-font-style);font-weight: var(--shiki-dark-font-weight);text-decoration: var(--shiki-dark-text-decoration);}html.dark .shiki span {color: var(--shiki-dark);background: var(--shiki-dark-bg);font-style: var(--shiki-dark-font-style);font-weight: var(--shiki-dark-font-weight);text-decoration: var(--shiki-dark-text-decoration);}"]],"toc":{"title":"","searchDepth":2,"depth":2,"links":[{"id":"what-is-static-hosting","depth":2,"text":"What is Static Hosting?"},{"id":"building-with-ssg","depth":2,"text":"Building with SSG"},{"id":"what-about-the-database","depth":2,"text":"What about the Database?"}]}}', 'How to deploy Nuxt Content to static hosting with static site generation.', 'md', NULL, '{}', '{"title":"Static"}', '/nuxt/nuxtcontent/vi/deploy/static', '{"title":"Static Hosting","description":"How to deploy Nuxt Content to static hosting with static site generation."}', 'nuxt/nuxtcontent/vi/6.deploy/10.static', NULL, 'ooBtjDy8AC_5nanwRafhXCf3MCSoYh8iR4KC6A_kXxo'); -- ooBtjDy8AC_5nanwRafhXCf3MCSoYh8iR4KC6A_kXxo
INSERT INTO _content_nuxtcontent VALUES ('nuxtcontent/nuxt/nuxtcontent/vi/6.deploy/2.serverless.md', 'Serverless Hosting', '{"type":"minimark","value":[["h2",{"id":"what-is-serverless-hosting"},"What is Serverless Hosting?"],["p",{},"Việc lưu trữ serverless cho phép bạn chạy mã và ứng dụng mà không cần quản lý máy chủ trực tiếp - bạn chỉ cần tải lên mã của mình và nhà cung cấp đám mây sẽ tự động xử lý tất cả cơ sở hạ tầng, mở rộng quy mô và bảo trì trong khi chỉ tính phí cho các tài nguyên tính toán thực tế bạn sử dụng."],["p",{},["strong",{},"Trong môi trường serverless, mỗi yêu cầu của người dùng kích hoạt một phiên bản mới của máy chủ Nuxt của bạn, nghĩa là nó khởi động từ đầu mỗi lần."]," Bản chất \"không trạng thái\" này có nghĩa là bạn không thể lưu trữ dữ liệu trong bộ nhớ máy chủ hoặc sử dụng cơ sở dữ liệu dựa trên tệp như SQLite. Đó là lý do tại sao chúng ta cần sử dụng các dịch vụ cơ sở dữ liệu bên ngoài (như D1, Turso hoặc PostgreSQL) để duy trì dữ liệu độc lập với các phiên bản máy chủ của bạn."],["h2",{"id":"deploy-with-serverless"},"Deploy with Serverless"],["p",{},"Module có hỗ trợ tích hợp cho một số nền tảng serverless nổi tiếng. Bạn có thể triển khai dự án của mình lên chúng một cách dễ dàng. Xem các hướng dẫn cho từng nền tảng:"],["ul",{},["li",{},["a",{"href":"/docs/deploy/nuxthub"},"NuxtHub"]],["li",{},["a",{"href":"/docs/deploy/cloudflare-pages"},"Cloudflare Pages"]],["li",{},["a",{"href":"/docs/deploy/vercel"},"Vercel"]]],["p",{},"Nếu bạn muốn triển khai lên các nền tảng khác, bạn có thể làm theo các bước dưới đây để triển khai dự án của mình."],["h3",{"id":"_1-select-a-database-service"},"1. Select a database service"],["p",{},"Trước khi triển khai dự án của bạn, bạn cần chọn một dịch vụ cơ sở dữ liệu:"],["code-group",{},["pre",{"className":"language-ts shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"// 1. Create a PostgreSQL database\n// 2. And add the `POSTGRES_URL` to the env variables\nexport default defineNuxtConfig({\n  content: {\n    database: {\n      type: ''postgres'',\n      url: process.env.POSTGRES_URL\n    }\n  }\n})\n","filename":"PostgreSQL","language":"ts","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"sWuyu"},"// 1. Create a PostgreSQL database\n"]],["span",{"class":"line","line":2},["span",{"class":"sWuyu"},"// 2. And add the `POSTGRES_URL` to the env variables\n"]],["span",{"class":"line","line":3},["span",{"class":"s8R28"},"export"],["span",{"class":"s8R28"}," default"],["span",{"class":"s3cPz"}," defineNuxtConfig"],["span",{"class":"sZSNi"},"("],["span",{"class":"sDfIl"},"{\n"]],["span",{"class":"line","line":4},["span",{"class":"sRlkE"},"  content"],["span",{"class":"sDfIl"},":"],["span",{"class":"sDfIl"}," {\n"]],["span",{"class":"line","line":5},["span",{"class":"sRlkE"},"    database"],["span",{"class":"sDfIl"},":"],["span",{"class":"sDfIl"}," {\n"]],["span",{"class":"line","line":6},["span",{"class":"sRlkE"},"      type"],["span",{"class":"sDfIl"},":"],["span",{"class":"sDfIl"}," ''"],["span",{"class":"sGFVr"},"postgres"],["span",{"class":"sDfIl"},"''"],["span",{"class":"sDfIl"},",\n"]],["span",{"class":"line","line":7},["span",{"class":"sRlkE"},"      url"],["span",{"class":"sDfIl"},":"],["span",{"class":"sZSNi"}," process"],["span",{"class":"sDfIl"},"."],["span",{"class":"sZSNi"},"env"],["span",{"class":"sDfIl"},"."],["span",{"class":"sZSNi"},"POSTGRES_URL\n"]],["span",{"class":"line","line":8},["span",{"class":"sDfIl"},"    }\n"]],["span",{"class":"line","line":9},["span",{"class":"sDfIl"},"  }\n"]],["span",{"class":"line","line":10},["span",{"class":"sDfIl"},"}"],["span",{"class":"sZSNi"},")\n"]]]],["pre",{"className":"language-ts shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"// 1. Create a D1 database in your CF account\n// 2. Link it to your project with the same binding name\nexport default defineNuxtConfig({\n  c