INSERT INTO _content_nuxtcontent VALUES ('nuxtcontent/nuxt/nuxtcontent/vi/6.deploy/3.nuxthub.md', 'NuxtHub', '{"type":"minimark","value":[["card",{},["p",{},"Quick Setup"],["ol",{},["li",{},"Cài đặt module ",["code",{},"@nuxthub/core"]," ",["code",{},"nuxi module add hub"]],["li",{},"Sử dụng ",["code",{},"npx nuxthub deploy"]," để triển khai nội dung của bạn lên NuxtHub"]]],["hr",{}],["p",{},"Module Nuxt Content có tích hợp sẵn với ",["a",{"href":"https://hub.nuxt.com","rel":["nofollow"]},"NuxtHub"]," để triển khai nội dung của bạn."],["p",{},"Để kích hoạt tích hợp NuxtHub, bạn cần cài đặt module ",["code",{},"@nuxthub/core"]," và đăng ký nó trong ",["code",{},"nuxt.config.ts"]," của bạn. Hiệu quả hơn, bạn có thể sử dụng lệnh ",["code",{},"nuxi module"]," để làm cả hai cùng một lúc."],["pre",{"className":"language-bash shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"npx nuxi module add hub\n","language":"bash","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s52Pk"},"npx"],["span",{"class":"sGFVr"}," nuxi"],["span",{"class":"sGFVr"}," module"],["span",{"class":"sGFVr"}," add"],["span",{"class":"sGFVr"}," hub\n"]]]],["p",{},"Đó là nó 🎉"],["p",{},"Bây giờ bạn có thể sử dụng lệnh ",["code",{},"npx nuxthub deploy"]," để triển khai nội dung của bạn lên NuxtHub."],["pre",{"className":"language-bash shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"npx nuxthub deploy\n","language":"bash","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s52Pk"},"npx"],["span",{"class":"sGFVr"}," nuxthub"],["span",{"class":"sGFVr"}," deploy\n"]]]],["note",{},["p",{},"Module Nuxt Content tự động kích hoạt cơ sở dữ liệu NuxtHub. Và cập nhật cấu hình cơ sở dữ liệu để sử dụng Cloudflare D1 với tên binding ",["code",{},"DB"],". (Đây là cấu hình mặc định cho cơ sở dữ liệu NuxtHub.)"],["br",{}],["p