INSERT INTO _content_nuxtcontent VALUES ('nuxtcontent/nuxt/nuxtcontent/vi/6.deploy/4.cloudflare-pages.md', 'Cloudflare Pages', '{"type":"minimark","value":[["card",{},["p",{},"Quick Setup"],["ol",{},["li",{},"Sử dụng ",["code",{},"nuxi build --preset=cloudflare_pages"]," để xây dựng ứng dụng của bạn"],["li",{},"Tạo cơ sở dữ liệu D1 và kết nối với dự án của bạn trong bảng điều khiển Cloudflare dưới tên binding ",["code",{},"DB"]],["li",{},"Triển khai/Tái triển khai ứng dụng của bạn"]]],["hr",{}],["p",{},"Module Nuxt Content có tích hợp sẵn với ",["a",{"href":"https://pages.cloudflare.com","rel":["nofollow"]},"Cloudflare Pages"]," để triển khai nội dung của bạn."],["p",{},"Module sẽ tự động phát hiện mục tiêu xây dựng và chuẩn bị cấu hình cần thiết cho Cloudflare Pages."],["p",{},"Bạn có thể sử dụng tùy chọn ",["code",{},"--preset=cloudflare_pages"]," trên lệnh ",["code",{},"nuxi build"]," hoặc sử dụng ",["code",{},"nuxt.config.ts"]," để cấu hình preset."],["pre",{"className":"language-ts shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"export default defineNuxtConfig({\n  nitro: {\n    preset: ''cloudflare_pages'',\n  },\n});\n","language":"ts","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s8R28"},"export"],["span",{"class":"s8R28"}," default"],["span",{"class":"s3cPz"}," defineNuxtConfig"],["span",{"class":"sZSNi"},"("],["span",{"class":"sDfIl"},"{\n"]],["span",{"class":"line","line":2},["span",{"class":"sRlkE"},"  nitro"],["span",{"class":"sDfIl"},":"],["span",{"class":"sDfIl"}," {\n"]],["span",{"class":"line","line":3},["span",{"class":"sRlkE"},"    preset"],["span",{"class":"sDfIl"},":"],["span",{"class":"sDfIl"}," ''"],["span",{"class":"sGFVr"},"cloudflare_pages"],["span",{"class":"sDfIl"},"''"],["span",{"class":"sDfIl"},",\n"]],["span",{"class":"line","line":4},["span",{"class":"sDfIl"},"  },\n"]],["span",{"class":"line","line":5},["span",{"class":"sDfIl"},"