INSERT INTO _content_nuxtcontent VALUES ('nuxtcontent/nuxt/nuxtcontent/vi/6.deploy/5.cloudflare-workers.md', 'Cloudflare Workers', '{"type":"minimark","value":[["card",{},["p",{},"Quick Setup"],["ol",{},["li",{},"Sử dụng preset ",["code",{},"cloudflare_module"]," và ngày tương thích của ",["code",{},"2024-09-19"]," hoặc sau đó."],["li",{},"Tạo một cơ sở dữ liệu D1 và kết nối nó với dự án của bạn trong bảng điều khiển Cloudflare dưới tên binding ",["code",{},"DB"],", và cấu hình cấu hình cơ sở dữ liệu trong tệp ",["code",{},"nuxt.config.ts"],"."],["li",{},"Xây dựng và triển khai ứng dụng của bạn"]]],["hr",{}],["p",{},"Module Nuxt Content có tích hợp sẵn với ",["a",{"href":"https://workers.cloudflare.com","rel":["nofollow"]},"Cloudflare Workers"]," để triển khai nội dung của bạn."],["p",{},"Module sẽ tự động phát hiện mục tiêu xây dựng và chuẩn bị cấu hình cần thiết cho Cloudflare Workers."],["p",{},"Tất cả những gì bạn cần làm là tạo một cơ sở dữ liệu Cloudflare D1 và kết nối nó với dự án của bạn. Sau khi tạo một cơ sở dữ liệu D1, bạn nên\nđịnh nghĩa cấu hình cơ sở dữ liệu trong tệp ",["code",{},"nuxt.config.ts"]," với tùy chọn ",["code",{},"nitro.cloudflare.wrangler.d1_databases"],"."],["p",{},"Theo mặc định, module sẽ sử dụng tên binding ",["code",{},"DB"],". Bạn có thể ghi đè cấu hình cơ sở dữ liệu bằng cách cung cấp cấu hình cơ sở dữ liệu của riêng mình trong ",["code",{},"nuxt.config.ts"],"."],["pre",{"className":"language-ts shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"export default defineNuxtConfig({\n  compatibilityDate: \"2025-05-15\",\n  nitro: {\n    preset: ''cloudflare_module'',\n    cloudflare: {\n      deployConfig: true,\n      wrangler: {\n        d1_databases: [\n          {\n            binding: ''DB'',\n            database_name: ''database-name'',\n            database_id: ''*********-***-****-****-*********''\n          }\n        ]\n      },\n    },\n  }\n})\n","filename":"nuxt.config.ts","language":"ts","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s8R28"},"export"],["span",{"class":"s8R28"}," default"],["span",{"class":"s3cPz"}," defineNuxtConfig"],["span",{"class":"sZSNi"},"("],["span",{"class":"sDfIl"},"{\n"]],["span",{"class":"line","line":2},["span",{"class":"sRlkE"},"  compatibilityDate"],["span",{"class":"sDfIl"},":"],["span",{"class":"sDfIl"}," \""],["span",{"class":"sGFVr"},"2025-05-15"],["span",{"class":"sDfIl"},"\""],["span",{"class":"sDfIl"},",\n"]],["span",{"class":"line","line":3},["span",{"class":"sRlkE"},"  nitro"],["span",{"class":"sDfIl"},":"],["span",{"class":"sDfIl"}," {\n"]],["span",{"class":"line","line":4},["span",{"class":"sRlkE"},"    preset"],["span",{"class":"sDfIl"},":"],["span",{"class":"sDfIl"}," ''"],["span",{"class":"sGFVr"},"cloudflare_module"],["span",{"class":"sDfIl"},"''"],["span",{"class":"sDfIl"},",\n"]],["span",{"class":"line","line":5},["span",{"class":"sRlkE"},"    cloudflare"],["span",{"class":"sDfIl"},":"],["span",{"class":"sDfIl"}," {\n"]],["span",{"class":"line","line":6},["span",{"class":"sRlkE"},"      deployConfig"],["span",{"class":"sDfIl"},":"],["span",{"class":"sbKd-"}," true"],["span",{"class":"sDfIl"},",\n"]],["span",{"class":"line","line":7},["span",{"class":"sRlkE"},"      wrangler"],["span",{"class":"sDfIl"},":"],["span",{"class":"sDfIl"}," {\n"]],["span",{"class":"line","line":8},["span",{"class":"sRlkE"},"        d1_databases"],["span",{"class":"sDfIl"},":"],["span",{"class":"sZSNi"}," [\n"]],["span",{"class":"line","line":9},["span",{"class":"sDfIl"},"          {\n"]],["span",{"class":"line","line":10},["span",{"class":"sRlkE"},"            binding"],["span",{"class":"sDfIl"},":"],["span",{"class":"sDfIl"}," ''"],["span",{"class":"sGFVr"},"DB"],["span",{"class":"sDfIl"},"''"],["span",{"class":"sDfIl"},",\n"]],["span",{"class":"line","line":11},["span",{"class":"sRlkE"},"            database_name"],["span",{"class":"sDfIl"},":"],["span",{"