INSERT INTO _content_nuxtcontent VALUES ('nuxtcontent/nuxt/nuxtcontent/vi/6.deploy/6.vercel.md', 'Vercel', '{"type":"minimark","value":[["card",{},["p",{},"Quick Setup"],["ul",{},["li",{},"Thực thi lệnh ",["code",{},"npx vercel deploy"]," hoặc truy cập bảng điều khiển Vercel và tạo một dự án mới bằng kho git."]]],["hr",{}],["p",{},"Các dự án Nuxt Content có thể được triển khai lên Vercel mà không cần cấu hình. Module sẽ tự động phát hiện môi trường Vercel và chuẩn bị cấu hình cần thiết cho việc triển khai."],["p",{},"Tất cả những gì bạn cần làm là thực thi lệnh ",["code",{},"npx vercel deploy"]," hoặc truy cập bảng điều khiển Vercel và tạo một dự án mới bằng kho git."],["p",{},"Đó là nó 🎉"],["note",{},["p",{},"Theo mặc định, module sẽ sử dụng cơ sở dữ liệu SQlite trong Vercel nằm tại thư mục ",["code",{},"/tmp"],". Bạn có thể ghi đè cấu hình cơ sở dữ liệu bằng cách cung cấp cấu hình cơ sở dữ liệu của riêng mình."],["br",{}],["p",{},"Có một số nhà cung cấp cơ sở dữ liệu được Vercel hỗ trợ. Bạn có thể sử dụng bất kỳ nhà cung cấp nào bằng cách cung cấp chuỗi kết nối chính xác trong ",["code",{},"nuxt.config.ts"],"."]],["p",{},"Tham khảo:"],["ul",{},["li",{},["a",{"href":"https://nuxt.com/deploy/vercel","rel":["nofollow"]},"Nuxt Deploy documentation"]],["li",{},["a",{"href":"https://vercel.com/docs/deployments/deployment-methods","rel":["nofollow"]},"Vercel documentation"]]]],"toc":{"title":"","searchDepth":2,"depth":2,"links":[]}}', 'Deploy your Content app to Vercel', 'md', NULL, '{}', 'true', '/nuxt/nuxtcontent/vi/deploy/vercel', '{"title":"Vercel","description":"Deploy your Content app to Vercel"}', 'nuxt/nuxtcontent/vi/6.deploy/6.vercel', NULL, 'XHuDt8ODjvy4qO0HsgcnTQe-7aBpMTR685Rup3gHPNU'); -- XHuDt8ODjvy4qO0HsgcnTQe-7aBpMTR685Rup3gHPNU
INSERT INTO _content_nuxtcontent VALUES ('nuxtcontent/nuxt/nuxtcontent/vi/6.deploy/7.netlify.md', 'Netlify', '{"type":"minimark","value":[["card",{},["p",{},"Quick Setup"],["ul",{},["li",{},"Truy cập bảng điều khiển Netlify và tạo một dự án mới bằng kho git."],["li",{},"Truy cập ",["code",{},"Site Configuration"]," trong ",["code",{},"Dependency management"]," và thay đổi phiên bản Node thành ",["code",{},"20.x"]," hoặc cao hơn."],["li",{},"Truy cập ",["code",{},"deploys"]," và thử lại lần triển khai cuối cùng."]]],["hr",{}],["p",{},"Các dự án Nuxt Content có thể được triển khai lên Netlify mà không cần cấu hình. Module sẽ tự động phát hiện môi trường Netlify và chuẩn bị cấu hình cần thiết cho Netlify."],["p",{},"Tất cả những gì bạn cần làm là truy cập bảng điều khiển Netlify và tạo một dự án mới bằng kho git."],["note",{},["p",{},"Theo mặc định, Netlify sử dụng Node.js 18.x mà không được module hỗ trợ. Bạn cần thay đổi phiên bản Node.js trong ",["code",{},"Site Configuration"]," dưới ",["code",{},"Dependency management"],"."]],["p",{},"Đó là nó 🎉"],["p",{},"Tham khảo:"],["ul",{},["li",{},["a",{"href":"https://nuxt.com/deploy/netlify","rel":["nofollow"]},"Nuxt Deploy documentation"]],["li",{},["a",{"href":"https://www.netlify.com/blog/2016/09/29/a-step-by-step-guide-deploying-on-netlify/","rel":["nofollow"]},"Netlify documentation"]]]],"toc":{"title":"","searchDepth":2,"depth":2,"links":[]}}', 'Deploy your Content app to Netlify', 'md', NULL, '{}', 'true', '/nuxt/nuxtcontent/vi/deploy/netlify', '{"title":"Netlify","description":"Deploy your Content app to Netlify"}', 'nuxt/nuxtcontent/vi/6.deploy/7.netlify', NULL, 'SM7SUqZm9GIi4SFYtwAxEdgs1ZfPVAjNUWMLllfDLsk'); -- SM7SUqZm9GIi4SFYtwAxEdgs1ZfPVAjNUWMLllfDLsk
INSERT INTO _content_nuxtcontent VALUES ('nuxtcontent/nuxt/nuxtcontent/vi/6.deploy/8.aws-amplify.md', 'AWS Amplify', '{"type":"minimark","value":[["card",{},["p",{},"Quick Setup"],["ul",{},["li",{},"Cài đặt gói ",["code",{},"sqlite3"]," trong dự án của bạn."],["li",{},"Truy cập bảng điều khiển AWS Amplify và tạo một dự án mới bằng kho git và triển khai ứng dụng."]]],["hr",{}],["p",{},"Các dự án Nuxt Content có thể được triển khai lên AWS Amplify mà không cần cấu hình.\nModule sẽ tự động phát hiện môi trường AWS Amplify và chuẩn bị cấu hình cần thiết cho việc triển khai."],["p",{},"Tất cả những gì bạn cần làm là cài đặt gói ",["code",{},"sqlite3"]," trong dự án của bạn và truy cập bảng điều khiển AWS Amplify và tạo một dự án mới bằng kho git."],["p",{},"Đó là nó 🎉"],["note",{},["p",{},"Theo mặc định, module sẽ sử dụng cơ sở dữ liệu SQlite nằm tại thư mục ",["code",{},"/tmp"],". Bạn có thể ghi đè cấu hình cơ sở dữ liệu bằng cách cung cấp cấu hình cơ sở dữ liệu của riêng mình."]],["p",{},"Tham khảo:"],["ul",{},["li",{},["a",{"href":"https://nuxt.com/deploy/aws-amplify","rel":["nofollow"]},"Nuxt Deploy documentation"]]]],"toc":{"title":"","searchDepth":2,"depth":2,"links":[]}}', 'Deploy your Content app to AWS Amplify', 'md', NULL, '{}', 'true', '/nuxt/nuxtcontent/vi/deploy/aws-amplify', '{"title":"AWS Amplify","description":"Deploy your Content app to AWS Amplify"}', 'nuxt/nuxtcontent/vi/6.deploy/8.aws-amplify', NULL, 'aneseu3Q4d8tRgNkmXOhlSlrc0WHjZUmF_AuP7l2fj0'); -- aneseu3Q4d8tRgNkmXOhlSlrc0WHjZUmF_AuP7l2fj0
INSERT INTO _content_nuxtcontent VALUES ('nuxtcontent/nuxt/nuxtcontent/vi/6.deploy/9.docker.md', 'Docker', '{"type":"minimark","value":[["p",{},"Docker là một nền tảng containerization phổ biến cho phép bạn đóng gói ứng dụng của mình với tất cả các phụ thuộc vào một container duy nhất. Điều này giúp dễ dàng triển khai ứng dụng Content của bạn trên bất kỳ nền tảng nào hỗ trợ Docker."],["h2",{"id":"with-nodejs-image"},"With Node.js image"],["p",{},"Sử dụng hình ảnh Node.js của Docker, bạn có thể triển khai ứng dụng Content của mình. Tất cả những gì bạn cần là tạo một Dockerfile và xây dựng hình ảnh. Đây là một ví dụ Dockerfile:"],["pre",{"className":"language-docker shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"# Build Stage 1\n\nFROM node:22-alpine AS build\nWORKDIR /app\n\nRUN corepack enable\n\n# Copy package.json and your lockfile, here we add pnpm-lock.yaml for illustration\nCOPY package.json pnpm-lock.yaml .npmrc ./\n\n# Install dependencies\nRUN pnpm i\n\n# Copy the entire project\nCOPY . ./\n\n# Build the project\nRUN pnpm run build\n\n# Build Stage 2\n\nFROM node:22-alpine\nWORKDIR /app\n\n# Only `.output` folder is needed from the build stage\nCOPY --from=build /app/.output/ ./\n\n# Change the port and host\nENV PORT=80\nENV HOST=0.0.0.0\n\nEXPOSE 80\n\nCMD [\"node\", \"/app/server/index.mjs\"]\n","filename":"Dockerfile","language":"docker","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{},"# Build Stage 1\n"]],["span",{"class":"line","line":2},["span",{"emptyLinePlaceholder":true},"\n"]],["span",{"class":"line","line":3},["span",{},"FROM node:22-alpine AS build\n"]],["span",{"class":"line","line":4},["span",{},"WORKDIR /app\n"]],["span",{"class":"line","line":5},["span",{"emptyLinePlaceholder":true},"\n"]],["span",{"class":"line","line":6},["span",{},"RUN corepack enable\n"]],["span",{"class":"line","line":7},["span",{"emptyLinePlaceholder":true},"\n"]],["span",