INSERT INTO _content_nuxtcontent VALUES ('nuxtcontent/nuxt/nuxtcontent/vi/8.advanced/3.database.md', 'Database', '{"type":"minimark","value":[["p",{},"Trong Content v3, chúng tôi đã giới thiệu một lớp lưu trữ mạnh mẽ dựa trên SQLite, cung cấp một phương pháp mạnh mẽ và hiệu quả để quản lý nội dung. Điều này đánh dấu một cải tiến đáng kể so với hệ thống lưu trữ dựa trên tệp trước đó, bị hạn chế bởi hiệu suất và khả năng mở rộng."],["blockquote",{},["p",{},"Trong Content v2, hệ thống đọc và phân tích nội dung trong thời gian chạy Nitro, tạo một tệp cache cho mỗi tệp nội dung để lưu trữ dữ liệu đã phân tích. Phương pháp này gây ra chi phí đáng kể cho thời gian chạy của trang web."],["ul",{},["li",{},"Thời gian I/O trong sản xuất là đáng kể, vì mô-đun phải tải tất cả các tệp cache để tìm kiếm qua nội dung."],["li",{},"Ngoài ra, thiếu tối ưu hóa và nén cho nội dung dẫn đến kích thước bundle lớn, đặc biệt có vấn đề trong môi trường edge."]]],["p",{},"Quản lý nội dung trong Content v3 bao gồm ba bước chính, được thiết kế để hợp lý hóa quy trình và nâng cao hiệu suất."],["h2",{"id":"generating-database-dump"},"Generating Database Dump"],["p",{},"Đối với mỗi bộ sưu tập trong dự án của bạn, mô-đun đọc nội dung từ nguồn được định nghĩa và phân tích nó thành Cây Cú Pháp Trừu Tượng (AST). Nó tạo một bảng cụ thể cho mỗi bộ sưu tập dựa trên schema của nó. Nội dung đã phân tích sau đó được chèn vào bảng tương ứng, đảm bảo cấu trúc dữ liệu phù hợp với schema được định nghĩa để tối ưu hóa truy vấn. Mọi thứ sau đó được lưu trong một tệp dump."],["h2",{"id":"restoring-dump-on-cold-start"},"Restoring Dump on Cold Start"],["p",{},"Trong thời gian chạy, khi ứng dụng thực hiện truy vấn đầu tiên để lấy nội dung, mô-đun đọc dump đã tạo từ bước trước và khôi phục nó vào cơ sở dữ liệu mục tiêu. Quy trình này nhanh chóng và được tối ưu hóa cho mỗi chế độ triển khai và nền tảng."],["p",{},"Mô-đun sử dụng một cơ chế kiểm tra tính toàn vẹn đặc biệt để đảm bảo cơ sở dữ liệu được cập nhật với nội dung mới nhất. Cùng cơ chế kiểm tra tính toàn vẹn này cũng ngăn chặn việc nhập trùng lặp, duy trì tính toàn vẹn và độ chính xác của dữ liệu được lưu trữ."],["h2",{"id":"wasm-sqlite-in-browser"},"WASM SQLite in Browser"],["p",{},"Đối với điều hướng phía client, mô-đun sử dụng cách tiếp cận tương tự. Khi ứng dụng thực hiện truy vấn đầu tiên cho nội dung, nó tải xuống dump đã tạo từ máy chủ và khởi tạo cơ sở dữ liệu SQLite cục bộ trong trình duyệt. Từ thời điểm đó trở đi, tất cả các truy vấn được thực hiện cục bộ mà không cần gọi máy chủ, cải thiện đáng kể khả năng đáp ứng của ứng dụng và cung cấp trải nghiệm người dùng liền mạch."],["p",{},"Kiến trúc này không chỉ nâng cao hiệu suất mà còn cho phép khả năng ngoại tuyến, cho phép người dùng truy cập nội dung ngay cả khi không có kết nối internet hoạt động. Sự kết hợp của xử lý phía máy chủ và phía client đảm bảo rằng Nuxt Content v3 vừa mạnh mẽ vừa linh hoạt, phục vụ cho nhiều trường hợp sử dụng và môi trường khác nhau."]],"toc":{"title":"","searchDepth":2,"depth":2,"links":[{"id":"generating-database-dump","depth":2,"text":"Generating Database Dump"},{"id":"restoring-dump-on-cold-start","depth":2,"text":"Restoring Dump on Cold Start"},{"id":"wasm-sqlite-in-browser","depth":2,"text":"WASM SQLite in Browser"}]}}', 'How Nuxt Content stores and retrieves content', 'md', NULL, '{}', '{"title":"SQL Storage"}', '/nuxt/nuxtcontent/vi/advanced/database', '{"title":"Database","description":"How Nuxt Content stores and retrieves content"}', 'nuxt/nuxtcontent/vi/8.advanced/3.database', NULL, 'pO_Swg6ND6POguinfV3-aTsYM4kpAXGboPjn8OwmdTY'); -- pO_Swg6ND6POguinfV3-aTsYM4kpAXGboPjn8OwmdTY
INSERT INTO _content_nuxtcontent VALUES ('nuxtcontent/nuxt/nuxtcontent/vi/8.advanced/4.tools.md', 'Tools', '{"type":"minimark","value":[["p",{},"Nuxt Content sử dụng ",["strong",{},"cơ sở dữ liệu SQLite (",["code",{},"contents.sqlite"],")"]," để lưu trữ và truy vấn nội dung một cách hiệu quả. Nếu bạn gặp phải ",["strong",{},"nội dung bị thiếu, truy vấn chậm, hoặc vấn đề cơ sở dữ liệu"],", việc gỡ lỗi cơ sở dữ liệu SQLite của bạn có thể giúp đỡ!"],["callout",{"icon":"i-simple-icons-visualstudiocode","to":"https://marketplace.visualstudio.com/items?itemName=alexcvzz.vscode-sqlite"},["p",{},"Một cách đơn giản để kiểm tra nó? ",["strong",{},"Sử dụng tiện ích mở rộng SQLite VS Code!"]]],["h2",{"id":"install-sqlite-vs-code-extension"},"Install SQLite VS Code Extension"],["ol",{},["li",{},"Mở ",["strong",{},"Visual Studio Code"],"."],["li",{},"Đi đến ",["strong",{},"bảng Extensions"]," (",["code",{},"Ctrl+Shift+X"]," / ",["code",{},"Cmd+Shift+X"]," trên Mac)."],["li",{},"Tìm kiếm ",["strong",{},"\"SQLite\""]," (bởi ",["code",{},"alexcvzz"],") và cài đặt nó."],["li",{},"Mở cơ sở dữ liệu Nuxt Content của bạn (",["code",{},".data/content/contents.sqlite"],")."]],["tip",{"icon":"i-lucide-lightbulb"},["p",{},"Nếu bạn không thấy ",["code",{},"contents.sqlite"],", hãy khởi động ứng dụng Nuxt của bạn trước:"],["pre",{"className":"language-bash shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"npx nuxi dev\n","filename":"Terminal","language":"bash","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s52Pk"},"npx"],["span",{"class":"sGFVr"}," nuxi"],["span",{"class":"sGFVr"}," dev\n"]]]]],["h2",{"id":"locate-your-sqlite-database"},"Locate Your SQLite Database"],["p",{},"Nuxt Content lưu trữ cơ sở dữ liệu của nó ở đây:"],["pre",{"className":"language-bash shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":".data/content/contents.sqlite\n","language":"bash","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s52Pk"},".data/content/contents.sqlite\n"]]]],["note",{"to":"https://nuxt.com/docs/getting-started/prerendering"},["p",{},"Tệp này được tạo tự động khi bạn khởi động ứng dụng Nuxt. Không cần tạo thủ công!"]],["h2",{"id":"open-explore-the-database"},"Open & Explore the Database"],["ol",{},["li",{},["strong",{},"Nhấp chuột phải"]," vào ",["code",{},"contents.sqlite"]," trong VS Code."],["li",{},"Chọn ",["strong",{},"\"Open Database\""],"."],["li",{},"Mở rộng ",["strong",{},"bảng Database Explorer"]," để xem bảng & dữ liệu."]],["p",{},["img",{"alt":"SQLite Explorer in VS Code","src":"https://github.com/user-attachments/assets/c9f22c4c-7a95-43e8-ab03-aa76f2e49c8e"}]],["h2",{"id":"fixing-common-issues"},"Fixing Common Issues"],["h3",{"id":"nội-dung-không-hiển-thị"},"Nội dung không hiển thị?"],["ol",{},["li",{},["strong",{},"Kiểm tra xem cơ sở dữ liệu có tồn tại không"]," (",["code",{},".data/content/contents.sqlite"],")."],["li",{},["strong",{},"Chạy cleanup & khởi động lại Nuxt"],":\n",["pre",{"className":"language-bash shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"npx nuxi cleanup && npx nuxi dev\n","filename":"Terminal","language":"bash","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s52Pk"},"npx"],["span",{"class":"sGFVr"}," nuxi"],["span",{"class":"sGFVr"}," cleanup"],["span",{"class":"sDfIl"}," &&"],["span",{"class":"s52Pk"}," npx"],["span",{"class":"sGFVr"}," nuxi"],["span",{"class":"sGFVr"}," dev\n"]]]]],["li",{},["strong",{},"Kiểm tra xem nội dung có trong cơ sở dữ liệu không"]," (chạy truy vấn SQL)."]],["h3",{"id":"manually-reset-the-database"},"Manually Reset the Database"],["p",{},"Nếu mọi thứ có vẻ ",["strong",{},"thật sự hỏng"],", hãy thử đặt lại nó:"],["ol",{},["li",{},["strong",{},"Xóa tệp cơ sở dữ liệu"],":\n",["pre",{"className":"language-bash shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"rm -rf .data/content/contents.sqlite\n","filename":"Terminal","language":"bash","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s52Pk"},"rm"],["span",{"class":"sGFVr"}," -rf"],["span",{"class":"sGFVr"}," .data/content/contents.sqlite\n"]]]]],["li",{},["strong",{},"Chạy cleanup"]," để xóa cache cũ:\n",["pre",{"className":"language-bash shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"npx nuxi cleanup\n","filename":"Terminal","language":"bash","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s52Pk"},"npx"],["span",{"class":"sGFVr"}," nuxi"],["span",{"class":"sGFVr"}," cleanup\n"]]]]],["li",{},["strong",{},"Khởi động lại Nuxt"]," để tạo cơ sở dữ liệu mới:\n",["pre",{"className":"language-bash shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"npx nuxi dev\n","filename":"Terminal","language":"bash","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s52Pk"},"npx"],["span",{"class":"sGFVr"}," nuxi"],["span",{"class":"sGFVr"}," dev\n"]]]]]],["note",{"icon":"i-lucide-triangle-alert"},["p",{},"Việc cleanup sẽ xóa dữ liệu cache. Đừng lo—nó sẽ tạo lại tự động!"]],["h2",{"id":"more-debugging-tools"},"More Debugging Tools"],["p",{},"Nếu VS Code không đủ, hãy xem:"],["ul",{},["li",{},"🖥️ ",["a",{"href":"https://sqlitebrowser.org/","rel":["nofollow"]},["strong",{},"DB Browser for SQLite"]]," – Một công cụ trực quan để kiểm tra & sửa đổi cơ sở dữ liệu."],["li",{},"🛠️ ",["strong",{},"SQLite Command Line"]," – Sử dụng ",["code",{},"sqlite3 contents.sqlite"]," để chạy truy vấn SQL từ terminal của bạn."]],["style",{},"html pre.shiki code .s52Pk, html code.shiki .s52Pk{--shiki-light:#E2931D;--shiki-default:#E2931D;--shiki-dark:#FF